        i0.ɵɵtext(52, "\u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DE\u05E9\u05DE\u05E9, \u05D1\u05D9\u05DF \u05D4\u05D9\u05EA\u05E8, \u05DB\u05DE\u05E0\u05D5\u05E2 \u05D7\u05D9\u05E4\u05D5\u05E9 \u05D5\u05DB\u05DE\u05D3\u05E8\u05D9\u05DA \u05DE\u05E7\u05D9\u05E3 \u05DC\u05E2\u05E1\u05E7\u05D9\u05DD \u05D5\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D1\u05D9\u05E9\u05E8\u05D0\u05DC. \u05D4\u05EA\u05DB\u05E0\u05D9\u05DD \u05D5\u05D4\u05DE\u05D9\u05D3\u05E2 \u05D4\u05DE\u05D5\u05E6\u05D2\u05D9\u05DD \u05D1\u05D5 \u05E0\u05D0\u05E1\u05E4\u05D9\u05DD \u05E2\u05DC-\u05D9\u05D3\u05D9 \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DE\u05DE\u05D2\u05D5\u05D5\u05DF \u05DE\u05E7\u05D5\u05E8\u05D5\u05EA, \u05DE\u05E7\u05D5\u05D5\u05E0\u05D9\u05DD \u05D5\u05D0\u05D7\u05E8\u05D9\u05DD \u05D5\u05D7\u05DC\u05E7\u05DD \u05DE\u05D5\u05D6\u05E0\u05D9\u05DD \u05D0\u05DC\u05D9\u05D5 \u05E2\u05DC-\u05D9\u05D3\u05D9 \u05DE\u05E9\u05EA\u05DE\u05E9\u05D9 \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA . \u05D1\u05D9\u05DF \u05D4\u05D9\u05EA\u05E8, \u05EA\u05D5\u05DB\u05DC \u05DC\u05DE\u05E6\u05D5\u05D0 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05EA\u05DB\u05E0\u05D9\u05DD \u05D5\u05DE\u05D9\u05D3\u05E2 \u05D0\u05D5\u05D3\u05D5\u05EA \u05D1\u05EA\u05D9 \u05E2\u05E1\u05E7, \u05D1\u05E2\u05DC\u05D9 \u05DE\u05E7\u05E6\u05D5\u05E2, \u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD, \u05D4\u05D5\u05E4\u05E2\u05D5\u05EA \u05D5\u05D0\u05D9\u05E8\u05D5\u05E2\u05D9 \u05D1\u05D9\u05D3\u05D5\u05E8, \u05DE\u05E9\u05E8\u05D3\u05D9\u05DD \u05DE\u05DE\u05E9\u05DC\u05EA\u05D9\u05D9\u05DD, \u05DB\u05E1\u05E4\u05D5\u05DE\u05D8\u05D9\u05DD, \u05D7\u05D3\u05E8\u05D9 \u05DB\u05D5\u05E9\u05E8, \u05DE\u05D5\u05D6\u05D9\u05D0\u05D5\u05E0\u05D9\u05DD, \u05DE\u05D9\u05D7\u05D6\u05D5\u05E8\u05D9\u05D5\u05EA, \u05EA\u05D7\u05E0\u05D5\u05EA \u05D3\u05DC\u05E7 \u05D5\u05E2\u05D5\u05D3 \u05D5\u05E2\u05D5\u05D3, \u05EA\u05D5\u05DA \u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D0\u05E4\u05E9\u05E8\u05D5\u05D9\u05D5\u05EA \u05D5\u05D7\u05EA\u05DB\u05D9 \u05D4\u05D7\u05D9\u05E4\u05D5\u05E9 \u05D4\u05DE\u05EA\u05E7\u05D3\u05DE\u05D9\u05DD \u05E9\u05DE\u05E6\u05D9\u05E2 \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(53, "p");
        i0.ɵɵtext(54, "\u05E1\u05D3\u05E8 \u05D4\u05EA\u05D5\u05E6\u05D0\u05D5\u05EA \u05D4\u05DE\u05D5\u05E6\u05D2 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05E0\u05E7\u05D1\u05E2 \u05D1\u05D0\u05D5\u05E4\u05DF \u05D0\u05D5\u05D8\u05D5\u05DE\u05D8\u05D9 \u05E2\u05DC-\u05D9\u05D3\u05D9 \u05D0\u05DC\u05D2\u05D5\u05E8\u05D9\u05EA\u05DD \u05DE\u05DE\u05D5\u05D7\u05E9\u05D1, \u05D1\u05D9\u05DF \u05D4\u05D9\u05EA\u05E8 \u05D1\u05D4\u05EA\u05D1\u05E1\u05E1 \u05E2\u05DC \u05D4\u05DE\u05E8\u05D7\u05E7 \u05DE\u05DE\u05D9\u05E7\u05D5\u05DE\u05D5 \u05D1\u05E4\u05D5\u05E2\u05DC \u05E9\u05DC \u05D4\u05DE\u05E9\u05EA\u05DE\u05E9 \u05D5\u05D4\u05D4\u05EA\u05D0\u05DE\u05D4 \u05DC\u05E7\u05E8\u05D9\u05D8\u05E8\u05D9\u05D5\u05DF \u05D4\u05D7\u05D9\u05E4\u05D5\u05E9 \u05E9\u05D1\u05D9\u05E6\u05E2 \u05D4\u05DE\u05E9\u05EA\u05DE\u05E9, \u05D5\u05D4\u05D5\u05D0 \u05D0\u05D9\u05E0\u05D5 \u05DE\u05D5\u05E9\u05E4\u05E2 \u05DC\u05D8\u05D5\u05D1\u05EA \u05D1\u05D9\u05EA \u05E2\u05E1\u05E7 \u05D0\u05D5 \u05E9\u05D9\u05E8\u05D5\u05EA \u05DB\u05D6\u05D4 \u05D0\u05D5 \u05D0\u05D7\u05E8. \u05D0\u05D9\u05DF \u05D1\u05D0\u05DE\u05D5\u05E8 \u05DB\u05D3\u05D9 \u05DC\u05D2\u05E8\u05D5\u05E2 \u05DE\u05E9\u05D9\u05E7\u05D5\u05DC \u05D3\u05E2\u05EA\u05D4 \u05D4\u05DE\u05DC\u05D0 \u05E9\u05DC \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05D9\u05D7\u05E1 \u05DC\u05E1\u05D3\u05E8 \u05D4\u05D5\u05E4\u05E2\u05EA \u05EA\u05D5\u05E6\u05D0\u05D5\u05EA \u05D4\u05D7\u05D9\u05E4\u05D5\u05E9.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(55, "p");
        i0.ɵɵtext(56, "\u05DB\u05D0\u05DE\u05D5\u05E8, \u05D7\u05DC\u05E7 \u05DE\u05EA\u05DB\u05E0\u05D9\u05D5 \u05E9\u05DC \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DE\u05D5\u05D6\u05DF \u05D0\u05DC\u05D9\u05D5 \u05E2\u05DC-\u05D9\u05D3\u05D9 \u05DE\u05E9\u05EA\u05DE\u05E9\u05D9\u05D5 \u05D4\u05E8\u05E9\u05D5\u05DE\u05D9\u05DD, \u05D4\u05DE\u05E9\u05EA\u05EA\u05E4\u05D9\u05DD \u05D1\u05D4\u05D6\u05E0\u05EA \u05D4\u05EA\u05D5\u05DB\u05DF \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05D0\u05D5\u05E4\u05DF \u05E4\u05E2\u05D9\u05DC. \u05D1\u05DB\u05DC\u05DC \u05D6\u05D4, \u05DE\u05D0\u05E4\u05E9\u05E8\u05EA \u05DC\u05DA \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D4\u05D5\u05E1\u05D9\u05E3 \u05DE\u05D9\u05D3\u05E2 \u05D5\u05EA\u05DB\u05E0\u05D9\u05DD \u05D0\u05D5\u05D3\u05D5\u05EA \u05D1\u05EA\u05D9 \u05E2\u05E1\u05E7 \u05D5\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05E9\u05D8\u05E8\u05DD \u05D4\u05EA\u05E4\u05E8\u05E1\u05DE\u05D5 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05DB\u05DF \u05DC\u05DB\u05EA\u05D5\u05D1 \u05D1\u05D9\u05E7\u05D5\u05E8\u05D5\u05EA \u05D4\u05D5\u05DC\u05DE\u05D5\u05EA \u05D5\u05D7\u05D5\u05E7\u05D9\u05D5\u05EA, \u05D4\u05DE\u05D4\u05D5\u05D5\u05EA \u05D4\u05D1\u05E2\u05EA \u05D3\u05E2\u05D4 \u05D0\u05D9\u05E9\u05D9\u05EA, \u05D0\u05D5\u05D3\u05D5\u05EA \u05D1\u05EA\u05D9 \u05D4\u05E2\u05E1\u05E7 \u05D5\u05D4\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D4\u05DE\u05D5\u05E6\u05D2\u05D9\u05DD \u05D1\u05D5. \u05D1\u05E0\u05D5\u05E1\u05E3, \u05EA\u05D5\u05DB\u05DC \u05DC\u05D4\u05D5\u05E1\u05D9\u05E3 \u05E6\u05D9\u05DC\u05D5\u05DE\u05D9\u05DD \u05D0\u05D5\u05D3\u05D5\u05EA \u05D1\u05EA\u05D9 \u05D4\u05E2\u05E1\u05E7 \u05D5\u05D4\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D4\u05DE\u05D5\u05E6\u05D2\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DC\u05E2\u05D3\u05DB\u05DF \u05E4\u05E8\u05D8\u05D9\u05DD \u05D0\u05D5\u05D3\u05D5\u05EA\u05D9\u05D4\u05DD, \u05DC\u05D3\u05D5\u05D5\u05D7 \u05E2\u05DC \u05D8\u05E2\u05D5\u05D9\u05D5\u05EA \u05D1\u05DE\u05D9\u05D3\u05E2 \u05D5\u05D1\u05EA\u05DB\u05E0\u05D9\u05DD \u05D4\u05DE\u05D5\u05E6\u05D2\u05D9\u05DD, \u05DC\u05D3\u05D5\u05D5\u05D7 \u05E2\u05DC \u05D1\u05EA\u05D9 \u05E2\u05E1\u05E7 \u05D5\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05E9\u05D0\u05D9\u05E0\u05DD \u05E4\u05E2\u05D9\u05DC\u05D9\u05DD \u05D9\u05D5\u05EA\u05E8, \u05D5\u05E2\u05D5\u05D3 \u05DE\u05D9\u05D3\u05E2 \u05E0\u05D5\u05E1\u05E3 \u05E2\u05DC \u05D6\u05DB\u05D5\u05D9\u05D5\u05EA \u05D1\u05EA\u05DB\u05E0\u05D9\u05DD \u05E9\u05DE\u05E2\u05DC\u05D9\u05DD \u05DE\u05E9\u05EA\u05DE\u05E9\u05D9\u05DD \u05EA\u05D5\u05DB\u05DC \u05DC\u05DE\u05E6\u05D5\u05D0 \u05D1\u05D4\u05DE\u05E9\u05DA \u05EA\u05E0\u05D0\u05D9 \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(57, "p");
        i0.ɵɵtext(58, "\u05D1\u05DE\u05D9\u05D3\u05D4 \u05D5\u05D4\u05E0\u05DA \u05D1\u05E2\u05DC\u05D9\u05D5 \u05D4\u05D7\u05D5\u05E7\u05D9 \u05E9\u05DC \u05E2\u05E1\u05E7 \u05D4\u05DE\u05D5\u05E6\u05D2 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA (\"");
        i0.ɵɵelementStart(59, "b");
        i0.ɵɵtext(60, "\u05D1\u05E2\u05DC \u05E2\u05E1\u05E7");
        i0.ɵɵelementEnd();
        i0.ɵɵtext(61, "\"), \u05E2\u05E9\u05D5\u05D9\u05D4 \u05DC\u05D0\u05E4\u05E9\u05E8 \u05DC\u05DA \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D2\u05D9\u05E9\u05D4 \u05DC\u05D0\u05E4\u05E9\u05E8\u05D5\u05D9\u05D5\u05EA \u05E2\u05E8\u05D9\u05DB\u05D4 \u05DE\u05EA\u05E7\u05D3\u05DE\u05D5\u05EA \u05DB\u05DE\u05D5 \u05D2\u05DD \u05DC\u05E1\u05D8\u05D8\u05D9\u05E1\u05D8\u05D9\u05E7\u05D5\u05EA \u05E9\u05D5\u05E0\u05D5\u05EA \u05D4\u05E0\u05D5\u05D2\u05E2\u05D5\u05EA \u05DC\u05DE\u05D9\u05D3\u05E2 \u05D5\u05DC\u05EA\u05DB\u05E0\u05D9\u05DD \u05D4\u05DE\u05EA\u05E4\u05E8\u05E1\u05DE\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05D9\u05D7\u05E1 \u05DC\u05D1\u05D9\u05EA \u05D4\u05E2\u05E1\u05E7 \u05E9\u05DC\u05DA. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D9\u05E0\u05D4 \u05D7\u05D9\u05D9\u05D1\u05EA \u05DC\u05D0\u05E4\u05E9\u05E8 \u05DC\u05D1\u05E2\u05DC \u05E2\u05E1\u05E7 \u05D0\u05EA \u05D4\u05D0\u05E4\u05E9\u05E8\u05D5\u05D9\u05D5\u05EA \u05DB\u05D0\u05DE\u05D5\u05E8 \u05D5\u05D4\u05D9\u05D0 \u05EA\u05E2\u05E9\u05D4 \u05DB\u05DF \u05DC\u05E4\u05D9 \u05E9\u05D9\u05E7\u05D5\u05DC \u05D3\u05E2\u05EA\u05D4 \u05D4\u05D1\u05DC\u05E2\u05D3\u05D9 \u05D5\u05D1\u05DB\u05E4\u05D5\u05E3 \u05DC\u05DE\u05E1\u05D9\u05E8\u05EA \u05D0\u05E1\u05DE\u05DB\u05EA\u05D0\u05D5\u05EA \u05DE\u05EA\u05D0\u05D9\u05DE\u05D5\u05EA \u05DC\u05D4\u05E0\u05D7\u05EA \u05D3\u05E2\u05EA\u05D4 \u05E9\u05DC \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA. \u05D9\u05D5\u05D1\u05D4\u05E8 \u05DB\u05D9 \u05D1\u05E2\u05DC\u05D9 \u05E2\u05E1\u05E7\u05D9\u05DD \u05D0\u05D9\u05E0\u05DD \u05E8\u05E9\u05D0\u05D9\u05DD \u05DC\u05DE\u05E0\u05D5\u05E2 \u05DE\u05DE\u05E9\u05EA\u05DE\u05E9\u05D9 \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05E2\u05E8\u05D5\u05DA \u05DE\u05D9\u05D3\u05E2 \u05D5\u05EA\u05DB\u05E0\u05D9\u05DD, \u05DB\u05DE\u05D5 \u05D2\u05DD \u05DC\u05D4\u05D5\u05E1\u05D9\u05E3 \u05EA\u05DB\u05E0\u05D9\u05DD \u05D7\u05D3\u05E9\u05D9\u05DD \u05D1\u05D9\u05D7\u05E1 \u05DC\u05D1\u05D9\u05EA \u05D4\u05E2\u05E1\u05E7 \u05E9\u05D1\u05D1\u05E2\u05DC\u05D5\u05EA\u05DD.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(62, "p");
        i0.ɵɵtext(63, "\u05DE\u05D1\u05DC\u05D9 \u05DC\u05D2\u05E8\u05D5\u05E2 \u05DE\u05DB\u05DC\u05DC\u05D9\u05D5\u05EA \u05D4\u05D0\u05DE\u05D5\u05E8 \u05DC\u05E2\u05D9\u05DC \u05D1\u05D9\u05D7\u05E1 \u05DC\u05E1\u05D3\u05E8 \u05D4\u05EA\u05D5\u05E6\u05D0\u05D5\u05EA \u05D4\u05DE\u05D5\u05E6\u05D2\u05D5\u05EA \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05E8\u05E9\u05D0\u05D9\u05EA \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D4\u05EA\u05D9\u05E8 \u05DC\u05D1\u05E2\u05DC\u05D9 \u05E2\u05E1\u05E7\u05D9\u05DD \u05D0\u05E4\u05E9\u05E8\u05D5\u05D9\u05D5\u05EA \u05E7\u05D9\u05D3\u05D5\u05DD \u05E9\u05DC \u05EA\u05DB\u05E0\u05D9\u05DD \u05DE\u05E1\u05D5\u05D9\u05DE\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05DB\u05E0\u05D2\u05D3 \u05EA\u05E9\u05DC\u05D5\u05DD. \u05EA\u05DB\u05E0\u05D9\u05DD \u05DE\u05E7\u05D5\u05D3\u05DE\u05D9\u05DD \u05D9\u05EA\u05E4\u05E8\u05E1\u05DE\u05D5 \u05D0\u05DA \u05D5\u05E8\u05E7 \u05D1\u05D0\u05D6\u05D5\u05E8\u05D9\u05DD \u05D4\u05DE\u05D9\u05D5\u05E2\u05D3\u05D9\u05DD \u05DC\u05DB\u05DA \u05D1\u05D9\u05DF \u05E2\u05DE\u05D5\u05D3\u05D9\u05D5 \u05E9\u05DC \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D9\u05E0\u05D4 \u05DE\u05EA\u05E2\u05E8\u05D1\u05EA \u05D1\u05D0\u05D5\u05E4\u05DF \u05DE\u05DC\u05D0\u05DB\u05D5\u05EA\u05D9 \u05D1\u05E1\u05D3\u05E8 \u05D4\u05E6\u05D2\u05EA \u05EA\u05D5\u05E6\u05D0\u05D5\u05EA \u05D4\u05D7\u05D9\u05E4\u05D5\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA. \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DE\u05E1\u05D5\u05E8 \u05E9\u05D9\u05E7\u05D5\u05DC \u05D4\u05D3\u05E2\u05EA \u05D4\u05D1\u05DC\u05E2\u05D3\u05D9 \u05DC\u05D4\u05D7\u05DC\u05D9\u05D8 \u05D0\u05D9\u05DC\u05D5 \u05EA\u05DB\u05E0\u05D9\u05DD \u05D5\u05DE\u05D9\u05D3\u05E2 \u05D9\u05D5\u05E6\u05D2\u05D5 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DE\u05E9\u05DA \u05D4\u05D6\u05DE\u05DF \u05D1\u05D4\u05DD \u05D9\u05D5\u05E6\u05D2\u05D5, \u05DE\u05D4 \u05D9\u05D4\u05D9\u05D4 \u05DE\u05D9\u05E7\u05D5\u05DE\u05DD \u05D5\u05E2\u05D9\u05E6\u05D5\u05D1\u05DD \u05D5\u05DB\u05DC \u05E2\u05E0\u05D9\u05D9\u05DF \u05D0\u05D7\u05E8 \u05D4\u05E7\u05E9\u05D5\u05E8 \u05D1\u05D4\u05DD \u05D5\u05D1\u05DB\u05DC\u05DC \u05D6\u05D0\u05EA \u05DC\u05D4\u05E1\u05D9\u05E8 \u05DB\u05DC \u05EA\u05D5\u05DB\u05DF \u05DE\u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05E4\u05D9 \u05E9\u05D9\u05E7\u05D5\u05DC \u05D3\u05E2\u05EA\u05D4 \u05D4\u05D1\u05DC\u05E2\u05D3\u05D9. \u05DE\u05D9\u05D3\u05E2 \u05E0\u05D5\u05E1\u05E3 \u05D0\u05D5\u05D3\u05D5\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05EA\u05DB\u05D5\u05E0\u05D5\u05EA\u05D9\u05D5 \u05D5\u05D0\u05E4\u05E9\u05E8\u05D5\u05D9\u05D5\u05EA \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D5, \u05EA\u05D5\u05DB\u05DC \u05DC\u05DE\u05E6\u05D5\u05D0 \u05D1\u05E2\u05DE\u05D5\u05D3\u05D9\u05DD \u05D4\u05E8\u05DC\u05D1\u05E0\u05D8\u05D9\u05D9\u05DD \u05E9\u05DC \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(64, "p");
        i0.ɵɵtext(65, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DE\u05E2\u05DE\u05D9\u05D3\u05D4 \u05DC\u05E8\u05E9\u05D5\u05EA\u05DA \u05DB\u05DC\u05D9\u05DD \u05DC\u05E9\u05D9\u05EA\u05D5\u05E3 \u05D5\u05E7\u05D9\u05E9\u05D5\u05E8 \u05EA\u05DB\u05E0\u05D9\u05DD \u05DE\u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05E8\u05E9\u05EA\u05D5\u05EA \u05D7\u05D1\u05E8\u05EA\u05D9\u05D5\u05EA \u05E9\u05D5\u05E0\u05D5\u05EA \u05D5\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05DE\u05E7\u05D5\u05D5\u05E0\u05D9\u05DD \u05E9\u05DC \u05E6\u05D3\u05D3\u05D9\u05DD \u05E9\u05DC\u05D9\u05E9\u05D9\u05D9\u05DD, \u05DB\u05D2\u05D5\u05DF \u05E4\u05D9\u05D9\u05E1\u05D1\u05D5\u05E7. \u05EA\u05E9\u05D5\u05DE\u05EA \u05DC\u05D1\u05DA \u05DB\u05D9 \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05E8\u05E9\u05EA\u05D5\u05EA \u05D7\u05D1\u05E8\u05EA\u05D9\u05D5\u05EA \u05D5\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9 \u05E6\u05D3 \u05D2' \u05DB\u05D0\u05DE\u05D5\u05E8 \u05E0\u05E2\u05E9\u05D4 \u05E2\u05DC \u05D0\u05D7\u05E8\u05D9\u05D5\u05EA\u05DA \u05D1\u05DC\u05D1\u05D3. \u05E9\u05D9\u05DE\u05D5\u05E9 \u05DB\u05D0\u05DE\u05D5\u05E8 \u05D0\u05D9\u05E0\u05D5 \u05E7\u05E9\u05D5\u05E8 \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05D4\u05D5\u05D0 \u05D9\u05D4\u05D9\u05D4 \u05DB\u05E4\u05D5\u05E3 \u05DC\u05E8\u05D5\u05D1 \u05DC\u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05D4\u05DE\u05E9\u05E4\u05D8\u05D9\u05D9\u05DD \u05D4\u05DE\u05E1\u05D3\u05D9\u05E8\u05D9\u05DD \u05D0\u05EA \u05E4\u05E2\u05D9\u05DC\u05D5\u05EA\u05DF \u05E9\u05DC \u05D4\u05E8\u05E9\u05EA\u05D5\u05EA \u05D4\u05D7\u05D1\u05E8\u05EA\u05D9\u05D5\u05EA \u05D5\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9 \u05E6\u05D3 \u05D2' \u05D4\u05DC\u05DC\u05D5, \u05DC\u05E8\u05D1\u05D5\u05EA \u05EA\u05E0\u05D0\u05D9 \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05DE\u05E7\u05D5\u05D5\u05E0\u05D9\u05DD \u05D0\u05DC\u05D4 \u05D5\u05DE\u05D3\u05D9\u05E0\u05D9\u05D5\u05EA \u05D4\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA \u05D4\u05E0\u05D5\u05D4\u05D2\u05EA \u05D1\u05D4\u05DD. \u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05DE\u05E9\u05E4\u05D8\u05D9\u05D9\u05DD \u05D0\u05DC\u05D4 \u05D0\u05D9\u05E0\u05DD \u05DE\u05D8\u05E2\u05DE\u05D4 \u05E9\u05DC \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(66, "p");
        i0.ɵɵelementStart(67, "b");
        i0.ɵɵtext(68, "\u05DC\u05EA\u05E9\u05D5\u05DE\u05EA \u05DC\u05D1\u05DA - \u05D7\u05DC\u05E7 \u05DE\u05D4\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D4\u05E0\u05D9\u05EA\u05E0\u05D9\u05DD \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D4\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF \u05D4\u05DD \"\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05DE\u05D1\u05D5\u05E1\u05E1\u05D9 \u05DE\u05D9\u05E7\u05D5\u05DD\", \u05D4\u05E0\u05E2\u05D6\u05E8\u05D9\u05DD \u05D1\u05D8\u05DB\u05E0\u05D5\u05DC\u05D5\u05D2\u05D9\u05D9\u05EAGPS \u05E2\u05DC-\u05DE\u05E0\u05EA \u05DC\u05E7\u05D1\u05D5\u05E2 \u05D0\u05EA \u05DE\u05D9\u05E7\u05D5\u05DE\u05DA \u05D5\u05DC\u05E1\u05E4\u05E7 \u05DC\u05DA \u05EA\u05D5\u05E6\u05D0\u05D5\u05EA \u05D7\u05D9\u05E4\u05D5\u05E9 \u05D4\u05EA\u05D5\u05D0\u05DE\u05D5\u05EA \u05DB\u05DB\u05DC \u05D4\u05E0\u05D9\u05EA\u05DF \u05DC\u05DE\u05D9\u05E7\u05D5\u05DD \u05D6\u05D4. \u05DC\u05D0 \u05E0\u05D9\u05EA\u05DF \u05DC\u05E1\u05E4\u05E7 \u05D0\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D4\u05DC\u05DC\u05D5 \u05DC\u05DC\u05D0 \u05D4\u05E1\u05DB\u05DE\u05EA\u05DA \u05DC\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D8\u05DB\u05E0\u05D5\u05DC\u05D5\u05D2\u05D9\u05D4 \u05D6\u05D5 \u05D5\u05E2\u05DC-\u05DB\u05DF \u05D1\u05E2\u05E6\u05DD \u05D4\u05EA\u05E7\u05E0\u05EA \u05D4\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF \u05D5\u05D4\u05E1\u05DB\u05DE\u05EA\u05DA \u05DC\u05EA\u05E0\u05D0\u05D9\u05DD \u05D0\u05DC\u05D4 \u05D0\u05EA\u05D4 \u05DE\u05E1\u05DB\u05D9\u05DD \u05DC\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D6\u05D4. \u05EA\u05D5\u05DB\u05DC \u05D1\u05DB\u05DC \u05E2\u05EA \u05DC\u05D1\u05D8\u05DC \u05D0\u05EA \u05D4\u05D0\u05E4\u05E9\u05E8\u05D5\u05EA \u05DC\u05D6\u05D4\u05D5\u05EA \u05D0\u05EA \u05DE\u05D9\u05E7\u05D5\u05DE\u05DA \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D4\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF, \u05E2\u05DC-\u05D9\u05D3\u05D9 \u05D1\u05D9\u05D8\u05D5\u05DC \u05EA\u05DB\u05D5\u05E0\u05D4 \u05D6\u05D5 \u05D1\u05D4\u05D2\u05D3\u05E8\u05D5\u05EA \u05DE\u05DB\u05E9\u05D9\u05E8 \u05D4\u05E7\u05E6\u05D4 \u05E9\u05D1\u05E8\u05E9\u05D5\u05EA\u05DA. \u05E2\u05DC-\u05DE\u05E0\u05EA \u05DC\u05E6\u05E4\u05D5\u05EA \u05D1\u05EA\u05D5\u05E6\u05D0\u05D5\u05EA \u05D7\u05D9\u05E4\u05D5\u05E9 \u05D4\u05EA\u05D5\u05D0\u05DE\u05D5\u05EA \u05D0\u05EA \u05DE\u05D9\u05E7\u05D5\u05DE\u05DA \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D4\u05D0\u05EA\u05E8, \u05D9\u05D4\u05D9\u05D4 \u05E2\u05DC\u05D9\u05DA \u05DC\u05D4\u05E7\u05DC\u05D9\u05D3 \u05D0\u05EA \u05DB\u05EA\u05D5\u05D1\u05EA\u05DA \u05D1\u05DE\u05E7\u05D5\u05DD \u05D4\u05DE\u05D9\u05D5\u05E2\u05D3 \u05DC\u05DB\u05DA \u05D1\u05D0\u05EA\u05E8, \u05D0\u05DD \u05EA\u05D1\u05D7\u05E8 \u05DC\u05E2\u05E9\u05D5\u05EA \u05DB\u05DF, \u05DC\u05E4\u05D9 \u05E9\u05D9\u05E7\u05D5\u05DC \u05D3\u05E2\u05EA\u05DA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(69, "p");
        i0.ɵɵtext(70, "\u05E7\u05D1\u05DC\u05EA \u05D5\u05E9\u05DC\u05D9\u05D7\u05EA \u05DE\u05D9\u05D3\u05E2 \u05D1\u05D6\u05DE\u05DF \u05D0\u05DE\u05EA \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D3\u05D5\u05E8\u05E9\u05D9\u05DD \u05D7\u05D9\u05D1\u05D5\u05E8 \u05DE\u05E7\u05D5\u05D5\u05DF \u05E9\u05DC \u05D4\u05DE\u05D7\u05E9\u05D1 \u05D0\u05D5 \u05DE\u05DB\u05E9\u05D9\u05E8 \u05D4\u05E7\u05E6\u05D4 \u05E9\u05D1\u05E8\u05E9\u05D5\u05EA\u05DA \u05DC\u05D0\u05D9\u05E0\u05D8\u05E8\u05E0\u05D8. \u05E2\u05DC\u05D5\u05D9\u05D5\u05EA \u05D7\u05D9\u05D1\u05D5\u05E8 \u05DB\u05D0\u05DE\u05D5\u05E8 \u05DB\u05E4\u05D5\u05E4\u05D5\u05EA \u05DC\u05D4\u05E1\u05DB\u05DE\u05D9\u05DD \u05E9\u05D1\u05D9\u05E0\u05DA \u05DC\u05D1\u05D9\u05DF \u05E1\u05E4\u05E7\u05D9 \u05D4\u05EA\u05E7\u05E9\u05D5\u05E8\u05EA \u05E9\u05DC\u05DA \u05D5\u05DC\u05EA\u05E9\u05DC\u05D5\u05DE\u05D9\u05DD \u05D4\u05D7\u05DC\u05D9\u05DD \u05E2\u05DC\u05D9\u05DA \u05DC\u05E4\u05D9\u05D4\u05DD \u05D5\u05D4\u05DF \u05DE\u05E6\u05D5\u05D9\u05D5\u05EA \u05D1\u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05D4\u05DE\u05DC\u05D0\u05D4.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(71, "h2", 5);
        i0.ɵɵtext(72, "\u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(73, "p");
        i0.ɵɵtext(74, "\u05D4\u05E0\u05DA \u05E8\u05E9\u05D0\u05D9 \u05DC\u05D4\u05E9\u05EA\u05DE\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D1\u05EA\u05DB\u05E0\u05D9\u05D5 \u05D5\u05D1\u05D0\u05E4\u05E9\u05E8\u05D5\u05D9\u05D5\u05EA \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05E9\u05D4\u05D5\u05D0 \u05DE\u05E6\u05D9\u05E2, \u05D1\u05D4\u05EA\u05D0\u05DD \u05DC\u05DB\u05DC\u05DC\u05D9\u05DD \u05D4\u05DE\u05E4\u05D5\u05E8\u05D8\u05D9\u05DD \u05DC\u05D4\u05DC\u05DF. \u05D0\u05D9\u05DF \u05DC\u05D4\u05E9\u05EA\u05DE\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05D0\u05D5\u05E4\u05DF \u05D0\u05D7\u05E8, \u05D0\u05DC\u05D0 \u05D0\u05DD \u05DB\u05DF \u05E7\u05D9\u05D1\u05DC\u05EA \u05D0\u05EA \u05D4\u05E1\u05DB\u05DE\u05EA\u05D4 \u05D4\u05DE\u05E4\u05D5\u05E8\u05E9\u05EA \u05E9\u05DC \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05DB\u05DA, \u05DE\u05E8\u05D0\u05E9, \u05D1\u05DB\u05EA\u05D1 \u05D5\u05D1\u05DB\u05E4\u05D5\u05E3 \u05DC\u05EA\u05E0\u05D0\u05D9 \u05D0\u05D5\u05EA\u05D4 \u05D4\u05E1\u05DB\u05DE\u05D4 (\u05DB\u05DB\u05DC \u05E9\u05EA\u05D9\u05E0\u05EA\u05DF) - ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(75, "ul");
        i0.ɵɵelementStart(76, "li");
        i0.ɵɵtext(77, "\u05D0\u05D9\u05DF \u05DC\u05E2\u05E9\u05D5\u05EA \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05DB\u05DC \u05E9\u05D9\u05DE\u05D5\u05E9 \u05D4\u05DE\u05D4\u05D5\u05D5\u05D4 \u05D0\u05D5 \u05D4\u05E2\u05DC\u05D5\u05DC \u05DC\u05D4\u05D5\u05D5\u05EA \u05E2\u05D1\u05D9\u05E8\u05D4 \u05D0\u05D5 \u05E2\u05D5\u05D5\u05DC\u05D4 \u05D0\u05D6\u05E8\u05D7\u05D9\u05EA \u05D1\u05D4\u05EA\u05D0\u05DD \u05DC\u05D4\u05D5\u05E8\u05D0\u05D5\u05EA \u05DB\u05DC \u05D3\u05D9\u05DF \u05D5/\u05D0\u05D5 \u05D4\u05E4\u05D5\u05D2\u05E2 \u05D1\u05D6\u05DB\u05D5\u05D9\u05D5\u05EA\u05D9\u05D5 \u05E9\u05DC \u05E6\u05D3 \u05E9\u05DC\u05D9\u05E9\u05D9 \u05DB\u05DC\u05E9\u05D4\u05D5;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(78, "li");
        i0.ɵɵtext(79, "\u05D4\u05E0\u05DA \u05E8\u05E9\u05D0\u05D9 \u05DC\u05D4\u05E9\u05EA\u05DE\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05DE\u05D8\u05E8\u05D5\u05EA \u05E9\u05D0\u05D9\u05E0\u05DF \u05DE\u05E1\u05D7\u05E8\u05D9\u05D5\u05EA \u05D1\u05DC\u05D1\u05D3 \u05D5\u05D6\u05D0\u05EA \u05DE\u05D1\u05DC\u05D9 \u05DC\u05D2\u05E8\u05D5\u05E2 \u05DE\u05D6\u05DB\u05D5\u05EA\u05D4 \u05E9\u05DC \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D0\u05E4\u05E9\u05E8 \u05E9\u05D9\u05DE\u05D5\u05E9 \u05DE\u05E1\u05D7\u05E8\u05D9 \u05DE\u05D5\u05D2\u05D1\u05DC \u05D1\u05D4\u05D9\u05E7\u05E4\u05D5 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D1\u05E2\u05DC\u05D9 \u05E2\u05E1\u05E7\u05D9\u05DD, \u05DC\u05E6\u05D5\u05E8\u05DA \u05DE\u05D8\u05E8\u05D5\u05EA \u05D4\u05DE\u05E7\u05D3\u05DE\u05D5\u05EA \u05D0\u05EA \u05D1\u05D9\u05EA \u05D4\u05E2\u05E1\u05E7 \u05E9\u05D1\u05D1\u05E2\u05DC\u05D5\u05EA\u05DD \u05D1\u05DC\u05D1\u05D3; \u05D0\u05D9\u05DF \u05DC\u05D4\u05E2\u05EA\u05D9\u05E7 \u05D5\u05DC\u05D4\u05E9\u05EA\u05DE\u05E9, \u05D0\u05D5 \u05DC\u05D0\u05E4\u05E9\u05E8 \u05DC\u05D0\u05D7\u05E8\u05D9\u05DD \u05DC\u05D4\u05E2\u05EA\u05D9\u05E7 \u05D5\u05DC\u05D4\u05E9\u05EA\u05DE\u05E9, \u05D1\u05DB\u05DC \u05D3\u05E8\u05DA \u05D0\u05D7\u05E8\u05EA \u05D1\u05EA\u05DB\u05E0\u05D9\u05DD \u05DE\u05EA\u05D5\u05DA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DC\u05E8\u05D1\u05D5\u05EA \u05D1\u05D0\u05EA\u05E8\u05D9\u05DD \u05D5\u05D1\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05E0\u05D9\u05DD \u05D0\u05D7\u05E8\u05D9\u05DD, \u05D1\u05E4\u05E8\u05E1\u05D5\u05DE\u05D9\u05DD \u05D0\u05DC\u05E7\u05D8\u05E8\u05D5\u05E0\u05D9\u05D9\u05DD, \u05D1\u05E4\u05E8\u05E1\u05D5\u05DE\u05D9 \u05D3\u05E4\u05D5\u05E1 \u05D5\u05DB\u05D9\u05D5\"\u05D1, \u05DC\u05DB\u05DC \u05DE\u05D8\u05E8\u05D4 \u05D0\u05D7\u05E8\u05EA;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(80, "li");
        i0.ɵɵtext(81, "\u05DB\u05DB\u05DC \u05E9\u05E0\u05E8\u05E9\u05DE\u05EA \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05D4\u05EA\u05D0\u05DD \u05DC\u05DE\u05E4\u05D5\u05E8\u05D8 \u05D1\u05EA\u05E0\u05D0\u05D9\u05DD \u05D0\u05DC\u05D4, \u05D4\u05E0\u05DA \u05E8\u05E9\u05D0\u05D9 \u05DC\u05D4\u05E9\u05EA\u05DE\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D7\u05E9\u05D1\u05D5\u05DF \u05D4\u05DE\u05E9\u05EA\u05DE\u05E9 \u05E9\u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA\u05D5 \u05E0\u05E8\u05E9\u05DE\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(82, "li");
        i0.ɵɵtext(83, "\u05D0\u05D9\u05DF \u05DC\u05D4\u05E4\u05E2\u05D9\u05DC \u05D0\u05DE\u05E6\u05E2\u05D9\u05DD \u05DC\u05E2\u05E7\u05D9\u05E4\u05D4, \u05E0\u05D8\u05E8\u05D5\u05DC \u05D0\u05D5 \u05D4\u05EA\u05D2\u05D1\u05E8\u05D5\u05EA \u05E2\u05DC \u05DE\u05E0\u05D2\u05E0\u05D5\u05E0\u05D9 \u05D4\u05D0\u05D1\u05D8\u05D7\u05D4 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D5\u05D0\u05D9\u05DF \u05DC\u05E4\u05D2\u05D5\u05E2 \u05D0\u05D5 \u05DC\u05D4\u05E4\u05E8\u05D9\u05E2 \u05D1\u05D3\u05E8\u05DA \u05DB\u05DC\u05E9\u05D4\u05D9 \u05DC\u05E4\u05E2\u05D9\u05DC\u05D5\u05EA\u05D5 \u05D4\u05EA\u05E7\u05D9\u05E0\u05D4 \u05E9\u05DC \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(84, "li");
        i0.ɵɵtext(85, "\u05D0\u05D9\u05DF \u05DC\u05D4\u05E4\u05E2\u05D9\u05DC \u05D0\u05D5 \u05DC\u05D0\u05E4\u05E9\u05E8 \u05DC\u05D4\u05E4\u05E2\u05D9\u05DC \u05DB\u05DC \u05D9\u05D9\u05E9\u05D5\u05DD \u05DE\u05D7\u05E9\u05D1 \u05D0\u05D5 \u05DB\u05DC \u05D0\u05DE\u05E6\u05E2\u05D9 \u05D0\u05D7\u05E8, \u05DC\u05E8\u05D1\u05D5\u05EA \u05EA\u05D5\u05DB\u05E0\u05D5\u05EA \u05DE\u05E1\u05D5\u05D2 Crawlers, Robots \u05D5\u05DB\u05D3\u05D5\u05DE\u05D4, \u05DC\u05E9\u05DD \u05D7\u05D9\u05E4\u05D5\u05E9, \u05E1\u05E8\u05D9\u05E7\u05D4, \u05D4\u05E2\u05EA\u05E7\u05D4 \u05D0\u05D5 \u05D0\u05D7\u05D6\u05D5\u05E8 \u05D0\u05D5\u05D8\u05D5\u05DE\u05D8\u05D9 \u05E9\u05DC \u05EA\u05DB\u05E0\u05D9\u05DD \u05DE\u05D4\u05E9\u05D9\u05E8\u05D5\u05EA. \u05D1\u05DB\u05DC\u05DC \u05D6\u05D4, \u05D0\u05D9\u05DF \u05DC\u05D9\u05E6\u05D5\u05E8 \u05D5\u05D0\u05D9\u05DF \u05DC\u05D4\u05E9\u05EA\u05DE\u05E9 \u05D1\u05D0\u05DE\u05E6\u05E2\u05D9\u05DD \u05DB\u05D0\u05DE\u05D5\u05E8 \u05DC\u05E9\u05DD \u05D9\u05E6\u05D9\u05E8\u05EA \u05DC\u05E7\u05D8, \u05D0\u05D5\u05E1\u05E3 \u05D0\u05D5 \u05DE\u05D0\u05D2\u05E8 \u05E9\u05D9\u05DB\u05D9\u05DC\u05D5 \u05EA\u05DB\u05E0\u05D9\u05DD \u05D0\u05D5 \u05DE\u05D9\u05D3\u05E2 \u05DE\u05D4\u05E9\u05D9\u05E8\u05D5\u05EA;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(86, "li");
        i0.ɵɵtext(87, "\u05D0\u05D9\u05DF \u05DC\u05D4\u05E6\u05D9\u05D2 \u05EA\u05DB\u05E0\u05D9\u05DD \u05DE\u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05EA\u05D5\u05DA \u05DE\u05E1\u05D2\u05E8\u05EA (Frame), \u05D2\u05DC\u05D5\u05D9\u05D4 \u05D0\u05D5 \u05E1\u05DE\u05D5\u05D9\u05D4, \u05D0\u05DC\u05D0 \u05D1\u05D0\u05D9\u05E9\u05D5\u05E8 \u05DE\u05E8\u05D0\u05E9 \u05D5\u05D1\u05DB\u05EA\u05D1 \u05E9\u05DC \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA. \u05D0\u05D9\u05DF \u05DC\u05D4\u05E6\u05D9\u05D2 \u05EA\u05DB\u05E0\u05D9\u05DD \u05DE\u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05DB\u05DC \u05D3\u05E8\u05DA \u05E9\u05D4\u05D9\u05D0 \u2013 \u05D5\u05D1\u05DB\u05DC\u05DC \u05D6\u05D4 \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05DB\u05DC \u05EA\u05D5\u05DB\u05E0\u05D4, \u05DE\u05DB\u05E9\u05D9\u05E8, \u05D0\u05D1\u05D9\u05D6\u05E8 \u05D0\u05D5 \u05E4\u05E8\u05D5\u05D8\u05D5\u05E7\u05D5\u05DC \u05EA\u05E7\u05E9\u05D5\u05E8\u05EA \u05D4\u05DE\u05E9\u05E0\u05D9\u05DD \u05D0\u05EA \u05E2\u05D9\u05E6\u05D5\u05D1\u05DD \u05D0\u05D5 \u05DE\u05D7\u05E1\u05D9\u05E8\u05D9\u05DD \u05DE\u05D4\u05DD \u05EA\u05DB\u05E0\u05D9\u05DD \u05DB\u05DC\u05E9\u05D4\u05DD \u05D5\u05D1\u05E4\u05E8\u05D8 \u05E4\u05E8\u05E1\u05D5\u05DE\u05D5\u05EA \u05D5\u05EA\u05DB\u05E0\u05D9\u05DD \u05DE\u05E1\u05D7\u05E8\u05D9\u05D9\u05DD;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(88, "li");
        i0.ɵɵtext(89, "\u05D0\u05D9\u05DF \u05DC\u05E7\u05E9\u05E8 \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05DC\u05EA\u05DB\u05E0\u05D9\u05DD \u05DE\u05DB\u05DC \u05DE\u05E7\u05D5\u05E8 \u05D4\u05DE\u05DB\u05D9\u05DC \u05EA\u05DB\u05E0\u05D9\u05DD \u05E4\u05D5\u05E8\u05E0\u05D5\u05D2\u05E8\u05D0\u05E4\u05D9\u05D9\u05DD, \u05EA\u05DB\u05E0\u05D9\u05DD \u05D4\u05DE\u05E2\u05D5\u05D3\u05D3\u05D9\u05DD \u05DC\u05D2\u05D6\u05E2\u05E0\u05D5\u05EA \u05D0\u05D5 \u05DC\u05D4\u05E4\u05DC\u05D9\u05D4 \u05E4\u05E1\u05D5\u05DC\u05D4, \u05D0\u05D5 \u05D4\u05DE\u05E0\u05D5\u05D2\u05D3\u05D9\u05DD \u05DC\u05D7\u05D5\u05E7, \u05D0\u05D5 \u05D4\u05DE\u05E2\u05D5\u05D3\u05D3\u05D9\u05DD \u05E4\u05E2\u05D9\u05DC\u05D5\u05EA \u05D4\u05DE\u05E0\u05D5\u05D2\u05D3\u05EA \u05DC\u05D7\u05D5\u05E7, \u05D0\u05D5 \u05E9\u05E4\u05E8\u05E1\u05D5\u05DE\u05DD \u05DE\u05E0\u05D5\u05D2\u05D3 \u05DC\u05D7\u05D5\u05E7;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(90, "li");
        i0.ɵɵtext(91, "\u05D0\u05D9\u05DF \u05DC\u05E7\u05E9\u05E8 \u05DC\u05EA\u05DB\u05E0\u05D9\u05DD \u05DE\u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E9\u05D0\u05D9\u05E0\u05DD \u05D3\u05E3 \u05D4\u05D1\u05D9\u05EA \u05E9\u05DC \u05D4\u05D0\u05EA\u05E8 (\"\u05E7\u05D9\u05E9\u05D5\u05E8 \u05E2\u05DE\u05D5\u05E7\") \u05D5\u05D0\u05D9\u05DF \u05DC\u05D4\u05E6\u05D9\u05D2, \u05D0\u05D5 \u05DC\u05E4\u05E8\u05E1\u05DD \u05EA\u05DB\u05E0\u05D9\u05DD \u05DB\u05D0\u05DE\u05D5\u05E8 \u05D1\u05DB\u05DC \u05D3\u05E8\u05DA \u05D0\u05D7\u05E8\u05EA, \u05D0\u05DC\u05D0 \u05D0\u05DD \u05D4\u05E7\u05D9\u05E9\u05D5\u05E8 \u05D4\u05E2\u05DE\u05D5\u05E7 \u05D9\u05D4\u05D9\u05D4 \u05DC\u05D3\u05E3 \u05D0\u05D9\u05E0\u05D8\u05E8\u05E0\u05D8 \u05D1\u05D0\u05EA\u05E8 \u05D1\u05DE\u05DC\u05D5\u05D0\u05D5 \u05D5\u05DB\u05E4\u05D9 \u05E9\u05D4\u05D5\u05D0 (AS IS), \u05DB\u05DA \u05E9\u05E0\u05D9\u05EA\u05DF \u05D9\u05D4\u05D9\u05D4 \u05DC\u05E6\u05E4\u05D5\u05EA \u05D5\u05DC\u05D4\u05E9\u05EA\u05DE\u05E9 \u05D1\u05D5 \u05D1\u05D0\u05D5\u05E4\u05DF \u05D4\u05D6\u05D4\u05D4 \u05DC\u05D7\u05DC\u05D5\u05D8\u05D9\u05DF \u05DC\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D5\u05DC\u05E6\u05E4\u05D9\u05D9\u05D4 \u05D1\u05D5 \u05D1\u05D0\u05EA\u05E8. \u05D1\u05DE\u05E1\u05D2\u05E8\u05EA \u05D6\u05D5, \u05D7\u05DC \u05D0\u05D9\u05E1\u05D5\u05E8 \u05DC\u05E7\u05E9\u05E8 \u05DC\u05EA\u05DB\u05E0\u05D9\u05DD \u05DE\u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D1\u05DE\u05E0\u05D5\u05EA\u05E7 \u05DE\u05D3\u05E4\u05D9 \u05D4\u05D0\u05D9\u05E0\u05D8\u05E8\u05E0\u05D8 \u05D0\u05D5 \u05D4\u05D0\u05D6\u05D5\u05E8\u05D9\u05DD \u05E9\u05D1\u05D4\u05DD \u05D4\u05DD \u05DE\u05D5\u05E4\u05D9\u05E2\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA (\u05DC\u05D3\u05D5\u05D2\u05DE\u05D4 - \u05D0\u05E1\u05D5\u05E8 \u05DC\u05E7\u05E9\u05E8 \u05D1\u05DE\u05D9\u05E9\u05E8\u05D9\u05DF \u05DC\u05EA\u05DE\u05D5\u05E0\u05D4 \u05D0\u05D5 \u05DC\u05E7\u05D5\u05D1\u05E5 \u05D2\u05E8\u05E4\u05D9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D0\u05DC\u05D0 \u05DC\u05E2\u05DE\u05D5\u05D3 \u05D4\u05DE\u05DC\u05D0 \u05E9\u05D1\u05D5 \u05D4\u05DD \u05DE\u05D5\u05E4\u05D9\u05E2\u05D9\u05DD). \u05DB\u05DE\u05D5 \u05DB\u05DF, \u05E2\u05DC \u05DB\u05EA\u05D5\u05D1\u05EA\u05D5 \u05D4\u05DE\u05D3\u05D5\u05D9\u05E7\u05EA \u05E9\u05DC \u05D3\u05E3 \u05D4\u05D0\u05D9\u05E0\u05D8\u05E8\u05E0\u05D8 \u05D1\u05D0\u05EA\u05E8 \u05DC\u05D4\u05D5\u05E4\u05D9\u05E2 \u05D1\u05DE\u05E7\u05D5\u05DD \u05D4\u05E8\u05D2\u05D9\u05DC \u05D4\u05DE\u05D9\u05D5\u05E2\u05D3 \u05DC\u05DB\u05DA \u05D1\u05DE\u05DE\u05E9\u05E7 \u05D4\u05DE\u05E9\u05EA\u05DE\u05E9 (\u05DC\u05D3\u05D5\u05D2\u05DE\u05D4 - \u05D1\u05E9\u05D5\u05E8\u05EA \u05D4\u05DB\u05EA\u05D5\u05D1\u05EA (Status Bar) \u05D1\u05D3\u05E4\u05D3\u05E4\u05DF \u05E9\u05DC \u05D4\u05DE\u05E9\u05EA\u05DE\u05E9). \u05D0\u05D9\u05DF \u05DC\u05E9\u05E0\u05D5\u05EA, \u05DC\u05E1\u05DC\u05E3 \u05D0\u05D5 \u05DC\u05D4\u05E1\u05EA\u05D9\u05E8 \u05DB\u05EA\u05D5\u05D1\u05EA \u05D6\u05D5 \u05D5\u05D0\u05D9\u05DF \u05DC\u05D4\u05D7\u05DC\u05D9\u05E4\u05D4 \u05D1\u05DB\u05DC \u05DB\u05EA\u05D5\u05D1\u05EA \u05D0\u05D7\u05E8\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(92, "p");
        i0.ɵɵtext(93, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D0 \u05EA\u05D9\u05E9\u05D0 \u05D1\u05DB\u05DC \u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05DC\u05DB\u05DC \u05E0\u05D6\u05E7 \u05E9\u05D9\u05D9\u05D2\u05E8\u05DD \u05DB\u05EA\u05D5\u05E6\u05D0\u05D4 \u05DE\u05DB\u05DC \u05E7\u05D9\u05E9\u05D5\u05E8 \u05DC\u05EA\u05DB\u05E0\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05DE\u05DB\u05DC \u05D4\u05E6\u05D2\u05D4 \u05D0\u05D5 \u05E4\u05E8\u05E1\u05D5\u05DD \u05E9\u05DC \u05EA\u05DB\u05E0\u05D9\u05DD \u05D1\u05DB\u05DC \u05D3\u05E8\u05DA \u05D0\u05D7\u05E8\u05EA. \u05D4\u05E0\u05DA \u05E0\u05D5\u05E9\u05D0 \u05D1\u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05D4\u05DE\u05DC\u05D0\u05D4 \u05D5\u05D4\u05D1\u05DC\u05E2\u05D3\u05D9\u05EA \u05DC\u05DB\u05DC \u05E7\u05D9\u05E9\u05D5\u05E8, \u05D4\u05E6\u05D2\u05D4 \u05D0\u05D5 \u05E4\u05E8\u05E1\u05D5\u05DD \u05E9\u05DC \u05D4\u05EA\u05DB\u05E0\u05D9\u05DD \u05E9\u05E0\u05E2\u05E9\u05D5 \u05E2\u05DC \u05D9\u05D3\u05DA, \u05D5\u05DE\u05EA\u05D7\u05D9\u05D9\u05D1 \u05DC\u05E9\u05E4\u05D5\u05EA \u05D0\u05EA \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05D2\u05D9\u05DF \u05DB\u05DC \u05E0\u05D6\u05E7 \u05E9\u05D9\u05D9\u05D2\u05E8\u05DD \u05DB\u05EA\u05D5\u05E6\u05D0\u05D4 \u05DE\u05DB\u05DA. ");
        i0.ɵɵelementEnd();
        i0.ɵɵelement(94, "p");
        i0.ɵɵelementStart(95, "h2", 6);
        i0.ɵɵtext(96, "\u05D3\u05D9\u05D5\u05D5\u05E8 \u05E4\u05E8\u05E1\u05D5\u05DE\u05D9 \u05D5\u05D4\u05D5\u05D3\u05E2\u05D5\u05EA");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(97, "p");
        i0.ɵɵelementStart(98, "b");
        i0.ɵɵtext(99, "\u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05D0\u05D9\u05E9\u05D5\u05E8\u05DA \u05D0\u05EA \u05D4\u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD \u05DE\u05E2\u05D9\u05D3 \u05E2\u05DC \u05D4\u05E1\u05DB\u05DE\u05EA\u05DA \u05D4\u05DE\u05E4\u05D5\u05E8\u05E9\u05EA \u05DC\u05E7\u05D1\u05DC\u05EA \u05D3\u05D1\u05E8\u05D9 \u05E4\u05E8\u05E1\u05D5\u05DE\u05EA \u05DE\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5/\u05D0\u05D5 \u05DE\u05D9 \u05DE\u05D8\u05E2\u05DE\u05D4, \u05DB\u05DE\u05E9\u05DE\u05E2\u05D5\u05EA \u05D4\u05E1\u05DB\u05DE\u05D4 \u05D6\u05D5 \u05D1\u05D4\u05D5\u05E8\u05D0\u05D5\u05EA \u05E1\u05E2\u05D9\u05E3 30\u05D0 \u05DC\u05D7\u05D5\u05E7 \u05D4\u05EA\u05E7\u05E9\u05D5\u05E8\u05EA (\u05D1\u05D6\u05E7 \u05D5\u05E9\u05D9\u05D3\u05D5\u05E8\u05D9\u05DD), \u05D4\u05EA\u05E9\u05DE\"\u05D1 \u2013 1982. \u05D3\u05D1\u05E8\u05D9 \u05D4\u05E4\u05E8\u05E1\u05D5\u05DE\u05EA \u05D9\u05E9\u05DC\u05D7\u05D5 \u05D0\u05DC\u05D9\u05DA \u05DC\u05E4\u05D9 \u05D4\u05DE\u05D9\u05D3\u05E2 \u05E9\u05DE\u05E1\u05E8\u05EA \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05DE\u05D4\u05DC\u05DA \u05D4\u05E8\u05D9\u05E9\u05D5\u05DD \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D0\u05D5 \u05D4\u05E4\u05E8\u05D8\u05D9\u05DD \u05E9\u05D0\u05E4\u05E9\u05E8\u05EA \u05DC\u05E0\u05D5 \u05D2\u05D9\u05E9\u05D4 \u05D0\u05DC\u05D9\u05D4\u05DD \u05DB\u05DB\u05DC \u05D5\u05E0\u05E8\u05E9\u05DE\u05EA \u05DC\u05E9\u05D9\u05E8\u05EA \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D7\u05E9\u05D1\u05D5\u05E0\u05DA \u05D1\u05E8\u05E9\u05EA\u05D5\u05EA \u05D7\u05D1\u05E8\u05EA\u05D9\u05D5\u05EA, \u05D0\u05D5 \u05D1\u05DB\u05DC \u05DE\u05E7\u05E8\u05D4 \u05D0\u05D7\u05E8 \u05D1\u05D5 \u05DE\u05E1\u05E8\u05EA \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05EA \u05E4\u05E8\u05D8\u05D9\u05D9\u05DA, \u05DB\u05D2\u05D5\u05DF \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D8\u05E4\u05E1\u05D9 \u05D9\u05E6\u05D9\u05E8\u05EA \u05D4\u05E7\u05E9\u05E8 \u05D4\u05DB\u05DC\u05D5\u05DC\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(100, "p");
        i0.ɵɵelementStart(101, "b");
        i0.ɵɵtext(102, "\u05D1\u05DB\u05DC \u05E2\u05EA, \u05EA\u05D5\u05DB\u05DC \u05DC\u05D7\u05D6\u05D5\u05E8 \u05D1\u05DA \u05DE\u05D4\u05E1\u05DB\u05DE\u05EA\u05DA \u05D5\u05DC\u05D4\u05D5\u05D3\u05D9\u05E2 \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E2\u05DC \u05E1\u05D9\u05E8\u05D5\u05D1 \u05DC\u05E7\u05D1\u05DC\u05EA \u05D3\u05D1\u05E8\u05D9 \u05E4\u05E8\u05E1\u05D5\u05DE\u05EA, \u05D3\u05E8\u05DA \u05DB\u05DC\u05DC \u05D0\u05D5 \u05DE\u05E1\u05D5\u05D2 \u05DE\u05E1\u05D5\u05D9\u05DD, \u05E2\u05DC-\u05D9\u05D3\u05D9 \u05DE\u05E9\u05DC\u05D5\u05D7 \u05D4\u05D5\u05D3\u05E2\u05EA \u05E1\u05D9\u05E8\u05D5\u05D1. \u05D4\u05D3\u05E8\u05DA \u05DC\u05DE\u05E9\u05DC\u05D5\u05D7 \u05D4\u05D5\u05D3\u05E2\u05EA \u05D4\u05E1\u05D9\u05E8\u05D5\u05D1 \u05EA\u05E4\u05D5\u05E8\u05D8 \u05D1\u05D3\u05D1\u05E8\u05D9 \u05D4\u05E4\u05E8\u05E1\u05D5\u05DE\u05EA \u05E9\u05EA\u05E9\u05DC\u05D7 \u05D0\u05DC\u05D9\u05DA \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5/\u05D0\u05D5 \u05DE\u05D9 \u05DE\u05D8\u05E2\u05DE\u05D4. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05E3 \u05EA\u05D4\u05D9\u05D4 \u05E8\u05E9\u05D0\u05D9\u05EA \u05DC\u05D4\u05E9\u05EA\u05DE\u05E9 \u05D1\u05E4\u05E8\u05D8\u05D9\u05DD \u05E9\u05DE\u05E1\u05E8\u05EA \u05DC\u05E6\u05D5\u05E8\u05DA \u05DE\u05E9\u05DC\u05D5\u05D7 \u05E2\u05DC\u05D5\u05E0\u05D9 \u05DE\u05D9\u05D3\u05E2 \u05E9\u05D0\u05D9\u05E0\u05DD \u05DE\u05D4\u05D5\u05D5\u05D9\u05DD \u05D3\u05D1\u05E8 \u05E4\u05E8\u05E1\u05D5\u05DE\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(103, "p");
        i0.ɵɵtext(104, "\u05D1\u05E0\u05D5\u05E1\u05E3, \u05EA\u05E9\u05D5\u05DE\u05EA \u05DC\u05D1\u05DA \u05DB\u05D9 \u05D1\u05E2\u05EA \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF (\u05DB\u05DB\u05DC \u05D5\u05DE\u05DB\u05E9\u05D9\u05E8 \u05D4\u05E7\u05E6\u05D4 \u05E9\u05D1\u05E8\u05E9\u05D5\u05EA\u05DA \u05EA\u05D5\u05DE\u05DA \u05D1\u05DB\u05DA), \u05D9\u05D9\u05EA\u05DB\u05DF \u05D5\u05EA\u05EA\u05D1\u05E7\u05E9 \u05DC\u05D0\u05E9\u05E8 \u05E7\u05D1\u05DC\u05EA \u05D4\u05D5\u05D3\u05E2\u05D5\u05EA \u05DE\u05EA\u05E4\u05E8\u05E6\u05D5\u05EA \u05DE\u05D4\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF (Push Notifications). \u05D1\u05DE\u05D9\u05D3\u05D4 \u05D5\u05EA\u05D0\u05E9\u05E8 \u05E7\u05D1\u05DC\u05EA \u05D4\u05D5\u05D3\u05E2\u05D5\u05EA \u05DB\u05D0\u05DE\u05D5\u05E8, \u05E2\u05E9\u05D5\u05D9\u05D4 \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05E9\u05DC\u05D5\u05D7 \u05D0\u05DC\u05D9\u05DA \u05D4\u05D5\u05D3\u05E2\u05D5\u05EA \u05DE\u05E1\u05D5\u05D2 \u05D6\u05D4 \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05DE\u05DE\u05E9\u05E7 \u05D4\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF \u05D0\u05D5 \u05DE\u05DB\u05E9\u05D9\u05E8 \u05D4\u05E7\u05E6\u05D4 \u05E9\u05D1\u05E8\u05E9\u05D5\u05EA\u05DA. \u05EA\u05E9\u05D5\u05DE\u05EA \u05DC\u05D1\u05DA \u05DB\u05D9 \u05DE\u05E9\u05DC\u05D5\u05D7 \u05D4\u05D5\u05D3\u05E2\u05D5\u05EA \u05DB\u05D0\u05DE\u05D5\u05E8 \u05D4\u05D9\u05D0 \u05EA\u05DB\u05D5\u05E0\u05D4 \u05DE\u05D5\u05D1\u05E0\u05D9\u05EA \u05D1\u05D7\u05DC\u05E7 \u05DE\u05DE\u05DB\u05E9\u05D9\u05E8\u05D9 \u05D4\u05E7\u05E6\u05D4. \u05D0\u05D9\u05DF \u05D1\u05D0\u05E4\u05E9\u05E8\u05D5\u05EA \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05DB\u05D1\u05D5\u05EA \u05D5/\u05D0\u05D5 \u05DC\u05E0\u05EA\u05E7 \u05EA\u05DB\u05D5\u05E0\u05D4 \u05D6\u05D5 \u05D1\u05DE\u05DB\u05E9\u05D9\u05E8\u05DA \u05D5\u05D1\u05DE\u05D9\u05D3\u05D4 \u05D5\u05EA\u05D1\u05D7\u05E8 \u05DC\u05D7\u05D3\u05D5\u05DC \u05DE\u05E7\u05D1\u05DC\u05EA \u05D4\u05D5\u05D3\u05E2\u05D5\u05EA \u05DB\u05D0\u05DE\u05D5\u05E8, \u05D9\u05D4\u05D9\u05D4 \u05E2\u05DC\u05D9\u05DA \u05DC\u05D4\u05E1\u05D9\u05E8 \u05D0\u05EA \u05EA\u05DB\u05D5\u05E0\u05D4 \u05D6\u05D5 \u05D1\u05D9\u05D7\u05E1 \u05DC\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF \u05D1\u05DE\u05D9\u05E9\u05E8\u05D9\u05DF, \u05E2\u05DC-\u05D9\u05D3\u05D9 \u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05EA\u05E4\u05E8\u05D9\u05D8 \u05D4\u05D4\u05D2\u05D3\u05E8\u05D5\u05EA \u05D4\u05E8\u05DC\u05D1\u05E0\u05D8\u05D9 \u05D1\u05DE\u05DB\u05E9\u05D9\u05E8 \u05D4\u05E7\u05E6\u05D4 \u05E9\u05D1\u05E8\u05E9\u05D5\u05EA\u05DA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(105, "h2", 7);
        i0.ɵɵtext(106, "\u05E8\u05D9\u05E9\u05D5\u05DD \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(107, "p");
        i0.ɵɵtext(108, "\u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05E6\u05E4\u05D9\u05D9\u05D4 \u05D1\u05EA\u05DB\u05E0\u05D9\u05D5 (\u05D5\u05D1\u05DB\u05DC\u05DC \u05D6\u05D4 \u05D4\u05D5\u05E8\u05D3\u05EA \u05D4\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF) \u05D0\u05D9\u05E0\u05D5 \u05DE\u05D7\u05D9\u05D9\u05D1 \u05E8\u05D9\u05E9\u05D5\u05DD \u05D0\u05D5 \u05DE\u05E1\u05D9\u05E8\u05EA \u05E4\u05E8\u05D8\u05D9\u05DD \u05DE\u05D6\u05D4\u05D9\u05DD. \u05E2\u05DD \u05D6\u05D0\u05EA, \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D7\u05DC\u05E7 \u05DE\u05EA\u05DB\u05D5\u05E0\u05D5\u05EA\u05D9\u05D5 \u05E9\u05DC \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DB\u05D2\u05D5\u05DF \u05D4\u05D5\u05E1\u05E4\u05EA \u05D5\u05E2\u05D3\u05DB\u05D5\u05DF \u05DE\u05D9\u05D3\u05E2 \u05D5\u05EA\u05DB\u05E0\u05D9\u05DD \u05D0\u05D5\u05D3\u05D5\u05EA \u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D5\u05D1\u05EA\u05D9 \u05E2\u05E1\u05E7 \u05D4\u05DE\u05D5\u05E6\u05D2\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DB\u05EA\u05D9\u05D1\u05EA \u05D1\u05D9\u05E7\u05D5\u05E8\u05D5\u05EA \u05D5\u05DB\u05D9\u05D5\"\u05D1, \u05DB\u05DE\u05D5 \u05D2\u05DD \u05E8\u05D9\u05E9\u05D5\u05DD \u05DB\u05D1\u05E2\u05DC \u05E2\u05E1\u05E7, \u05DE\u05D7\u05D9\u05D9\u05D1 \u05E8\u05D9\u05E9\u05D5\u05DD \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DE\u05E1\u05D9\u05E8\u05EA \u05D4\u05DE\u05D9\u05D3\u05E2 \u05D4\u05DE\u05EA\u05D1\u05E7\u05E9 \u05D1\u05DE\u05D4\u05DC\u05DA \u05D4\u05E8\u05D9\u05E9\u05D5\u05DD \u05D5\u05E7\u05D1\u05DC\u05EA \u05D4\u05E1\u05DB\u05DE\u05EA\u05DA \u05DC\u05EA\u05E0\u05D0\u05D9 \u05D4\u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD. ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(109, "p");
        i0.ɵɵelementStart(110, "b");
        i0.ɵɵtext(111, "\u05D4\u05E8\u05D9\u05E9\u05D5\u05DD \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA \u05DE\u05D5\u05EA\u05E8 \u05DC\u05DE\u05E9\u05EA\u05DE\u05E9\u05D9\u05DD \u05E9\u05D4\u05DD \u05D1\u05E0\u05D9 18 \u05D5\u05DE\u05E2\u05DC\u05D4 \u05D1\u05DC\u05D1\u05D3. \u05D1\u05E8\u05D9\u05E9\u05D5\u05DE\u05DA \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05EA\u05D4 \u05DE\u05D0\u05E9\u05E8 \u05D5\u05DE\u05E6\u05D4\u05D9\u05E8 \u05DB\u05D9 \u05D0\u05EA\u05D4 \u05D1\u05DF 18 \u05D5\u05DE\u05E2\u05DC\u05D4. \u05D1\u05DE\u05D9\u05D3\u05D4 \u05D5\u05D2\u05D9\u05DC\u05DA \u05E0\u05DE\u05D5\u05DA \u05DE-18, \u05EA\u05D5\u05DB\u05DC \u05DC\u05D4\u05D9\u05E8\u05E9\u05DD \u05DC\u05D0\u05EA\u05E8 \u05D5\u05DC\u05D4\u05E9\u05EA\u05DE\u05E9 \u05D1\u05D5 \u05D1\u05D4\u05EA\u05D0\u05DD \u05DC\u05EA\u05E0\u05D0\u05D9\u05DD \u05D0\u05DC\u05D4, \u05E8\u05E7 \u05D1\u05DB\u05E4\u05D5\u05E3 \u05DC\u05E7\u05D1\u05DC\u05EA \u05D4\u05E1\u05DB\u05DE\u05EA\u05DD \u05E9\u05DC \u05D4\u05D5\u05E8\u05D9\u05D9\u05DA \u05D0\u05D5 \u05D4\u05D0\u05E4\u05D5\u05D8\u05E8\u05D5\u05E4\u05D5\u05E1 \u05D4\u05D7\u05D5\u05E7\u05D9 \u05E9\u05DC\u05DA \u05D5\u05D0\u05D9\u05E9\u05D5\u05E8\u05DD \u05D0\u05EA \u05D4\u05D5\u05E8\u05D0\u05D5\u05EA \u05D4\u05D4\u05E1\u05DB\u05DE\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD, \u05DC\u05D0\u05D7\u05E8 \u05E9\u05E7\u05E8\u05D0\u05D5 \u05D0\u05D5\u05EA\u05DD \u05D1\u05E2\u05D9\u05D5\u05DF. ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(112, "p");
        i0.ɵɵtext(113, "\u05D4\u05E8\u05D9\u05E9\u05D5\u05DD \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA \u05D9\u05D9\u05E2\u05E9\u05D4 \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D7\u05E9\u05D1\u05D5\u05E0\u05DA \u05D1\u05E8\u05E9\u05EA\u05D5\u05EA \u05D7\u05D1\u05E8\u05EA\u05D9\u05D5\u05EA \u05DB\u05D2\u05D5\u05DF \u05E4\u05D9\u05D9\u05E1\u05D1\u05D5\u05E7 \u05D5\u05D2\u05D5\u05D2\u05DC+. \u05DB\u05D0\u05E9\u05E8 \u05D0\u05EA\u05D4 \u05E0\u05E8\u05E9\u05DD \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D7\u05E9\u05D1\u05D5\u05DF \u05D4\u05E8\u05E9\u05EA \u05D4\u05D7\u05D1\u05E8\u05EA\u05D9\u05EA \u05E9\u05DC\u05DA, \u05D0\u05EA\u05D4 \u05DE\u05D0\u05E4\u05E9\u05E8 \u05DC\u05E0\u05D5 \u05D2\u05D9\u05E9\u05D4 \u05DC\u05DE\u05D9\u05D3\u05E2 \u05D4\u05D1\u05E1\u05D9\u05E1\u05D9 \u05D0\u05D5\u05D3\u05D5\u05EA\u05D9\u05D9\u05DA \u05D1\u05E4\u05E8\u05D5\u05E4\u05D9\u05DC \u05D4\u05E8\u05E9\u05EA \u05D4\u05D7\u05D1\u05E8\u05EA\u05D9\u05EA \u05E9\u05DC\u05DA, \u05DB\u05D2\u05D5\u05DF \u05E9\u05DE\u05DA, \u05DB\u05EA\u05D5\u05D1\u05EA \u05D4\u05D3\u05D5\u05D0\"\u05DC \u05E9\u05DC\u05DA \u05D5\u05DB\u05DC \u05DE\u05D9\u05D3\u05E2 \u05D5\u05EA\u05D5\u05DB\u05DF \u05D0\u05D7\u05E8 \u05D4\u05D6\u05DE\u05D9\u05DF \u05DC\u05E2\u05D9\u05D5\u05DF \u05D1\u05D7\u05E9\u05D1\u05D5\u05DF \u05D4\u05E8\u05E9\u05EA \u05D4\u05D7\u05D1\u05E8\u05EA\u05D9\u05EA \u05E9\u05DC\u05DA. \u05D2\u05D9\u05E9\u05EA\u05E0\u05D5 \u05DC\u05DE\u05D9\u05D3\u05E2 \u05D5\u05DC\u05EA\u05DB\u05E0\u05D9\u05DD \u05DB\u05D0\u05DE\u05D5\u05E8 \u05DE\u05D5\u05D2\u05D1\u05DC\u05EA \u05DC\u05DE\u05D9\u05D3\u05E2 \u05D5\u05DC\u05EA\u05DB\u05E0\u05D9\u05DD \u05E9\u05DE\u05E4\u05E2\u05D9\u05DC \u05D4\u05E8\u05E9\u05EA \u05D4\u05D7\u05D1\u05E8\u05EA\u05D9\u05EA \u05D1\u05D4 \u05D0\u05EA\u05D4 \u05DE\u05E9\u05EA\u05DE\u05E9 \u05DE\u05D0\u05E4\u05E9\u05E8 \u05DC\u05E0\u05D5 \u05D2\u05D9\u05E9\u05D4 \u05D0\u05DC\u05D9\u05D4\u05DD, \u05D5\u05D6\u05D0\u05EA \u05D1\u05D4\u05EA\u05D0\u05DD \u05DC\u05D4\u05D2\u05D3\u05E8\u05D5\u05EA \u05D4\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA \u05E9\u05DC\u05DA \u05D1\u05D0\u05D5\u05EA\u05D4 \u05E8\u05E9\u05EA \u05D7\u05D1\u05E8\u05EA\u05D9\u05EA, \u05D0\u05D5\u05EA\u05DF \u05D0\u05EA\u05D4 \u05D9\u05DB\u05D5\u05DC \u05DC\u05E9\u05E0\u05D5\u05EA \u05DE\u05E2\u05EA \u05DC\u05E2\u05EA \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05DE\u05DE\u05E9\u05E7 \u05D4\u05E8\u05E9\u05EA \u05D4\u05D7\u05D1\u05E8\u05EA\u05D9\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(114, "p");
        i0.ɵɵtext(115, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E8\u05E9\u05D0\u05D9\u05EA \u05DC\u05E7\u05D1\u05D5\u05E2 \u05D5\u05DC\u05D4\u05D5\u05E1\u05D9\u05E3 \u05DE\u05E2\u05EA \u05DC\u05E2\u05EA \u05D3\u05E8\u05DB\u05D9 \u05E8\u05D9\u05E9\u05D5\u05DD \u05D0\u05D7\u05E8\u05D5\u05EA \u05D5\u05E0\u05D5\u05E1\u05E4\u05D5\u05EA \u05D5\u05D1\u05DB\u05DC\u05DC \u05D6\u05D4 \u05E8\u05D9\u05E9\u05D5\u05DD \u05D9\u05E9\u05D9\u05E8 \u05E9\u05D0\u05D9\u05E0\u05D5 \u05DE\u05D7\u05D9\u05D9\u05D1 \u05D4\u05D6\u05D3\u05D4\u05D5\u05EA \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D7\u05E9\u05D1\u05D5\u05DF \u05E8\u05E9\u05EA \u05D7\u05D1\u05E8\u05EA\u05D9\u05EA. \u05D1\u05DE\u05D4\u05DC\u05DA \u05E8\u05D9\u05E9\u05D5\u05DD \u05DB\u05D0\u05DE\u05D5\u05E8 \u05EA\u05EA\u05D1\u05E7\u05E9 \u05DC\u05DE\u05E1\u05D5\u05E8 \u05DC\u05E0\u05D5 \u05E4\u05E8\u05D8\u05D9\u05DD \u05D0\u05D9\u05E9\u05D9\u05D9\u05DD \u05DE\u05D6\u05D4\u05D9\u05DD \u05D5\u05E4\u05E8\u05D8\u05D9 \u05D9\u05E6\u05D9\u05E8\u05EA \u05E7\u05E9\u05E8 \u05D0\u05D9\u05EA\u05DA, \u05DB\u05D2\u05D5\u05DF \u05E9\u05DE\u05DA \u05D4\u05DE\u05DC\u05D0, \u05DB\u05EA\u05D5\u05D1\u05EA \u05D3\u05D5\u05D0\"\u05DC \u05E4\u05E2\u05D9\u05DC\u05D4, \u05D2\u05D9\u05DC\u05DA \u05D5\u05E2\u05D5\u05D3. \u05D1\u05E0\u05D5\u05E1\u05E3, \u05DB\u05D0\u05E9\u05E8 \u05D0\u05EA\u05D4 \u05E0\u05E8\u05E9\u05DD \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA \u05DB\u05D1\u05E2\u05DC \u05E2\u05E1\u05E7, \u05D0\u05E0\u05D5 \u05E2\u05E9\u05D5\u05D9\u05D9\u05DD \u05DC\u05D3\u05E8\u05D5\u05E9 \u05DE\u05DE\u05DA \u05DE\u05D9\u05D3\u05E2 \u05E0\u05D5\u05E1\u05E3, \u05D5\u05D1\u05DB\u05DC\u05DC \u05D6\u05D4 \u05D0\u05E1\u05DE\u05DB\u05EA\u05D0\u05D5\u05EA \u05DC\u05D4\u05E0\u05D7\u05EA \u05D3\u05E2\u05EA\u05D4 \u05E9\u05DC \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DC\u05E4\u05D9 \u05E9\u05D9\u05E7\u05D5\u05DC \u05D3\u05E2\u05EA\u05D4 \u05D4\u05D1\u05DC\u05E2\u05D3\u05D9, \u05D4\u05DE\u05E2\u05D9\u05D3\u05D5\u05EA \u05DB\u05D9 \u05D4\u05E0\u05DA \u05D1\u05E2\u05DC\u05D9\u05D5 \u05E9\u05DC \u05D4\u05E2\u05E1\u05E7 \u05D4\u05E8\u05DC\u05D1\u05E0\u05D8\u05D9. \u05D1\u05E2\u05DC \u05E2\u05E1\u05E7 \u05D4\u05DE\u05E2\u05D5\u05E0\u05D9\u05D9\u05DF \u05DC\u05E8\u05DB\u05D5\u05E9 \u05E4\u05E8\u05E1\u05D5\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D9\u05EA\u05D1\u05E7\u05E9 \u05D2\u05DD \u05DC\u05DE\u05E1\u05D5\u05E8 \u05E4\u05E8\u05D8\u05D9 \u05D0\u05DE\u05E6\u05E2\u05D9 \u05EA\u05E9\u05DC\u05D5\u05DD \u05D5\u05DB\u05DC \u05DE\u05D9\u05D3\u05E2 \u05E0\u05D5\u05E1\u05E3 \u05D4\u05D3\u05E8\u05D5\u05E9 \u05DC\u05E9\u05DD \u05E8\u05DB\u05D9\u05E9\u05EA \u05E9\u05D9\u05E8\u05D5\u05EA\u05D9 \u05D4\u05E4\u05E8\u05E1\u05D5\u05DD \u05E9\u05DC \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(116, "p");
        i0.ɵɵtext(117, "\u05D1\u05DE\u05D9\u05D3\u05D4 \u05D5\u05EA\u05E8\u05E6\u05D4 \u05DC\u05E4\u05E0\u05D5\u05EA \u05D0\u05DC \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D8\u05D5\u05E4\u05E1 \u05D9\u05E6\u05D9\u05E8\u05EA \u05D4\u05E7\u05E9\u05E8 \u05D4\u05DE\u05E7\u05D5\u05D5\u05DF \u05E9\u05DE\u05E2\u05DE\u05D9\u05D3\u05D4 \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05E8\u05E9\u05D5\u05EA \u05D4\u05DE\u05E9\u05EA\u05DE\u05E9\u05D9\u05DD, \u05EA\u05D9\u05D3\u05E8\u05E9 \u05DC\u05DE\u05E1\u05D5\u05E8 \u05DC\u05E0\u05D5 \u05D0\u05EA \u05E9\u05DE\u05DA, \u05DB\u05EA\u05D5\u05D1\u05EA \u05D4\u05D3\u05D5\u05D0\"\u05DC \u05E9\u05DC\u05DA \u05D5\u05DC\u05D4\u05D6\u05D9\u05DF \u05D0\u05EA \u05EA\u05D5\u05DB\u05DF \u05E4\u05E0\u05D9\u05D9\u05EA\u05DA \u05D1\u05DE\u05E7\u05D5\u05DD \u05D4\u05DE\u05D9\u05D5\u05E2\u05D3 \u05DC\u05DB\u05DA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(118, "p");
        i0.ɵɵelementStart(119, "b");
        i0.ɵɵtext(120, "\u05D1\u05DB\u05DC \u05D0\u05D7\u05D3 \u05D0\u05D7\u05D3 \u05DE\u05D4\u05DE\u05E7\u05E8\u05D9\u05DD \u05D4\u05E0\"\u05DC, \u05E2\u05DC\u05D9\u05DA \u05DC\u05DE\u05E1\u05D5\u05E8 \u05E8\u05E7 \u05E4\u05E8\u05D8\u05D9\u05DD \u05E0\u05DB\u05D5\u05E0\u05D9\u05DD, \u05DE\u05D3\u05D5\u05D9\u05E7\u05D9\u05DD \u05D5\u05DE\u05DC\u05D0\u05D9\u05DD. \u05DE\u05E1\u05D9\u05E8\u05EA \u05E4\u05E8\u05D8\u05D9\u05DD \u05E9\u05D2\u05D5\u05D9\u05D9\u05DD \u05E2\u05DC\u05D5\u05DC\u05D4 \u05DC\u05DE\u05E0\u05D5\u05E2 \u05DE\u05DE\u05DA \u05D0\u05EA \u05D4\u05D0\u05E4\u05E9\u05E8\u05D5\u05EA \u05DC\u05D4\u05E9\u05EA\u05DE\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05D1\u05DE\u05E7\u05E8\u05D4 \u05D4\u05E6\u05D5\u05E8\u05DA \u05DC\u05E1\u05DB\u05DC \u05D9\u05E6\u05D9\u05E8\u05EA \u05E7\u05E9\u05E8 \u05D0\u05EA\u05DA. \u05D0\u05E0\u05D5 \u05E0\u05E6\u05D9\u05D9\u05DF \u05D1\u05DE\u05E4\u05D5\u05E8\u05E9 \u05D0\u05D9\u05DC\u05D5 \u05E9\u05D3\u05D5\u05EA \u05DE\u05D9\u05D3\u05E2 \u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD \u05D4\u05E9\u05DC\u05DE\u05D4. \u05DC\u05DC\u05D0 \u05DE\u05E1\u05D9\u05E8\u05EA \u05D4\u05DE\u05D9\u05D3\u05E2 \u05D4\u05DE\u05EA\u05D1\u05E7\u05E9 \u05D1\u05E9\u05D3\u05D5\u05EA \u05D4\u05D7\u05D5\u05D1\u05D4 \u05D4\u05DC\u05DC\u05D5, \u05DC\u05D0 \u05EA\u05D5\u05DB\u05DC \u05DC\u05D4\u05E9\u05DC\u05D9\u05DD \u05D0\u05EA \u05D4\u05E8\u05D9\u05E9\u05D5\u05DD \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E9\u05D5\u05DE\u05E8\u05EA \u05DC\u05E2\u05E6\u05DE\u05D4 \u05D0\u05EA \u05D4\u05D6\u05DB\u05D5\u05EA \u05DC\u05E0\u05E7\u05D5\u05D8 \u05D1\u05D0\u05DE\u05E6\u05E2\u05D9\u05DD \u05E9\u05D5\u05E0\u05D9\u05DD, \u05D8\u05DB\u05E0\u05D5\u05DC\u05D5\u05D2\u05D9\u05D9\u05DD \u05D5\u05D0\u05D7\u05E8\u05D9\u05DD, \u05DB\u05D3\u05D9 \u05DC\u05D0\u05DE\u05EA \u05D0\u05EA \u05D6\u05D4\u05D5\u05EA\u05DA \u05D5\u05DC\u05D5\u05D5\u05D3\u05D0 \u05D0\u05D5\u05EA\u05D4.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(121, "p");
        i0.ɵɵtext(122, "\u05D4\u05E4\u05E8\u05D8\u05D9\u05DD \u05E9\u05EA\u05DE\u05E1\u05D5\u05E8 \u05D1\u05E2\u05EA \u05E8\u05D9\u05E9\u05D5\u05DD \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA \u05D9\u05D9\u05E9\u05DE\u05E8\u05D5 \u05D1\u05DE\u05D0\u05D2\u05E8 \u05D4\u05DE\u05D9\u05D3\u05E2 \u05E9\u05D1\u05D1\u05E2\u05DC\u05D5\u05EA \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D0 \u05EA\u05E2\u05E9\u05D4 \u05D1\u05E4\u05E8\u05D8\u05D9\u05DA \u05E9\u05D9\u05DE\u05D5\u05E9, \u05D0\u05DC\u05D0 \u05D1\u05D4\u05EA\u05D0\u05DD ");
        i0.ɵɵelementStart(123, "a", 3);
        i0.ɵɵelementStart(124, "b");
        i0.ɵɵtext(125, "\u05DC\u05DE\u05D3\u05D9\u05E0\u05D9\u05D5\u05EA \u05D4\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵtext(126, " \u05E9\u05DC \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D4\u05DE\u05D4\u05D5\u05D5\u05D4 \u05D7\u05DC\u05E7 \u05D1\u05DC\u05EA\u05D9 \u05E0\u05E4\u05E8\u05D3 \u05DE\u05EA\u05E0\u05D0\u05D9 \u05E9\u05D9\u05DE\u05D5\u05E9 \u05D0\u05DC\u05D4.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(127, "p");
        i0.ɵɵelementStart(128, "b");
        i0.ɵɵtext(129, "\u05D9\u05D3\u05D5\u05E2 \u05DC\u05DA \u05D5\u05D0\u05EA\u05D4 \u05DE\u05E1\u05DB\u05D9\u05DD \u05DB\u05D9 \u05D4\u05E0\u05D4\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E8\u05E9\u05D0\u05D9\u05EA \u05DC\u05E4\u05E8\u05E1\u05DD \u05D0\u05EA \u05E9\u05DE\u05DA \u05DC\u05E6\u05D3 \u05DB\u05DC \u05EA\u05D5\u05DB\u05DF \u05E9\u05D8\u05E2\u05E0\u05EA \u05D0\u05D5 \u05D4\u05D6\u05E0\u05EA \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D5\u05D1\u05DB\u05DC\u05DC \u05D6\u05D0\u05EA \u05D1\u05D9\u05E7\u05D5\u05E8\u05D5\u05EA, \u05D5\u05D6\u05D0\u05EA \u05DC\u05E4\u05D9 \u05E9\u05D9\u05E7\u05D5\u05DC \u05D3\u05E2\u05EA\u05D4 \u05D4\u05D1\u05DC\u05E2\u05D3\u05D9.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(130, "p");
        i0.ɵɵtext(131, "\u05DB\u05DB\u05DC \u05D5\u05EA\u05D9\u05D3\u05E8\u05E9 \u05DC\u05D4\u05D6\u05D9\u05DF \u05E9\u05DD \u05DE\u05E9\u05EA\u05DE\u05E9 \u05D5\u05E1\u05D9\u05E1\u05DE\u05D4 \u05DC\u05E6\u05D5\u05E8\u05DA \u05D2\u05D9\u05E9\u05D4 \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD \u05E8\u05D9\u05E9\u05D5\u05DD, \u05D5\u05DB\u05D3\u05D9 \u05DC\u05DE\u05E0\u05D5\u05E2 \u05E9\u05D9\u05DE\u05D5\u05E9 \u05DC\u05E8\u05E2\u05D4 \u05D1\u05D7\u05E9\u05D1\u05D5\u05E0\u05DA \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05E2\u05DC\u05D9\u05DA \u05DC\u05E9\u05DE\u05D5\u05E8 \u05D1\u05E1\u05D5\u05D3\u05D9\u05D5\u05EA \u05D2\u05DE\u05D5\u05E8\u05D4 \u05E2\u05DC \u05E9\u05DD \u05D4\u05DE\u05E9\u05EA\u05DE\u05E9 \u05D5\u05D4\u05E1\u05D9\u05E1\u05DE\u05D4. \u05D4\u05E7\u05E4\u05D3 \u05DC\u05D4\u05D7\u05DC\u05D9\u05E3 \u05D0\u05EA \u05D4\u05E1\u05D9\u05E1\u05DE\u05D4 \u05D1\u05EA\u05D3\u05D9\u05E8\u05D5\u05EA \u05D2\u05D1\u05D5\u05D4\u05D4 \u05DB\u05DB\u05DC \u05D4\u05D0\u05E4\u05E9\u05E8 \u05D5\u05D1\u05DB\u05DC \u05DE\u05E7\u05E8\u05D4 \u05D0\u05D7\u05EA \u05DC\u05DE\u05E1\u05E4\u05E8 \u05D7\u05D5\u05D3\u05E9\u05D9\u05DD \u05DC\u05E4\u05D7\u05D5\u05EA. \u05D4\u05E0\u05DA \u05E0\u05D5\u05E9\u05D0 \u05D1\u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05D4\u05DE\u05DC\u05D0\u05D4 \u05DC\u05DB\u05DC \u05E9\u05D9\u05DE\u05D5\u05E9 \u05E9\u05E0\u05E2\u05E9\u05D4 \u05D1\u05D7\u05E9\u05D1\u05D5\u05E0\u05DA \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05D1\u05DB\u05DC\u05DC \u05D6\u05D0\u05EA \u05E2\u05E7\u05D1 \u05DE\u05D7\u05D3\u05DC\u05DA \u05DC\u05E9\u05DE\u05D5\u05E8 \u05D1\u05E1\u05D5\u05D3\u05D9\u05D5\u05EA \u05D0\u05EA \u05E4\u05E8\u05D8\u05D9 \u05D4\u05D2\u05D9\u05E9\u05D4 \u05DC\u05D7\u05E9\u05D1\u05D5\u05E0\u05DA \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(132, "p");
        i0.ɵɵtext(133, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E8\u05E9\u05D0\u05D9\u05EA \u05E9\u05DC\u05D0 \u05DC\u05D0\u05E4\u05E9\u05E8 \u05DC\u05DA \u05DC\u05D4\u05E9\u05EA\u05DE\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D4\u05D8\u05E2\u05D5\u05E0\u05D9\u05DD \u05E8\u05D9\u05E9\u05D5\u05DD \u05DC\u05E4\u05D9 \u05E9\u05D9\u05E7\u05D5\u05DC \u05D3\u05E2\u05EA\u05D4 \u05D4\u05DE\u05D5\u05D7\u05DC\u05D8. \u05DE\u05D1\u05DC\u05D9 \u05DC\u05D2\u05E8\u05D5\u05E2 \u05DE\u05D4\u05D0\u05DE\u05D5\u05E8 \u05DC\u05E2\u05D9\u05DC, \u05E8\u05E9\u05D0\u05D9\u05EA \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D1\u05D8\u05DC \u05D0\u05EA \u05E8\u05D9\u05E9\u05D5\u05DE\u05DA \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D0\u05D5 \u05DC\u05D7\u05E1\u05D5\u05DD \u05D0\u05EA \u05D2\u05D9\u05E9\u05EA\u05DA \u05D0\u05DC\u05D9\u05D5, \u05D1\u05D9\u05DF \u05D4\u05D9\u05EA\u05E8 \u05D1\u05DB\u05DC \u05D0\u05D7\u05D3 \u05DE\u05D4\u05DE\u05E7\u05E8\u05D9\u05DD \u05D4\u05D1\u05D0\u05D9\u05DD - ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(134, "ul");
        i0.ɵɵelementStart(135, "li");
        i0.ɵɵtext(136, "\u05D0\u05DD \u05DE\u05E1\u05E8\u05EA \u05E4\u05E8\u05D8\u05D9\u05DD \u05E9\u05D2\u05D5\u05D9\u05D9\u05DD \u05D0\u05D5 \u05D1\u05D3\u05D5\u05D9\u05D9\u05DD \u05D1\u05E2\u05EA \u05E8\u05D9\u05E9\u05D5\u05DD \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(137, "li");
        i0.ɵɵtext(138, "\u05D0\u05DD \u05D1\u05D9\u05E6\u05E2\u05EA \u05DE\u05E2\u05E9\u05D4 \u05D0\u05D5 \u05DE\u05D7\u05D3\u05DC \u05D4\u05E4\u05D5\u05D2\u05E2\u05D9\u05DD \u05D0\u05D5 \u05E2\u05DC\u05D5\u05DC\u05D9\u05DD \u05DC\u05E4\u05D2\u05D5\u05E2 \u05D1\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D5 \u05D1\u05E6\u05D3\u05D3\u05D9\u05DD \u05E9\u05DC\u05D9\u05E9\u05D9\u05D9\u05DD \u05DB\u05DC\u05E9\u05D4\u05DD, \u05DC\u05E8\u05D1\u05D5\u05EA \u05DE\u05E9\u05EA\u05DE\u05E9\u05D9\u05DD \u05D0\u05D7\u05E8\u05D9\u05DD \u05D0\u05D5 \u05E1\u05E4\u05E7\u05D9\u05DD \u05D0\u05D5 \u05E9\u05D5\u05EA\u05E4\u05D9\u05DD \u05E9\u05DC \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(139, "li");
        i0.ɵɵtext(140, "\u05D0\u05DD \u05D4\u05E9\u05EA\u05DE\u05E9\u05EA \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D1\u05E6\u05E2 \u05D0\u05D5 \u05DB\u05D3\u05D9 \u05DC\u05E0\u05E1\u05D5\u05EA \u05DC\u05D1\u05E6\u05E2 \u05DE\u05E2\u05E9\u05D4 \u05D1\u05DC\u05EA\u05D9 \u05D7\u05D5\u05E7\u05D9, \u05D0\u05D5 \u05DE\u05E2\u05E9\u05D4 \u05D4\u05E0\u05D7\u05D6\u05D4 \u05E2\u05DC \u05E4\u05E0\u05D9\u05D5 \u05DB\u05D1\u05DC\u05EA\u05D9 \u05D7\u05D5\u05E7\u05D9, \u05D0\u05D5 \u05DB\u05D3\u05D9 \u05DC\u05D0\u05E4\u05E9\u05E8, \u05DC\u05D4\u05E7\u05DC, \u05DC\u05E1\u05D9\u05D9\u05E2 \u05D0\u05D5 \u05DC\u05E2\u05D5\u05D3\u05D3 \u05D1\u05D9\u05E6\u05D5\u05E2\u05D5 \u05E9\u05DC \u05DE\u05E2\u05E9\u05D4 \u05DB\u05D6\u05D4;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(141, "li");
        i0.ɵɵtext(142, "\u05D0\u05DD \u05D4\u05E4\u05E8\u05EA \u05D0\u05EA \u05D0\u05D9\u05DC\u05D5 \u05DE\u05EA\u05E0\u05D0\u05D9 \u05D4\u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(143, "li");
        i0.ɵɵtext(144, "\u05D0\u05DD \u05EA\u05E2\u05E9\u05D4 \u05DB\u05DC \u05E4\u05E2\u05D5\u05DC\u05D4 \u05E9\u05EA\u05DE\u05E0\u05E2 \u05DE\u05D0\u05D7\u05E8\u05D9\u05DD \u05DC\u05D4\u05D9\u05E8\u05E9\u05DD \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D5 \u05DC\u05D4\u05DE\u05E9\u05D9\u05DA \u05D5\u05DC\u05D4\u05E9\u05EA\u05DE\u05E9 \u05D1\u05D5 \u05D1\u05DB\u05DC \u05D3\u05E8\u05DA \u05E9\u05D4\u05D9\u05D0;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(145, "li");
        i0.ɵɵtext(146, "\u05D0\u05DD \u05DE\u05E1\u05E8\u05EA \u05D0\u05EA \u05E9\u05DD \u05D4\u05DE\u05E9\u05EA\u05DE\u05E9 \u05D5\u05D4\u05E1\u05D9\u05E1\u05DE\u05D4 \u05E9\u05D4\u05D5\u05E7\u05E6\u05D5 \u05DC\u05DA, \u05D0\u05DD \u05D4\u05D5\u05E7\u05E6\u05D5, \u05DC\u05E9\u05D9\u05DE\u05D5\u05E9 \u05E9\u05DC \u05E6\u05D3 \u05E9\u05DC\u05D9\u05E9\u05D9;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(147, "li");
        i0.ɵɵtext(148, "\u05D0\u05DD \u05D9\u05E9 \u05DC\u05DA \u05D7\u05D5\u05D1 \u05DB\u05E1\u05E4\u05D9 \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05DC\u05D0 \u05E4\u05E8\u05E2\u05EA \u05D0\u05EA \u05D7\u05D5\u05D1\u05DA \u05DC\u05DE\u05E8\u05D5\u05EA \u05E9\u05D7\u05DC\u05E3 \u05D4\u05DE\u05D5\u05E2\u05D3 \u05E9\u05E0\u05E7\u05D1\u05E2 \u05DC\u05EA\u05E9\u05DC\u05D5\u05DE\u05D5;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(149, "li");
        i0.ɵɵtext(150, "\u05E2\u05DD \u05D4\u05E4\u05E1\u05E7\u05EA \u05E4\u05E2\u05D9\u05DC\u05D5\u05EA \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D5 \u05E1\u05D9\u05D5\u05DD \u05E4\u05E2\u05D9\u05DC\u05D5\u05EA\u05D5 \u05E9\u05DC \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(151, "li");
        i0.ɵɵtext(152, "\u05DC\u05E4\u05D9 \u05E6\u05D5 \u05E9\u05D9\u05E4\u05D5\u05D8\u05D9 \u05D0\u05D5 \u05D4\u05D5\u05E8\u05D0\u05D4 \u05E9\u05DC \u05E8\u05E9\u05D5\u05EA \u05DE\u05D5\u05E1\u05DE\u05DB\u05EA;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(153, "li");
        i0.ɵɵtext(154, "\u05D1\u05DB\u05DC \u05DE\u05E7\u05E8\u05D4 \u05D0\u05D7\u05E8 \u05D1\u05D5 \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E1\u05D1\u05D5\u05E8\u05D4 \u05DB\u05D9 \u05D4\u05D1\u05D9\u05D8\u05D5\u05DC \u05DE\u05D5\u05E6\u05D3\u05E7 \u05D5\u05E2\u05D5\u05DE\u05D3 \u05D1\u05D0\u05DE\u05D5\u05EA \u05D4\u05DE\u05D9\u05D3\u05D4 \u05D4\u05DE\u05E9\u05E4\u05D8\u05D9\u05D5\u05EA \u05D4\u05DE\u05E7\u05D5\u05D1\u05DC\u05D5\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelement(155, "br");
        i0.ɵɵelementStart(156, "b");
        i0.ɵɵtext(157, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E2\u05E9\u05D5\u05D9\u05D4 \u05DC\u05D4\u05E4\u05E2\u05D9\u05DC \u05D0\u05DE\u05E6\u05E2\u05D9\u05DD \u05E9\u05D5\u05E0\u05D9\u05DD \u05DC\u05E0\u05D9\u05D8\u05D5\u05E8 \u05D4\u05E4\u05E2\u05D9\u05DC\u05D5\u05EA \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05E2\u05DC-\u05DE\u05E0\u05EA \u05DC\u05D0\u05EA\u05E8 \u05DE\u05E7\u05E8\u05D9 \u05D4\u05D5\u05E0\u05D0\u05D4 \u05D0\u05D5 \u05E9\u05D9\u05DE\u05D5\u05E9 \u05DC\u05E8\u05E2\u05D4 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E9\u05D5\u05DE\u05E8\u05EA \u05DC\u05E2\u05E6\u05DE\u05D4 \u05D0\u05EA \u05D4\u05D6\u05DB\u05D5\u05EA \u05DC\u05D7\u05E1\u05D5\u05DD \u05D0\u05D5 \u05DC\u05D1\u05D8\u05DC \u05D0\u05EA \u05D2\u05D9\u05E9\u05EA\u05DA \u05D0\u05D5 \u05E8\u05D9\u05E9\u05D5\u05DE\u05DA \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DC\u05E4\u05D9 \u05E9\u05D9\u05E7\u05D5\u05DC \u05D3\u05E2\u05EA\u05D4 \u05D4\u05D1\u05DC\u05E2\u05D3\u05D9, \u05D1\u05DE\u05D9\u05D3\u05D4 \u05D5\u05E1\u05D1\u05E8\u05D4 \u05DB\u05D9 \u05D7\u05E9\u05D1\u05D5\u05E0\u05DA \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05E9\u05D9\u05DE\u05E9 \u05DC\u05D1\u05D9\u05E6\u05D5\u05E2 \u05DE\u05E2\u05E9\u05D9\u05DD \u05DB\u05D0\u05DE\u05D5\u05E8.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(158, "h2", 8);
        i0.ɵɵtext(159, "\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D1\u05EA\u05E9\u05DC\u05D5\u05DD");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(160, "p");
        i0.ɵɵtext(161, "\u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D9\u05E0\u05D5 \u05DB\u05E8\u05D5\u05DA \u05D1\u05EA\u05E9\u05DC\u05D5\u05DD. \u05D0\u05E3 \u05D4\u05D5\u05E8\u05D3\u05EA \u05D4\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF (Download) \u05DE\u05D7\u05E0\u05D5\u05EA \u05D4\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05E0\u05D9\u05DD \u05D4\u05DE\u05E7\u05D5\u05D5\u05E0\u05EA (\u05DB\u05D2\u05D5\u05DF \u05D4- App Store \u05D0\u05D5 Google Play) \u05D0\u05D9\u05E0\u05D4 \u05DB\u05E8\u05D5\u05DB\u05D4 \u05D1\u05EA\u05E9\u05DC\u05D5\u05DD. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E9\u05D5\u05DE\u05E8\u05EA \u05DC\u05E2\u05E6\u05DE\u05D4 \u05D0\u05EA \u05D4\u05D6\u05DB\u05D5\u05EA \u05DC\u05D2\u05D1\u05D5\u05EA \u05EA\u05E9\u05DC\u05D5\u05DD \u05D1\u05EA\u05DE\u05D5\u05E8\u05D4 \u05DC\u05DE\u05EA\u05DF \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD, \u05D0\u05D5 \u05D4\u05D5\u05E8\u05D3\u05EA \u05D4\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF, \u05D0\u05D5 \u05D1\u05D9\u05D7\u05E1 \u05DC\u05DB\u05DC \u05E9\u05D9\u05E8\u05D5\u05EA \u05E2\u05EA\u05D9\u05D3\u05D9 \u05D0\u05D7\u05E8 \u05E9\u05D9\u05D4\u05D5\u05D5\u05D4 \u05D7\u05DC\u05E7 \u05DE\u05D4\u05E9\u05D9\u05E8\u05D5\u05EA. \u05D1\u05DE\u05E7\u05E8\u05D4 \u05DB\u05D6\u05D4, \u05E4\u05E8\u05D8\u05D9 \u05D4\u05EA\u05E9\u05DC\u05D5\u05DD \u05D1\u05D2\u05D9\u05DF \u05DB\u05DC \u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05D3\u05E8\u05DA \u05D4\u05D7\u05D9\u05D5\u05D1 \u05D9\u05E4\u05D5\u05E8\u05E1\u05DE\u05D5 \u05DE\u05E8\u05D0\u05E9 \u05D1\u05E2\u05DE\u05D5\u05D3\u05D9\u05DD \u05D4\u05E8\u05DC\u05D1\u05E0\u05D8\u05D9\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(162, "p");
        i0.ɵɵtext(163, "\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D1\u05EA\u05E9\u05DC\u05D5\u05DD \u05D4\u05DE\u05D9\u05D5\u05E2\u05D3\u05D9\u05DD \u05DC\u05D1\u05E2\u05DC\u05D9 \u05E2\u05E1\u05E7\u05D9\u05DD \u05DB\u05E8\u05D5\u05DB\u05D9\u05DD \u05D1\u05EA\u05E9\u05DC\u05D5\u05DD \u05D4\u05EA\u05DE\u05D5\u05E8\u05D4 \u05D4\u05E0\u05D3\u05E8\u05E9\u05EA \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA. \u05EA\u05E0\u05D0\u05D9 \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05DB\u05D0\u05DE\u05D5\u05E8 (\u05D0\u05D5 \u05DB\u05DC \u05E9\u05D9\u05E8\u05D5\u05EA \u05E2\u05EA\u05D9\u05D3\u05D9 \u05E9\u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D5 \u05D9\u05D4\u05D9\u05D4 \u05DB\u05E8\u05D5\u05DA \u05D1\u05EA\u05E9\u05DC\u05D5\u05DD) \u05D5\u05D4\u05EA\u05E9\u05DC\u05D5\u05DD \u05D1\u05D2\u05D9\u05E0\u05DD \u05D9\u05EA\u05E4\u05E8\u05E1\u05DE\u05D5 \u05D1\u05DE\u05E7\u05D5\u05DE\u05D5\u05EA \u05D4\u05DE\u05D9\u05D5\u05E2\u05D3\u05D9\u05DD \u05DC\u05DB\u05DA \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D0\u05D5 \u05D9\u05D9\u05DE\u05E1\u05E8\u05D5 \u05DC\u05DA \u05D1\u05E2\"\u05E4 \u05D0\u05D5 \u05D1\u05DB\u05EA\u05D1 \u05E2\u05DC-\u05D9\u05D3\u05D9 \u05E0\u05E6\u05D9\u05D2\u05D9 \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA. \u05D4\u05EA\u05DE\u05D5\u05E8\u05D4 \u05EA\u05D9\u05D2\u05D1\u05D4 \u05DE\u05DE\u05DA \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05DB\u05E8\u05D8\u05D9\u05E1 \u05D0\u05E9\u05E8\u05D0\u05D9 \u05E9\u05D0\u05EA \u05E4\u05E8\u05D8\u05D9\u05D5 \u05D4\u05DE\u05DC\u05D0\u05D9\u05DD \u05D9\u05D4\u05D9\u05D4 \u05E2\u05DC\u05D9\u05D9\u05DA \u05DC\u05DE\u05E1\u05D5\u05E8 \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D0\u05D5 \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D3\u05E8\u05DB\u05D9 \u05D7\u05D9\u05D5\u05D1 \u05D0\u05D7\u05E8\u05D5\u05EA \u05D5\u05E0\u05D5\u05E1\u05E4\u05D5\u05EA \u05DB\u05E4\u05D9 \u05E9\u05D9\u05D4\u05D9\u05D5 \u05D6\u05DE\u05D9\u05E0\u05D5\u05EA \u05D1\u05DE\u05E1\u05D2\u05E8\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DE\u05E2\u05EA \u05DC\u05E2\u05EA \u05D5\u05DC\u05E4\u05D9 \u05E9\u05D9\u05E7\u05D5\u05DC \u05D3\u05E2\u05EA\u05D4 \u05D4\u05D1\u05DC\u05E2\u05D3\u05D9 \u05E9\u05DC \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D9\u05E0\u05D4 \u05E0\u05D5\u05D4\u05D2\u05EA \u05DC\u05E9\u05DE\u05D5\u05E8 \u05D0\u05EA \u05E4\u05E8\u05D8\u05D9 \u05D0\u05DE\u05E6\u05E2\u05D9 \u05D4\u05EA\u05E9\u05DC\u05D5\u05DD \u05D1\u05DE\u05D0\u05D2\u05E8\u05D9 \u05D4\u05DE\u05D9\u05D3\u05E2 \u05E9\u05D1\u05D1\u05E2\u05DC\u05D5\u05EA\u05D4.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(164, "p");
        i0.ɵɵtext(165, "\u05D1\u05D9\u05E6\u05D5\u05E2 \u05D4\u05EA\u05E9\u05DC\u05D5\u05DD \u05E2\u05E9\u05D5\u05D9 \u05DC\u05D4\u05D9\u05D5\u05EA \u05DB\u05E8\u05D5\u05DA \u05D1\u05E2\u05DE\u05DC\u05D5\u05EA \u05E9\u05D5\u05E0\u05D5\u05EA \u05E9\u05D9\u05D9\u05D2\u05D1\u05D5 \u05DE\u05DE\u05DA \u05E2\u05DC-\u05D9\u05D3\u05D9 \u05DE\u05E4\u05E2\u05D9\u05DC\u05D9 \u05E9\u05D9\u05E8\u05D5\u05EA \u05D4\u05E1\u05DC\u05D9\u05E7\u05D4 \u05D0\u05D5 \u05D4\u05EA\u05E9\u05DC\u05D5\u05DD \u05D1\u05D4\u05DD \u05EA\u05E9\u05EA\u05DE\u05E9 \u05D5\u05D1\u05D4\u05EA\u05D0\u05DD \u05DC\u05EA\u05E0\u05D0\u05D9\u05DD \u05E9\u05DC \u05E6\u05D3\u05D3\u05D9\u05DD \u05E9\u05DC\u05D9\u05E9\u05D9\u05D9\u05DD \u05D0\u05DC\u05D4. \u05D4\u05E0\u05DA \u05E0\u05D5\u05E9\u05D0 \u05D1\u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05D4\u05D1\u05DC\u05E2\u05D3\u05D9\u05EA \u05DC\u05E9\u05D0\u05EA \u05D1\u05DB\u05DC \u05E2\u05DE\u05DC\u05D4 \u05DB\u05D0\u05DE\u05D5\u05E8. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E9\u05D5\u05DE\u05E8\u05EA \u05DC\u05E2\u05E6\u05DE\u05D4 \u05D0\u05EA \u05D4\u05D6\u05DB\u05D5\u05EA \u05DC\u05D4\u05E4\u05E1\u05D9\u05E7 \u05D0\u05EA \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D0\u05DE\u05E6\u05E2\u05D9 \u05EA\u05E9\u05DC\u05D5\u05DD \u05DB\u05DC\u05E9\u05D4\u05D5 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DC\u05D4\u05EA\u05D9\u05E8 \u05EA\u05E9\u05DC\u05D5\u05DD \u05D1\u05D0\u05DE\u05E6\u05E2\u05D9\u05DD \u05E0\u05D5\u05E1\u05E4\u05D9\u05DD \u05D5\u05DC\u05D4\u05D7\u05D9\u05DC \u05D4\u05E1\u05D3\u05E8\u05D9 \u05EA\u05E9\u05DC\u05D5\u05DD \u05E9\u05D5\u05E0\u05D9\u05DD \u05E2\u05DC \u05E1\u05D5\u05D2\u05D9 \u05DB\u05E8\u05D8\u05D9\u05E1\u05D9 \u05D4\u05D0\u05E9\u05E8\u05D0\u05D9 \u05D0\u05D5 \u05D0\u05DE\u05E6\u05E2\u05D9 \u05D4\u05EA\u05E9\u05DC\u05D5\u05DD \u05D0\u05D5\u05EA\u05DD \u05EA\u05DB\u05D1\u05D3 \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA. \u05D1\u05DB\u05DC \u05DE\u05E7\u05E8\u05D4, \u05D0\u05D9\u05DF \u05DC\u05D4\u05E9\u05EA\u05DE\u05E9 \u05D1\u05D0\u05DE\u05E6\u05E2\u05D9 \u05EA\u05E9\u05DC\u05D5\u05DD \u05E9\u05D0\u05D9\u05E0\u05D5 \u05E9\u05D9\u05D9\u05DA \u05DC\u05DA \u05D5\u05DC\u05DC\u05D0 \u05D4\u05E8\u05E9\u05D0\u05D5\u05EA \u05D4\u05E0\u05D3\u05E8\u05E9\u05D5\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(166, "p");
        i0.ɵɵelementStart(167, "b");
        i0.ɵɵtext(168, "\u05D1\u05D9\u05D8\u05D5\u05DC \u05E2\u05E1\u05E7\u05D4");
        i0.ɵɵelementEnd();
        i0.ɵɵtext(169, " \u2013 \u05E2\u05E1\u05E7\u05EA \u05E9\u05D9\u05E8\u05D5\u05EA\u05D9 \u05D4\u05E7\u05D9\u05D3\u05D5\u05DD \u05D4\u05D9\u05D0 \u05E2\u05E1\u05E7\u05D4 \u05DE\u05EA\u05DE\u05E9\u05DB\u05EA \u05DC\u05EA\u05E7\u05D5\u05E4\u05D4 \u05DC\u05D0 \u05E7\u05E6\u05D5\u05D1\u05D4, \u05D0\u05E9\u05E8 \u05D4\u05EA\u05E9\u05DC\u05D5\u05DD \u05E2\u05D1\u05D5\u05E8\u05D4 \u05DE\u05EA\u05D7\u05D3\u05E9 \u05DE\u05D3\u05D9 \u05D7\u05D5\u05D3\u05E9. \u05D1\u05DE\u05D9\u05D3\u05D4 \u05D5\u05D4\u05DC\u05E7\u05D5\u05D7 \u05DE\u05E2\u05D5\u05E0\u05D9\u05D9\u05DF \u05DC\u05D1\u05D8\u05DC \u05D0\u05EA \u05D4\u05E2\u05E1\u05E7\u05D4, \u05E2\u05DC\u05D9\u05D5 \u05DC\u05D4\u05DB\u05E0\u05E1 \u05DC\u05D0\u05D9\u05D6\u05D5\u05E8 \u05D4\u05E0\u05D9\u05D4\u05D5\u05DC \u05D4\u05D0\u05D9\u05E9\u05D9 \u05D1\u05DC\u05D7\u05D9\u05E6\u05D4 \u05E2\u05DC \u05DB\u05E4\u05EA\u05D5\u05E8 \u05DB\u05E0\u05D9\u05E1\u05EA \u05D1\u05E2\u05DC\u05D9 \u05E2\u05E1\u05E7\u05D9\u05DD \u05E9\u05E0\u05DE\u05E6\u05D0 \u05D1\u05EA\u05E4\u05E8\u05D9\u05D8 \u05D4\u05E8\u05D0\u05E9\u05D9, \u05D5\u05DC\u05E2\u05E6\u05D5\u05E8 \u05D0\u05EA \u05D4\u05E4\u05E8\u05E1\u05D5\u05DD \u05E2\u05DC \u05D9\u05D3\u05D9 \u05E9\u05D9\u05E0\u05D5\u05D9 \u05D4\u05D2\u05D3\u05E8\u05D5\u05EA \u05D4\u05DE\u05E9\u05EA\u05DE\u05E9 . \u05D4\u05D1\u05D9\u05D8\u05D5\u05DC \u05D9\u05D4\u05D9\u05D4 \u05EA\u05E7\u05E3 \u05DE\u05E8\u05D2\u05E2 \u05D4\u05D1\u05D9\u05D8\u05D5\u05DC, \u05D0\u05DA \u05D4\u05EA\u05E9\u05DC\u05D5\u05DE\u05D9\u05DD \u05E2\u05D1\u05D5\u05E8 \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D9\u05DE\u05E9\u05DB\u05D5 \u05E2\u05D3 \u05DC\u05EA\u05D5\u05DD \u05D4\u05D7\u05D5\u05D3\u05E9 \u05D4\u05E7\u05DC\u05E0\u05D3\u05E8\u05D9 \u05D1\u05D5 \u05D1\u05D5\u05D8\u05DC\u05D4 \u05D4\u05E2\u05E1\u05E7\u05D4 \u2013 \u05DB\u05DC\u05D5\u05DE\u05E8, \u05D4\u05DE\u05E0\u05D5\u05D9 \u05D9\u05E9\u05DC\u05DD \u05D0\u05EA \u05EA\u05E9\u05DC\u05D5\u05DD \u05D4\u05DE\u05D9\u05E0\u05D9\u05DE\u05D5\u05DD \u05D4\u05D7\u05D5\u05D3\u05E9\u05D9 \u05D5\u05D1\u05E0\u05D5\u05E1\u05E3 \u05EA\u05E9\u05DC\u05D5\u05DE\u05D9\u05DD \u05E0\u05D5\u05E1\u05E4\u05D9\u05DD \u05E9\u05D4\u05E6\u05D8\u05D1\u05E8\u05D5 \u05D1\u05DE\u05D4\u05DC\u05DA \u05D4\u05D7\u05D5\u05D3\u05E9 \u05E2\u05D3 \u05DC\u05E8\u05D2\u05E2 \u05D4\u05D1\u05D9\u05D8\u05D5\u05DC. \u05DC\u05D1\u05D9\u05E8\u05D5\u05E8\u05D9\u05DD \u05D5\u05E9\u05D0\u05DC\u05D5\u05EA \u05D1\u05E0\u05D5\u05E9\u05D0 \u05D1\u05D9\u05D8\u05D5\u05DC \u05E2\u05E1\u05E7\u05D4 \u05D9\u05E9 \u05DC\u05E4\u05E0\u05D5\u05EA \u05D1\u05DB\u05EA\u05D1 \u05DC\u05DB\u05EA\u05D5\u05D1\u05EA : ");
        i0.ɵɵelementStart(170, "a", 9);
        i0.ɵɵtext(171, "info@BeautyS.co.il");
        i0.ɵɵelementEnd();
        i0.ɵɵtext(172, ".");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(173, "p");
        i0.ɵɵelementStart(174, "b");
        i0.ɵɵtext(175, "\u05D6\u05DE\u05E0\u05D9 \u05D0\u05E1\u05E4\u05E7\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA");
        i0.ɵɵelementEnd();
        i0.ɵɵtext(176, " \u2013 \u05D0\u05E1\u05E4\u05E7\u05EA \u05E9\u05D9\u05E8\u05D5\u05EA\u05D9 \u05D4\u05E7\u05D9\u05D3\u05D5\u05DD \u05EA\u05D7\u05DC \u05DC\u05D0\u05D7\u05E8 \u05D0\u05D9\u05E9\u05D5\u05E8 \u05D4\u05EA\u05E9\u05DC\u05D5\u05DD, \u05DB\u05D0\u05E9\u05E8 \u05D4\u05EA\u05E9\u05DC\u05D5\u05DD \u05D1\u05E4\u05D5\u05E2\u05DC \u05D9\u05D2\u05D1\u05D4 \u05DE\u05DE\u05D5\u05E2\u05D3 \u05D4\u05E2\u05DC\u05D9\u05D9\u05D4 \u05DC\u05D0\u05D5\u05D5\u05D9\u05E8. ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(177, "h2", 10);
        i0.ɵɵtext(178, "\u05EA\u05DB\u05E0\u05D9\u05DD \u05E9\u05DE\u05E9\u05EA\u05DE\u05E9\u05D9\u05DD \u05DE\u05D5\u05E1\u05E8\u05D9\u05DD \u05DC\u05E4\u05E8\u05E1\u05D5\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(179, "p");
        i0.ɵɵtext(180, "\u05DB\u05DE\u05E4\u05D5\u05E8\u05D8 \u05DC\u05E2\u05D9\u05DC (\u05D1\u05E4\u05E8\u05E7 \u05D4\u05E2\u05D5\u05E1\u05E7 \u05D1\u05EA\u05D9\u05D0\u05D5\u05E8 \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA), \u05D7\u05DC\u05E7 \u05DE\u05EA\u05DB\u05E0\u05D9\u05D5 \u05E9\u05DC \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DE\u05D5\u05D6\u05DF \u05D0\u05DC\u05D9\u05D5 \u05E2\u05DC-\u05D9\u05D3\u05D9 \u05DE\u05E9\u05EA\u05DE\u05E9\u05D9\u05D5 \u05D4\u05E8\u05E9\u05D5\u05DE\u05D9\u05DD (\u05DC\u05E8\u05D1\u05D5\u05EA \u05D1\u05E2\u05DC\u05D9 \u05E2\u05E1\u05E7\u05D9\u05DD), \u05D4\u05DE\u05E9\u05EA\u05EA\u05E4\u05D9\u05DD \u05D1\u05D4\u05D6\u05E0\u05EA \u05D4\u05EA\u05D5\u05DB\u05DF \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05D0\u05D5\u05E4\u05DF \u05E4\u05E2\u05D9\u05DC (\u05D5\u05D1\u05DB\u05DC\u05DC \u05D6\u05D4 \u05D4\u05D6\u05E0\u05EA \u05DE\u05D9\u05D3\u05E2 \u05D0\u05D5\u05D3\u05D5\u05EA \u05D1\u05EA\u05D9 \u05E2\u05E1\u05E7 \u05D7\u05D3\u05E9\u05D9\u05DD, \u05E2\u05D3\u05DB\u05D5\u05DF \u05D4\u05DE\u05D9\u05D3\u05E2 \u05E2\u05DC \u05D1\u05EA\u05D9 \u05E2\u05E1\u05E7 \u05E7\u05D9\u05D9\u05DE\u05D9\u05DD, \u05D4\u05E2\u05DC\u05D0\u05EA \u05E6\u05D9\u05DC\u05D5\u05DE\u05D9\u05DD, \u05D1\u05D9\u05E7\u05D5\u05E8\u05D5\u05EA \u05D5\u05E2\u05D5\u05D3). \u05EA\u05DB\u05E0\u05D9\u05DD \u05D0\u05DC\u05D5 \u05D9\u05EA\u05E4\u05E8\u05E1\u05DE\u05D5 \u05D1\u05D0\u05D6\u05D5\u05E8\u05D9\u05DD \u05D4\u05DE\u05D9\u05D5\u05E2\u05D3\u05D9\u05DD \u05DC\u05DB\u05DA \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA - \u05D1\u05D9\u05DF \u05DB\u05E9\u05DC\u05E2\u05E6\u05DE\u05DD, \u05D1\u05D0\u05D6\u05D5\u05E8\u05D9\u05DD \u05D4\u05DE\u05D9\u05D5\u05E2\u05D3\u05D9\u05DD \u05DC\u05EA\u05DB\u05E0\u05D9 \u05D4\u05DE\u05E9\u05EA\u05DE\u05E9\u05D9\u05DD \u05D1\u05DC\u05D1\u05D3, \u05D5\u05D1\u05D9\u05DF \u05D1\u05D9\u05D7\u05D3 \u05E2\u05DD \u05EA\u05DB\u05E0\u05D9\u05DD \u05DE\u05D8\u05E2\u05DE\u05D4 \u05E9\u05DC \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA. \u05DE\u05E1\u05D9\u05E8\u05EA \u05EA\u05DB\u05E0\u05D9\u05DD \u05DC\u05E4\u05E8\u05E1\u05D5\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D9\u05E0\u05D4 \u05DE\u05E7\u05E0\u05D4 \u05DC\u05DA \u05D6\u05DB\u05D5\u05EA \u05DC\u05D3\u05E8\u05D5\u05E9 \u05E9\u05D4\u05EA\u05DB\u05E0\u05D9\u05DD \u05D9\u05E4\u05D5\u05E8\u05E1\u05DE\u05D5 \u05D0\u05D5 \u05D9\u05D5\u05E1\u05D9\u05E4\u05D5 \u05DC\u05D4\u05EA\u05E4\u05E8\u05E1\u05DD.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(181, "p");
        i0.ɵɵtext(182, "\u05DC\u05E4\u05D9 \u05E8\u05D5\u05D1, \u05EA\u05DB\u05E0\u05D9\u05DD \u05E9\u05EA\u05E9\u05DC\u05D7 \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D9\u05D4\u05D9\u05D5 \u05DB\u05E4\u05D5\u05E4\u05D9\u05DD \u05DC\u05D1\u05D3\u05D9\u05E7\u05D4 \u05E8\u05D0\u05E9\u05D5\u05E0\u05D9\u05EA \u05E9\u05DC \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA. \u05E2\u05E6\u05DD \u05DE\u05E9\u05DC\u05D5\u05D7 \u05D4\u05EA\u05D5\u05DB\u05DF \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D9\u05E0\u05D5 \u05DE\u05D7\u05D9\u05D9\u05D1 \u05D0\u05EA \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05E4\u05E8\u05E1\u05DD \u05D0\u05EA \u05D4\u05EA\u05D5\u05DB\u05DF \u05E9\u05E9\u05DC\u05D7\u05EA. \u05DE\u05E8\u05D2\u05E2 \u05E9\u05E9\u05DC\u05D7\u05EA \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05EA\u05D5\u05DB\u05DF \u05DC\u05E4\u05E8\u05E1\u05D5\u05DD, \u05D9\u05D7\u05D5\u05DC\u05D5 \u05E2\u05DC\u05D9\u05D5 \u05D4\u05D5\u05E8\u05D0\u05D5\u05EA \u05EA\u05E0\u05D0\u05D9\u05DD \u05D0\u05DC\u05D5. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E8\u05E9\u05D0\u05D9\u05EA, \u05D0\u05DA \u05D0\u05D9\u05E0\u05E0\u05D4 \u05D7\u05D9\u05D9\u05D1\u05EA \u05DC\u05E4\u05E8\u05E1\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D0\u05EA \u05EA\u05DB\u05E0\u05D9\u05DA. \u05D4\u05D9\u05D0 \u05EA\u05E2\u05E9\u05D4 \u05D6\u05D0\u05EA \u05D1\u05D4\u05EA\u05D0\u05DD \u05DC\u05DE\u05D9\u05D8\u05D1 \u05E9\u05D9\u05E7\u05D5\u05DC \u05D3\u05E2\u05EA\u05D4 \u05D4\u05DE\u05E7\u05E6\u05D5\u05E2\u05D9. ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(183, "p");
        i0.ɵɵtext(184, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DE\u05E2\u05D5\u05D3\u05D3\u05EA \u05D0\u05EA \u05D7\u05D5\u05E4\u05E9 \u05D4\u05D1\u05D9\u05D8\u05D5\u05D9 \u05D5\u05D0\u05EA \u05E7\u05D9\u05D5\u05DE\u05D5 \u05E9\u05DC \u05D3\u05D9\u05D5\u05DF \u05E4\u05D5\u05E8\u05D4 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05EA\u05D5\u05DA \u05D4\u05E9\u05EA\u05EA\u05E4\u05D5\u05EA \u05E4\u05E2\u05D9\u05DC\u05D4 \u05E9\u05DC\u05DA \u05D5\u05E9\u05DC \u05D9\u05EA\u05E8 \u05D4\u05DE\u05E9\u05EA\u05DE\u05E9\u05D9\u05DD. \u05D9\u05D7\u05D3 \u05E2\u05DD \u05D6\u05D0\u05EA, \u05E2\u05DC \u05DE\u05E0\u05EA \u05DC\u05D0\u05E4\u05E9\u05E8 \u05D0\u05EA \u05E7\u05D9\u05D5\u05DE\u05D5 \u05E9\u05DC \u05E9\u05D9\u05D7 \u05EA\u05E8\u05D1\u05D5\u05EA\u05D9 \u05D5\u05E2\u05E0\u05D9\u05D9\u05E0\u05D9 \u05D5\u05DC\u05DE\u05E0\u05D5\u05E2 \u05E0\u05D9\u05E6\u05D5\u05DC \u05DC\u05E8\u05E2\u05D4 \u05E9\u05DC \u05D4\u05D0\u05E4\u05E9\u05E8\u05D5\u05D9\u05D5\u05EA \u05D4\u05E4\u05EA\u05D5\u05D7\u05D5\u05EA \u05D1\u05E4\u05E0\u05D9\u05DA \u05DC\u05D4\u05EA\u05D1\u05D8\u05D0 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05E8\u05E9\u05D0\u05D9\u05EA \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D1\u05D3\u05D5\u05E7 \u05D0\u05EA \u05D4\u05EA\u05DB\u05E0\u05D9\u05DD \u05D4\u05DC\u05DC\u05D5 \u05DC\u05E4\u05E0\u05D9 \u05E4\u05E8\u05E1\u05D5\u05DE\u05DD, \u05D0\u05DA \u05D4\u05D9\u05D0 \u05D0\u05D9\u05E0\u05D4 \u05D7\u05D9\u05D9\u05D1\u05EA \u05DC\u05E2\u05E9\u05D5\u05EA \u05DB\u05DF. \u05D1\u05D3\u05D9\u05E7\u05D5\u05EA \u05DB\u05D0\u05DC\u05D4 \u05E2\u05E9\u05D5\u05D9\u05D5\u05EA \u05DC\u05D4\u05D9\u05E2\u05E9\u05D5\u05EA \u05E2\u05DC-\u05D9\u05D3\u05D9 \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05D0\u05D5\u05E4\u05DF \u05DE\u05D3\u05D2\u05DE\u05D9, \u05D0\u05DA \u05D5\u05E8\u05E7 \u05DB\u05D3\u05D9 \u05DC\u05E0\u05E1\u05D5\u05EA \u05D5\u05DC\u05DE\u05E0\u05D5\u05E2 \u05E4\u05E8\u05E1\u05D5\u05DD \u05EA\u05DB\u05E0\u05D9\u05DD \u05E9\u05D4\u05DD \u05D1\u05DC\u05EA\u05D9 \u05D7\u05D5\u05E7\u05D9\u05D9\u05DD \u05D0\u05D5 \u05E4\u05D5\u05D2\u05E2\u05E0\u05D9\u05D9\u05DD \u05E2\u05DC \u05E4\u05E0\u05D9\u05D4\u05DD \u05D5\u05D1\u05DC\u05D0 \u05E6\u05D5\u05E8\u05DA \u05D1\u05D4\u05E4\u05E2\u05DC\u05EA \u05E9\u05D9\u05E7\u05D5\u05DC \u05D3\u05E2\u05EA \u05DE\u05E9\u05E4\u05D8\u05D9.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(185, "p");
        i0.ɵɵtext(186, "\u05D1\u05E2\u05EA \u05E9\u05D9\u05D2\u05D5\u05E8 \u05EA\u05DB\u05E0\u05D9\u05DD \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D5 \u05DE\u05E1\u05D9\u05E8\u05EA \u05EA\u05DB\u05E0\u05D9\u05DD \u05DC\u05E4\u05E8\u05E1\u05D5\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D7\u05DC\u05D4 \u05E2\u05DC\u05D9\u05DA \u05D4\u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05D4\u05DE\u05DC\u05D0\u05D4 \u05D5\u05D4\u05D1\u05DC\u05E2\u05D3\u05D9\u05EA \u05DC\u05DB\u05DC \u05EA\u05D5\u05E6\u05D0\u05D4 \u05E9\u05EA\u05E0\u05D1\u05E2 \u05DE\u05D4\u05E4\u05E8\u05E1\u05D5\u05DD, \u05D0\u05D5 \u05D4\u05E9\u05D9\u05D2\u05D5\u05E8 \u05DB\u05D0\u05DE\u05D5\u05E8. \u05E2\u05DC\u05D9\u05DA \u05DC\u05D4\u05E7\u05E4\u05D9\u05D3 \u05E9\u05EA\u05DB\u05E0\u05D9\u05DD \u05DB\u05D0\u05DC\u05D4 \u05D9\u05D4\u05D9\u05D5 \u05D7\u05D5\u05E7\u05D9\u05D9\u05DD. \u05D1\u05D9\u05DF \u05D4\u05E9\u05D0\u05E8 \u05D5\u05DC\u05DE\u05E2\u05DF \u05D4\u05D3\u05D5\u05D2\u05DE\u05D4 \u05D1\u05DC\u05D1\u05D3, \u05E0\u05D0\u05E1\u05E8 \u05E2\u05DC\u05D9\u05DA \u05DC\u05E4\u05E8\u05E1\u05DD \u05D5\u05DC\u05D8\u05E2\u05D5\u05DF \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D0\u05D5 \u05DC\u05E9\u05D2\u05E8 \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D0\u05EA \u05D4\u05EA\u05DB\u05E0\u05D9\u05DD \u05D4\u05D1\u05D0\u05D9\u05DD - ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(187, "ul");
        i0.ɵɵelementStart(188, "li");
        i0.ɵɵtext(189, "\u05DB\u05DC \u05EA\u05D5\u05DB\u05DF \u05E9\u05D4\u05D5\u05D0 \u05E9\u05E7\u05E8\u05D9, \u05DE\u05D8\u05E2\u05D4 \u05D0\u05D5 \u05DE\u05E1\u05D5\u05DC\u05E3;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(190, "li");
        i0.ɵɵtext(191, "\u05DB\u05DC \u05EA\u05D5\u05DB\u05DF \u05D4\u05E4\u05D5\u05D2\u05E2 \u05D1\u05D6\u05DB\u05D5\u05D9\u05D5\u05EA \u05E7\u05E0\u05D9\u05D9\u05E0\u05D9\u05D5\u05EA \u05E9\u05DC \u05D0\u05D7\u05E8\u05D9\u05DD \u05D0\u05D5 \u05DE\u05E4\u05E8 \u05D0\u05D5\u05EA\u05DF - \u05DC\u05E8\u05D1\u05D5\u05EA \u05D6\u05DB\u05D5\u05D9\u05D5\u05EA \u05D9\u05D5\u05E6\u05E8\u05D9\u05DD, \u05E1\u05D9\u05DE\u05E0\u05D9 \u05DE\u05E1\u05D7\u05E8 \u05D5\u05E1\u05D5\u05D3\u05D5\u05EA \u05DE\u05E1\u05D7\u05E8\u05D9\u05D9\u05DD;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(192, "li");
        i0.ɵɵtext(193, "\u05DB\u05DC \u05EA\u05D5\u05DB\u05DF \u05E4\u05D5\u05E8\u05E0\u05D5\u05D2\u05E8\u05D0\u05E4\u05D9 \u05D0\u05D5 \u05D1\u05E2\u05DC \u05D0\u05D5\u05E4\u05D9 \u05DE\u05D9\u05E0\u05D9 \u05D1\u05D5\u05D8\u05D4;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(194, "li");
        i0.ɵɵtext(195, "\u05DB\u05DC \u05EA\u05D5\u05DB\u05DF \u05D4\u05E0\u05D5\u05D2\u05E2 \u05DC\u05E7\u05D8\u05D9\u05E0\u05D9\u05DD \u05D5\u05DE\u05D6\u05D4\u05D4 \u05D0\u05D5\u05EA\u05DD, \u05D0\u05EA \u05E4\u05E8\u05D8\u05D9\u05D4\u05DD \u05D4\u05D0\u05D9\u05E9\u05D9\u05D9\u05DD \u05D0\u05D5 \u05D0\u05EA \u05DE\u05E2\u05E0\u05DD \u05D5\u05D3\u05E8\u05DB\u05D9 \u05D4\u05D4\u05EA\u05E7\u05E9\u05E8\u05D5\u05EA \u05E2\u05D9\u05DE\u05DD;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(196, "li");
        i0.ɵɵtext(197, "\u05DB\u05DC \u05EA\u05D5\u05DB\u05E0\u05EA \u05DE\u05D7\u05E9\u05D1, \u05E7\u05D5\u05D3 \u05DE\u05D7\u05E9\u05D1 \u05D0\u05D5 \u05D9\u05D9\u05E9\u05D5\u05DD \u05D4\u05DB\u05D5\u05DC\u05DC\u05D9\u05DD \u05E0\u05D2\u05D9\u05E3-\u05DE\u05D7\u05E9\u05D1 (\u05D5\u05D9\u05E8\u05D5\u05E1), \u05DC\u05E8\u05D1\u05D5\u05EA \u05EA\u05D5\u05DB\u05E0\u05D5\u05EA \u05E2\u05D5\u05D9\u05E0\u05D5\u05EA \u05D4\u05D9\u05D3\u05D5\u05E2\u05D5\u05EA \u05DB\u05E1\u05D5\u05E1-\u05D8\u05E8\u05D5\u05D9\u05D0\u05E0\u05D9, \u05EA\u05D5\u05DC\u05E2\u05D9\u05DD (Worms), \u05D5\u05D0\u05E0\u05D3\u05DC\u05D9\u05DD (Vandals), \u05D9\u05D9\u05E9\u05D5\u05DE\u05D9\u05DD \u05DE\u05D6\u05D9\u05E7\u05D9\u05DD (Malicious Applications) \u05D5\u05DB\u05D9\u05D5\"\u05D1;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(198, "li");
        i0.ɵɵtext(199, "\u05E1\u05D9\u05E1\u05DE\u05D0\u05D5\u05EA, \u05E9\u05DE\u05D5\u05EA \u05DE\u05E9\u05EA\u05DE\u05E9 \u05D5\u05E4\u05E8\u05D8\u05D9\u05DD \u05D0\u05D7\u05E8\u05D9\u05DD, \u05D4\u05DE\u05D0\u05E4\u05E9\u05E8\u05D9\u05DD \u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05EA\u05D5\u05DB\u05E0\u05D5\u05EA \u05DE\u05D7\u05E9\u05D1, \u05E7\u05D1\u05E6\u05D9\u05DD \u05D3\u05D9\u05D2\u05D9\u05D8\u05D0\u05DC\u05D9\u05D9\u05DD, \u05D0\u05EA\u05E8\u05D9 \u05D0\u05D9\u05E0\u05D8\u05E8\u05E0\u05D8, \u05D9\u05D9\u05E9\u05D5\u05DE\u05D9\u05DD \u05D0\u05D5 \u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD, \u05D4\u05D8\u05E2\u05D5\u05E0\u05D9\u05DD \u05D4\u05E8\u05E9\u05DE\u05D4 \u05D0\u05D5 \u05EA\u05E9\u05DC\u05D5\u05DD, \u05D1\u05DC\u05D0 \u05EA\u05E9\u05DC\u05D5\u05DD \u05D0\u05D5 \u05D4\u05E8\u05E9\u05DE\u05D4 \u05DB\u05D0\u05DE\u05D5\u05E8;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(200, "li");
        i0.ɵɵtext(201, "\u05DB\u05DC \u05EA\u05D5\u05DB\u05DF \u05D4\u05DE\u05D4\u05D5\u05D5\u05D4 \u05DC\u05E9\u05D5\u05DF \u05D4\u05E8\u05E2 \u05E2\u05DC \u05D0\u05D3\u05DD, \u05D0\u05D5 \u05D4\u05E4\u05D5\u05D2\u05E2 \u05D1\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA\u05D5, \u05D0\u05D5 \u05D1\u05E9\u05DE\u05D5 \u05D4\u05D8\u05D5\u05D1;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(202, "li");
        i0.ɵɵtext(203, "\u05DB\u05DC \u05EA\u05D5\u05DB\u05DF \u05D4\u05DE\u05D6\u05D4\u05D4 \u05D0\u05D9\u05E9\u05D9\u05EA \u05D0\u05E0\u05E9\u05D9\u05DD \u05D0\u05D7\u05E8\u05D9\u05DD, \u05DE\u05D1\u05DC\u05D9 \u05E9\u05E0\u05EA\u05E0\u05D5 \u05D0\u05EA \u05D4\u05E1\u05DB\u05DE\u05EA\u05DD \u05DC\u05E4\u05E8\u05E1\u05D5\u05DD \u05D6\u05D4\u05D5\u05EA\u05DD;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(204, "li");
        i0.ɵɵtext(205, "\u05DB\u05DC \u05EA\u05D5\u05DB\u05DF \u05D1\u05E2\u05DC \u05D0\u05D5\u05E4\u05D9 \u05D0\u05D5 \u05EA\u05D5\u05DB\u05DF \u05D8\u05D5\u05E8\u05D3\u05E0\u05D9, \u05DE\u05E2\u05DC\u05D9\u05D1, \u05E2\u05D5\u05D9\u05DF, \u05DE\u05D0\u05D9\u05D9\u05DD \u05D0\u05D5 \u05D2\u05E1 \u05E8\u05D5\u05D7;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(206, "li");
        i0.ɵɵtext(207, "\u05DB\u05DC \u05EA\u05D5\u05DB\u05DF \u05E9\u05D9\u05E9 \u05D1\u05D5, \u05D0\u05D5 \u05D4\u05D5\u05D0 \u05DE\u05E2\u05D5\u05D3\u05D3 \u05DC\u05D2\u05D6\u05E2\u05E0\u05D5\u05EA, \u05D0\u05D5 \u05D0\u05E4\u05DC\u05D9\u05D4 \u05E4\u05E1\u05D5\u05DC\u05D4 \u05E2\u05DC \u05D1\u05E1\u05D9\u05E1 \u05D2\u05D6\u05E2, \u05DE\u05D5\u05E6\u05D0, \u05E6\u05D1\u05E2 \u05E2\u05D5\u05E8, \u05E2\u05D3\u05D4, \u05DC\u05D0\u05D5\u05DE\u05D9\u05D5\u05EA, \u05D3\u05EA, \u05DE\u05D9\u05DF, \u05E2\u05D9\u05E1\u05D5\u05E7, \u05E0\u05D8\u05D9\u05D9\u05D4 \u05DE\u05D9\u05E0\u05D9\u05EA, \u05DE\u05D7\u05DC\u05D4, \u05E0\u05DB\u05D5\u05EA \u05D2\u05D5\u05E4\u05E0\u05D9\u05EA \u05D0\u05D5 \u05E0\u05E4\u05E9\u05D9\u05EA, \u05D0\u05DE\u05D5\u05E0\u05D4, \u05D4\u05E9\u05E7\u05E4\u05D4 \u05E4\u05D5\u05DC\u05D9\u05D8\u05D9\u05EA, \u05D0\u05D5 \u05DE\u05E2\u05DE\u05D3 \u05D7\u05D1\u05E8\u05EA\u05D9-\u05DB\u05DC\u05DB\u05DC\u05D9;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(208, "li");
        i0.ɵɵtext(209, "\u05DB\u05DC \u05EA\u05D5\u05DB\u05DF \u05D4\u05DE\u05E2\u05D5\u05D3\u05D3 \u05D1\u05D9\u05E6\u05D5\u05E2 \u05E2\u05D1\u05D9\u05E8\u05D4 \u05E4\u05DC\u05D9\u05DC\u05D9\u05EA \u05D0\u05D5 \u05E2\u05DC\u05D5\u05DC \u05DC\u05D4\u05D5\u05D5\u05EA \u05D1\u05E1\u05D9\u05E1 \u05DC\u05EA\u05D1\u05D9\u05E2\u05D4 \u05D0\u05D5 \u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05D0\u05D6\u05E8\u05D7\u05D9\u05EA;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(210, "li");
        i0.ɵɵtext(211, "\u05DB\u05DC \u05EA\u05D5\u05DB\u05DF \u05D4\u05E2\u05DC\u05D5\u05DC \u05DC\u05D4\u05D8\u05E2\u05D5\u05EA \u05E6\u05E8\u05DB\u05DF;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(212, "li");
        i0.ɵɵtext(213, "\u05DB\u05DC \u05EA\u05D5\u05DB\u05DF \u05D1\u05E2\u05DC \u05D0\u05D5\u05E4\u05D9 \u05E4\u05E8\u05E1\u05D5\u05DE\u05D9 \u05D0\u05D5 \u05DE\u05E1\u05D7\u05E8\u05D9, \u05E9\u05D0\u05D9\u05E0\u05D5 \u05E7\u05E9\u05D5\u05E8 \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5/\u05D0\u05D5 \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05D4 \u05D5/\u05D0\u05D5 \u05D4\u05DB\u05D5\u05DC\u05DC \u05DE\u05D9\u05D3\u05E2 \u05D4\u05E0\u05D5\u05D2\u05E2 \u05D1\u05DE\u05D9\u05E9\u05E8\u05D9\u05DF \u05DC\u05D1\u05D9\u05EA \u05D4\u05E2\u05E1\u05E7 \u05E0\u05E9\u05D5\u05D0 \u05D4\u05DE\u05D9\u05D3\u05E2;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(214, "li");
        i0.ɵɵtext(215, "\u05DB\u05DC \u05EA\u05D5\u05DB\u05DF \u05E9\u05D4\u05D2\u05D9\u05E9\u05D4 \u05D0\u05DC\u05D9\u05D5 \u05D7\u05E1\u05D5\u05DE\u05D4 \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05E1\u05D9\u05E1\u05DE\u05D4 \u05D5\u05DB\u05D9\u05D5\"\u05D1, \u05D5\u05D0\u05D9\u05E0\u05D4 \u05DE\u05EA\u05D0\u05E4\u05E9\u05E8\u05EA \u05D1\u05D0\u05D5\u05E4\u05DF \u05D7\u05D5\u05E4\u05E9\u05D9 \u05DC\u05DB\u05DC \u05DE\u05E9\u05EA\u05DE\u05E9\u05D9 \u05D4\u05D0\u05D9\u05E0\u05D8\u05E8\u05E0\u05D8.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(216, "p");
        i0.ɵɵtext(217, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E8\u05E9\u05D0\u05D9\u05EA \u05DC\u05E1\u05E8\u05D1 \u05DC\u05E4\u05E8\u05E1\u05DD, \u05D0\u05D5 \u05DC\u05DE\u05D7\u05D5\u05E7 \u05DC\u05D0\u05DC\u05EA\u05E8 \u05DB\u05DC \u05EA\u05D5\u05DB\u05DF \u05E9\u05DE\u05E1\u05E8\u05EA \u05DC\u05E4\u05E8\u05E1\u05D5\u05DD, \u05DC\u05E4\u05D9 \u05E9\u05D9\u05E7\u05D5\u05DC \u05D3\u05E2\u05EA\u05D4 \u05D4\u05D1\u05DC\u05E2\u05D3\u05D9, \u05DC\u05E8\u05D1\u05D5\u05EA \u05D1\u05DB\u05DC \u05E2\u05EA \u05E9\u05EA\u05DE\u05E6\u05D0 \u05DB\u05D9 \u05D4\u05E4\u05E8\u05EA \u05D0\u05EA \u05EA\u05E0\u05D0\u05D9 \u05D4\u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD \u05D0\u05D5 \u05E9\u05E2\u05E9\u05D9\u05EA \u05DE\u05E2\u05E9\u05D4 \u05D0\u05D5 \u05DE\u05D7\u05D3\u05DC \u05D4\u05E4\u05D5\u05D2\u05E2 \u05D0\u05D5 \u05E2\u05DC\u05D5\u05DC \u05DC\u05E4\u05D2\u05D5\u05E2 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D4\u05E0\u05D9\u05EA\u05E0\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D1\u05DE\u05E9\u05EA\u05DE\u05E9\u05D9\u05D5, \u05D1\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D5 \u05D1\u05DE\u05D9 \u05DE\u05D8\u05E2\u05DE\u05D4. \u05D1\u05DE\u05E7\u05E8\u05D4 \u05D6\u05D4, \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05EA\u05D4\u05D9\u05D4 \u05E8\u05E9\u05D0\u05D9\u05EA \u05D2\u05DD \u05DC\u05DE\u05E0\u05D5\u05E2 \u05DE\u05DE\u05DA \u05E4\u05E8\u05E1\u05D5\u05DD \u05EA\u05DB\u05E0\u05D9\u05DD \u05E0\u05D5\u05E1\u05E4\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA. \u05D4\u05D5\u05E8\u05D0\u05D5\u05EA \u05D4\u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD \u05DE\u05D5\u05E1\u05D9\u05E4\u05D5\u05EA \u05E2\u05DC \u05D6\u05DB\u05D5\u05D9\u05D5\u05EA \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05E4\u05D9 \u05DB\u05DC \u05D3\u05D9\u05DF.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(218, "p");
        i0.ɵɵtext(219, "\u05D4\u05D7\u05DC\u05D9\u05D8\u05D4 \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05E4\u05E8\u05E1\u05DD \u05D0\u05D5 \u05DC\u05D4\u05E9\u05EA\u05DE\u05E9 \u05D1\u05EA\u05D5\u05DB\u05DF \u05E9\u05DE\u05E1\u05E8\u05EA \u2013 \u05E8\u05E9\u05D0\u05D9\u05EA \u05D4\u05D9\u05D0 \u05DC\u05E2\u05E8\u05D5\u05DA \u05DB\u05DC \u05EA\u05D5\u05DB\u05DF \u05DB\u05D6\u05D4, \u05DC\u05E4\u05D9 \u05E9\u05D9\u05E7\u05D5\u05DC \u05D3\u05E2\u05EA\u05D4 \u05D4\u05DE\u05D5\u05D7\u05DC\u05D8 \u05D5\u05D1\u05DB\u05DC\u05DC \u05D6\u05D4 \u05DC\u05E7\u05E6\u05E8\u05D5. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D9\u05E0\u05D4 \u05DE\u05D7\u05D5\u05D9\u05D1\u05EA \u05DC\u05E6\u05D9\u05D9\u05DF \u05D0\u05EA \u05E9\u05DE\u05DA \u05DB\u05DE\u05E7\u05D5\u05E8 \u05D4\u05EA\u05D5\u05DB\u05DF \u05E9\u05DE\u05E1\u05E8\u05EA, \u05D0\u05DA \u05D4\u05D9\u05D0 \u05E8\u05E9\u05D0\u05D9\u05EA \u05DC\u05E2\u05E9\u05D5\u05EA \u05DB\u05DF \u05DC\u05E4\u05D9 \u05E9\u05D9\u05E7\u05D5\u05DC \u05D3\u05E2\u05EA\u05D4 \u05D5\u05D4\u05E0\u05DA \u05DE\u05D5\u05E1\u05E8 \u05D0\u05EA \u05D4\u05E1\u05DB\u05DE\u05EA\u05DA \u05DC\u05DB\u05DA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(220, "p");
        i0.ɵɵtext(221, "\u05DC\u05DE\u05E2\u05D8 \u05DE\u05E7\u05D5\u05DE\u05D5\u05EA \u05D1\u05D4\u05DD \u05EA\u05D9\u05E0\u05EA\u05DF \u05D4\u05D5\u05D3\u05E2\u05D4 \u05D0\u05D7\u05E8\u05EA, \u05D4\u05EA\u05DB\u05E0\u05D9\u05DD \u05E9\u05EA\u05DE\u05E1\u05D5\u05E8 \u05DC\u05E4\u05E8\u05E1\u05D5\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D9\u05D4\u05D9\u05D5 \u05D7\u05E9\u05D5\u05E4\u05D9\u05DD \u05DC\u05DB\u05DC \u05DE\u05E9\u05EA\u05DE\u05E9\u05D9 \u05D4\u05D0\u05D9\u05E0\u05D8\u05E8\u05E0\u05D8, \u05D0\u05D5 \u05DC\u05DB\u05DC \u05D4\u05E4\u05D7\u05D5\u05EA \u05DC\u05DE\u05E9\u05EA\u05DE\u05E9\u05D9\u05DD \u05D0\u05D7\u05E8\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA. \u05E0\u05D4\u05D2 \u05D1\u05EA\u05D1\u05D5\u05E0\u05D4 \u05D5\u05D1\u05D6\u05D4\u05D9\u05E8\u05D5\u05EA \u05D1\u05DE\u05E1\u05D9\u05E8\u05EA \u05E4\u05E8\u05D8\u05D9\u05DD \u05D0\u05D9\u05E9\u05D9\u05D9\u05DD (\u05DB\u05D3\u05D5\u05D2\u05DE\u05EA \u05DB\u05EA\u05D5\u05D1\u05EA \u05D0\u05D5 \u05DE\u05E1\u05E4\u05E8 \u05D8\u05DC\u05E4\u05D5\u05DF) \u05D5\u05DB\u05DF \u05D1\u05EA\u05D2\u05D5\u05D1\u05D5\u05EA \u05D5\u05D1\u05E4\u05E0\u05D9\u05D5\u05EA \u05E9\u05D9\u05EA\u05E7\u05D1\u05DC\u05D5 \u05D0\u05E6\u05DC\u05DA \u05D1\u05E2\u05E7\u05D1\u05D5\u05EA \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D5 \u05E4\u05E8\u05E1\u05D5\u05DD \u05D0\u05D9\u05D6\u05D4 \u05DE\u05E4\u05E8\u05D8\u05D9\u05DA. \u05D6\u05DB\u05D5\u05E8 - \u05E2\u05DC\u05D9\u05DA \u05DC\u05E0\u05D4\u05D5\u05D2 \u05D1\u05DE\u05E1\u05D9\u05E8\u05EA \u05D4\u05EA\u05DB\u05E0\u05D9\u05DD \u05D5\u05D1\u05E4\u05E0\u05D9\u05D5\u05EA \u05D0\u05DC\u05D9\u05DA \u05DC\u05E4\u05D7\u05D5\u05EA \u05D1\u05D0\u05D5\u05EA\u05D4 \u05DE\u05D9\u05D3\u05D4 \u05E9\u05DC \u05D6\u05D4\u05D9\u05E8\u05D5\u05EA \u05E9\u05D0\u05EA\u05D4 \u05E0\u05D5\u05E7\u05D8 \u05D1\u05D9\u05E6\u05D9\u05E8\u05EA \u05E7\u05E9\u05E8 \u05E9\u05D0\u05D9\u05E0\u05D4 \u05E0\u05E2\u05E9\u05D9\u05EA \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D4\u05D0\u05D9\u05E0\u05D8\u05E8\u05E0\u05D8.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(222, "p");
        i0.ɵɵelementStart(223, "b");
        i0.ɵɵtext(224, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DE\u05E2\u05D5\u05D3\u05D3\u05EA \u05D0\u05D5\u05EA\u05DA \u05DC\u05D4\u05EA\u05D9\u05D9\u05D7\u05E1 \u05D1\u05D6\u05D4\u05D9\u05E8\u05D5\u05EA \u05D5\u05D1\u05D1\u05D9\u05E7\u05D5\u05E8\u05EA\u05D9\u05D5\u05EA \u05DC\u05EA\u05DB\u05E0\u05D9\u05DD \u05D4\u05DE\u05EA\u05E4\u05E8\u05E1\u05DE\u05D9\u05DD \u05D1\u05D0\u05D9\u05E0\u05D8\u05E8\u05E0\u05D8 \u05D5\u05D1\u05DB\u05DC\u05DC \u05D6\u05D4 \u05DC\u05EA\u05DB\u05E0\u05D9\u05DD \u05E9\u05DE\u05E4\u05E8\u05E1\u05DE\u05D9\u05DD \u05DE\u05E9\u05EA\u05DE\u05E9\u05D9\u05DD \u05D0\u05D7\u05E8\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA. \u05EA\u05DB\u05E0\u05D9\u05DD \u05E9\u05E0\u05DE\u05E1\u05E8\u05D5 \u05E2\u05DC-\u05D9\u05D3\u05D9 \u05DE\u05E9\u05EA\u05DE\u05E9\u05D9\u05DD \u05D0\u05D9\u05E0\u05DD \u05DE\u05EA\u05E4\u05E8\u05E1\u05DE\u05D9\u05DD \u05DE\u05D8\u05E2\u05DE\u05D4 \u05E9\u05DC \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05DC\u05E4\u05D9\u05DB\u05DA \u05D4\u05D9\u05D0 \u05D0\u05D9\u05E0\u05D4 \u05D0\u05D7\u05E8\u05D0\u05D9\u05EA \u05DC\u05DE\u05D4\u05D9\u05DE\u05E0\u05D5\u05EA\u05DD, \u05D0\u05DE\u05D9\u05E0\u05D5\u05EA\u05DD, \u05D3\u05D9\u05D5\u05E7\u05DD \u05D0\u05D5 \u05E9\u05DC\u05DE\u05D5\u05EA\u05DD \u05D5\u05D0\u05D9\u05E0\u05D4 \u05D0\u05D7\u05E8\u05D0\u05D9\u05EA \u05DC\u05DB\u05DC \u05EA\u05D5\u05E6\u05D0\u05D4 \u05E9\u05EA\u05D9\u05D2\u05E8\u05DD \u05DE\u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D4\u05DD \u05D0\u05D5 \u05DE\u05D4\u05E1\u05EA\u05DE\u05DB\u05D5\u05EA \u05E2\u05DC\u05D9\u05D4\u05DD. ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(225, "h2", 11);
        i0.ɵɵtext(226, "\u05E7\u05D9\u05E9\u05D5\u05E8\u05D9\u05DD");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(227, "p");
        i0.ɵɵtext(228, "\u05EA\u05DE\u05E6\u05D0 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05E7\u05D9\u05E9\u05D5\u05E8\u05D9\u05DD (\u05DC\u05D9\u05E0\u05E7\u05D9\u05DD) \u05DC\u05E2\u05DE\u05D5\u05D3\u05D9\u05DD \u05E9\u05D5\u05E0\u05D9\u05DD \u05D1\u05D0\u05D9\u05E0\u05D8\u05E8\u05E0\u05D8 \u05D5\u05D1\u05DB\u05DC\u05DC \u05D6\u05D4 \u05E7\u05D9\u05E9\u05D5\u05E8\u05D9\u05DD \u05DC\u05D1\u05EA\u05D9 \u05D4\u05E2\u05E1\u05E7 \u05D5\u05E0\u05D5\u05EA\u05E0\u05D9 \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D4\u05DB\u05DC\u05D5\u05DC\u05D9\u05DD \u05D1\u05D0\u05D9\u05E0\u05D3\u05E7\u05E1 \u05E9\u05DC\u05E0\u05D5. \u05D4\u05E7\u05D9\u05E9\u05D5\u05E8\u05D9\u05DD \u05DE\u05D0\u05E4\u05E9\u05E8\u05D9\u05DD \u05DC\u05DA \u05DC\u05DE\u05E6\u05D5\u05D0 \u05EA\u05DB\u05E0\u05D9\u05DD \u05D4\u05DE\u05EA\u05E4\u05E8\u05E1\u05DE\u05D9\u05DD \u05D1\u05D0\u05D9\u05E0\u05D8\u05E8\u05E0\u05D8. \u05EA\u05DB\u05E0\u05D9\u05DD \u05D0\u05DC\u05D4 \u05DC\u05E8\u05D5\u05D1 \u05D0\u05D9\u05E0\u05DD \u05DE\u05EA\u05E4\u05E8\u05E1\u05DE\u05D9\u05DD \u05E2\u05DC \u05D9\u05D3\u05D9 \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D0\u05D5 \u05DE\u05D8\u05E2\u05DE\u05D4 \u05D5\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D9\u05E0\u05E0\u05D4 \u05E9\u05D5\u05DC\u05D8\u05EA \u05D0\u05D5 \u05DE\u05E4\u05E7\u05D7\u05EA \u05E2\u05DC\u05D9\u05D4\u05DD. \u05D4\u05E2\u05D5\u05D1\u05D3\u05D4 \u05E9\u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DE\u05E7\u05E9\u05E8 \u05DC\u05EA\u05DB\u05E0\u05D9\u05DD \u05D0\u05DC\u05D4 \u05D0\u05D9\u05E0\u05D4 \u05DE\u05E2\u05D9\u05D3\u05D4 \u05E2\u05DC \u05D4\u05E1\u05DB\u05DE\u05EA \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05EA\u05D5\u05DB\u05E0\u05DD \u05D5\u05D0\u05D9\u05E0\u05D4 \u05DE\u05D4\u05D5\u05D5\u05D4 \u05E2\u05E8\u05D5\u05D1\u05D4 \u05DC\u05D0\u05DE\u05D9\u05E0\u05D5\u05EA\u05DD, \u05DC\u05E2\u05D3\u05DB\u05E0\u05D9\u05D5\u05EA\u05DD, \u05DC\u05D7\u05D5\u05E7\u05D9\u05D5\u05EA\u05DD, \u05DC\u05E0\u05D5\u05D4\u05D2\u05D9 \u05D1\u05E2\u05DC\u05D9 \u05D4\u05EA\u05DB\u05E0\u05D9\u05DD \u05D1\u05EA\u05D7\u05D5\u05DD \u05D4\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA \u05D5\u05DC\u05DB\u05DC \u05D4\u05D9\u05D1\u05D8 \u05D0\u05D7\u05E8 \u05D4\u05DB\u05E8\u05D5\u05DA \u05D1\u05D4\u05DD. \u05D9\u05EA\u05DB\u05DF \u05E9\u05EA\u05DE\u05E6\u05D0 \u05E9\u05EA\u05DB\u05E0\u05D9\u05DD \u05D0\u05DC\u05D4 \u05D0\u05D9\u05E0\u05DD \u05D4\u05D5\u05DC\u05DE\u05D9\u05DD \u05D0\u05EA \u05E6\u05E8\u05DB\u05D9\u05DA, \u05D0\u05D5 \u05E9\u05D0\u05EA\u05D4 \u05DE\u05EA\u05E0\u05D2\u05D3 \u05DC\u05EA\u05D5\u05DB\u05E0\u05DD, \u05D0\u05D5 \u05E1\u05D1\u05D5\u05E8 \u05DB\u05D9 \u05D4\u05DD \u05DE\u05E7\u05D5\u05DE\u05DE\u05D9\u05DD, \u05DE\u05E8\u05D2\u05D9\u05D6\u05D9\u05DD, \u05D1\u05DC\u05EA\u05D9 \u05E0\u05D0\u05D5\u05EA\u05D9\u05DD, \u05D1\u05DC\u05EA\u05D9 \u05D7\u05D5\u05E7\u05D9\u05D9\u05DD \u05D0\u05D5 \u05D1\u05DC\u05EA\u05D9 \u05DE\u05D5\u05E1\u05E8\u05D9\u05D9\u05DD. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D9\u05E0\u05D4 \u05D0\u05D7\u05E8\u05D0\u05D9\u05EA \u05DC\u05EA\u05DB\u05E0\u05D9\u05DD \u05E9\u05D0\u05DC\u05D9\u05D4\u05DD \u05DE\u05D5\u05DC\u05D9\u05DB\u05D9\u05DD \u05D4\u05E7\u05D9\u05E9\u05D5\u05E8\u05D9\u05DD \u05D5\u05D0\u05D9\u05E0\u05D4 \u05D0\u05D7\u05E8\u05D0\u05D9\u05EA \u05DC\u05DB\u05DC \u05EA\u05D5\u05E6\u05D0\u05D4 \u05E9\u05EA\u05D9\u05D2\u05E8\u05DD \u05DE\u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D4\u05DD \u05D0\u05D5 \u05DE\u05D4\u05E1\u05EA\u05DE\u05DB\u05D5\u05EA \u05E2\u05DC\u05D9\u05D4\u05DD.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(229, "p");
        i0.ɵɵtext(230, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D9\u05E0\u05E0\u05D4 \u05DE\u05EA\u05D7\u05D9\u05D9\u05D1\u05EA \u05DB\u05D9 \u05D4\u05E7\u05D9\u05E9\u05D5\u05E8\u05D9\u05DD \u05E9\u05D9\u05DE\u05E6\u05D0\u05D5 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D9\u05D4\u05D9\u05D5 \u05EA\u05E7\u05D9\u05E0\u05D9\u05DD \u05D5\u05D9\u05D5\u05D1\u05D9\u05DC\u05D5 \u05D0\u05D5\u05EA\u05DA \u05DC\u05E2\u05DE\u05D5\u05D3 \u05D0\u05D9\u05E0\u05D8\u05E8\u05E0\u05D8 \u05E4\u05E2\u05D9\u05DC \u05D5\u05D4\u05D9\u05D0 \u05E8\u05E9\u05D0\u05D9\u05EA \u05DC\u05D4\u05E1\u05D9\u05E8 \u05DE\u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E7\u05D9\u05E9\u05D5\u05E8\u05D9\u05DD \u05E9\u05E0\u05DB\u05DC\u05DC\u05D5 \u05D1\u05D5 \u05D1\u05E2\u05D1\u05E8, \u05D0\u05D5 \u05DC\u05D4\u05D9\u05DE\u05E0\u05E2 \u05DE\u05D4\u05D5\u05E1\u05E4\u05EA \u05E7\u05D9\u05E9\u05D5\u05E8\u05D9\u05DD \u05D7\u05D3\u05E9\u05D9\u05DD - \u05D4\u05DB\u05D5\u05DC, \u05DC\u05E4\u05D9 \u05E9\u05D9\u05E7\u05D5\u05DC \u05D3\u05E2\u05EA\u05D4 \u05D4\u05DE\u05D5\u05D7\u05DC\u05D8.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(231, "h2", 12);
        i0.ɵɵtext(232, "\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(233, "p");
        i0.ɵɵtext(234, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DE\u05DB\u05D1\u05D3\u05EA \u05D0\u05EA \u05E4\u05E8\u05D8\u05D9\u05D5\u05EA\u05DA \u05D1\u05E2\u05EA \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA. ");
        i0.ɵɵelementStart(235, "a", 3);
        i0.ɵɵelementStart(236, "b");
        i0.ɵɵtext(237, "\u05DE\u05D3\u05D9\u05E0\u05D9\u05D5\u05EA \u05D4\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵtext(238, " \u05E9\u05DC \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DE\u05D4\u05D5\u05D5\u05D4 \u05D7\u05DC\u05E7 \u05D1\u05DC\u05EA\u05D9 \u05E0\u05E4\u05E8\u05D3 \u05DE\u05D4\u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD. \u05D4\u05D5\u05D0\u05D9\u05DC \u05D5\u05DE\u05D3\u05D9\u05E0\u05D9\u05D5\u05EA \u05D4\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA \u05D9\u05DB\u05D5\u05DC\u05D4 \u05DC\u05D4\u05E9\u05EA\u05E0\u05D5\u05EA \u05DE\u05D3\u05D9 \u05E4\u05E2\u05DD, \u05DE\u05D5\u05DE\u05DC\u05E5 \u05E9\u05EA\u05D7\u05D6\u05D5\u05E8 \u05D5\u05EA\u05E2\u05D9\u05D9\u05DF \u05D1\u05D4 \u05DE\u05E2\u05EA \u05DC\u05E2\u05EA. ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(239, "h2", 13);
        i0.ɵɵtext(240, "\u05EA\u05DB\u05E0\u05D9\u05DD \u05DE\u05E1\u05D7\u05E8\u05D9\u05D9\u05DD");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(241, "p");
        i0.ɵɵtext(242, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E2\u05E9\u05D5\u05D9\u05D4 \u05DC\u05D4\u05E6\u05D9\u05D1 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DC\u05E4\u05D9 \u05E9\u05D9\u05E7\u05D5\u05DC \u05D3\u05E2\u05EA\u05D4 \u05D4\u05D1\u05DC\u05E2\u05D3\u05D9, \u05EA\u05DB\u05E0\u05D9\u05DD \u05DE\u05E1\u05D7\u05E8\u05D9\u05D9\u05DD, \u05DB\u05D2\u05D5\u05DF \u05E4\u05E8\u05E1\u05D5\u05DE\u05D5\u05EA \u05D5\u05DE\u05D5\u05D3\u05E2\u05D5\u05EA, \u05D4\u05E0\u05DE\u05E1\u05E8\u05D9\u05DD \u05DC\u05E4\u05E8\u05E1\u05D5\u05DD \u05DE\u05D8\u05E2\u05DE\u05DD \u05E9\u05DC \u05DE\u05E4\u05E8\u05E1\u05DE\u05D9\u05DD \u05E9\u05D5\u05E0\u05D9\u05DD \u05D4\u05DE\u05D1\u05E7\u05E9\u05D9\u05DD \u05DC\u05D4\u05E6\u05D9\u05E2 \u05DC\u05DE\u05DB\u05D9\u05E8\u05D4 \u05DE\u05D5\u05E6\u05E8\u05D9\u05DD \u05D0\u05D5 \u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D5\u05D1\u05DB\u05DC\u05DC \u05D6\u05D4 \u05DE\u05D9\u05D3\u05E2 \u05E9\u05DE\u05E7\u05D5\u05E8\u05D5 \u05D1\u05EA\u05D5\u05DE\u05DB\u05D9\u05DD \u05D5\u05E0\u05D5\u05EA\u05E0\u05D9 \u05D7\u05E1\u05D5\u05EA, \u05D4\u05E2\u05E9\u05D5\u05D9 \u05DC\u05DC\u05D1\u05D5\u05E9 \u05D0\u05D5\u05E4\u05D9 \u05DE\u05E1\u05D7\u05E8\u05D9.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(243, "p");
        i0.ɵɵtext(244, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D0 \u05EA\u05D9\u05E9\u05D0 \u05D1\u05DB\u05DC \u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05DC\u05EA\u05DB\u05E0\u05D9\u05DD \u05D4\u05DE\u05E1\u05D7\u05E8\u05D9\u05D9\u05DD \u05E9\u05D9\u05E4\u05D5\u05E8\u05E1\u05DE\u05D5 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05DE\u05D8\u05E2\u05DD \u05E6\u05D3\u05D3\u05D9\u05DD \u05E9\u05DC\u05D9\u05E9\u05D9\u05D9\u05DD. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D9\u05E0\u05D4 \u05DB\u05D5\u05EA\u05D1\u05EA, \u05D1\u05D5\u05D3\u05E7\u05EA, \u05DE\u05D5\u05D5\u05D3\u05D0\u05EA \u05D0\u05D5 \u05E2\u05D5\u05E8\u05DB\u05EA \u05D0\u05EA \u05EA\u05D5\u05DB\u05DF \u05D4\u05E4\u05E8\u05E1\u05D5\u05DE\u05D9\u05DD \u05D4\u05DC\u05DC\u05D5 \u05D0\u05D5 \u05D0\u05DE\u05D9\u05EA\u05D5\u05EA\u05DD. \u05D4\u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05D4\u05D9\u05D7\u05D9\u05D3\u05D4 \u05DC\u05EA\u05DB\u05E0\u05D9\u05DD \u05D4\u05DE\u05E1\u05D7\u05E8\u05D9\u05D9\u05DD \u05D5\u05DC\u05DB\u05DC \u05EA\u05D5\u05E6\u05D0\u05D4 \u05E9\u05EA\u05D9\u05D2\u05E8\u05DD \u05DE\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D4\u05DD \u05D0\u05D5 \u05DE\u05D4\u05E1\u05EA\u05DE\u05DB\u05D5\u05EA \u05E2\u05DC\u05D9\u05D4\u05DD \u05D7\u05DC\u05D4 \u05E2\u05DC \u05D4\u05DE\u05E4\u05E8\u05E1\u05DE\u05D9\u05DD. \u05E4\u05E8\u05E1\u05D5\u05DD \u05EA\u05DB\u05E0\u05D9\u05DD \u05DE\u05E1\u05D7\u05E8\u05D9\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D9\u05E0\u05D5 \u05DE\u05D4\u05D5\u05D5\u05D4 \u05D4\u05DE\u05DC\u05E6\u05D4 \u05D0\u05D5 \u05E2\u05D9\u05D3\u05D5\u05D3 \u05DC\u05E8\u05DB\u05D5\u05E9 \u05D0\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD, \u05D4\u05E0\u05DB\u05E1\u05D9\u05DD \u05D0\u05D5 \u05D4\u05DE\u05D5\u05E6\u05E8\u05D9\u05DD \u05D4\u05DE\u05D5\u05E6\u05E2\u05D9\u05DD \u05D1\u05D4\u05DD \u05DC\u05DE\u05DB\u05D9\u05E8\u05D4.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(245, "p");
        i0.ɵɵtext(246, "\u05DB\u05DC \u05E2\u05E1\u05E7\u05D4 \u05E9\u05EA\u05D9\u05E2\u05E9\u05D4 \u05D1\u05E2\u05E7\u05D1\u05D5\u05EA \u05EA\u05DB\u05E0\u05D9\u05DD \u05DE\u05E1\u05D7\u05E8\u05D9\u05D9\u05DD \u05D4\u05DE\u05EA\u05E4\u05E8\u05E1\u05DE\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05EA\u05E1\u05D5\u05DB\u05DD \u05D9\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05D9\u05E0\u05DA \u05DC\u05D1\u05D9\u05DF \u05D4\u05DE\u05E4\u05E8\u05E1\u05DD \u05D4\u05E0\u05D5\u05D2\u05E2 \u05D1\u05D3\u05D1\u05E8. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D9\u05E0\u05E0\u05D4 \u05E6\u05D3 \u05DC\u05DB\u05DC \u05E2\u05E1\u05E7\u05D4 \u05DB\u05D6\u05D5 \u05D5\u05D4\u05D9\u05D0 \u05DC\u05D0 \u05EA\u05D9\u05E9\u05D0 \u05D1\u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D5\u05D8\u05D5\u05D1\u05D9\u05DF \u05E9\u05D9\u05D5\u05E6\u05E2\u05D5 \u05D1\u05EA\u05DB\u05E0\u05D9\u05DD \u05D4\u05DE\u05E1\u05D7\u05E8\u05D9\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D5 \u05D9\u05E8\u05DB\u05E9\u05D5 \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA\u05DD. \u05EA\u05D5\u05DB\u05DF \u05DE\u05E1\u05D7\u05E8\u05D9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D9\u05E1\u05D5\u05DE\u05DF \u05DB\u05DE\u05D5\u05D3\u05E2\u05D4 \u05E4\u05E8\u05E1\u05D5\u05DE\u05D9\u05EA \u05D5\u05D1\u05DB\u05DC \u05DE\u05E7\u05E8\u05D4 \u05DC\u05D0 \u05D9\u05E9\u05E4\u05D9\u05E2 \u05E2\u05DC \u05E1\u05D3\u05E8 \u05D4\u05EA\u05D5\u05E6\u05D0\u05D5\u05EA \u05D4\u05DE\u05D5\u05E6\u05D2\u05D5\u05EA \u05DC\u05D2\u05D5\u05DC\u05E9.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(247, "h2", 14);
        i0.ɵɵtext(248, "\u05E7\u05E0\u05D9\u05D9\u05DF \u05E8\u05D5\u05D7\u05E0\u05D9");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(249, "p");
        i0.ɵɵtext(250, "\u05DB\u05DC \u05D6\u05DB\u05D5\u05D9\u05D5\u05EA \u05D4\u05E7\u05E0\u05D9\u05D9\u05DF \u05D4\u05E8\u05D5\u05D7\u05E0\u05D9 \u05D5\u05D1\u05DB\u05DC\u05DC \u05D6\u05D4 \u05D6\u05DB\u05D5\u05D9\u05D5\u05EA \u05D4\u05D9\u05D5\u05E6\u05E8\u05D9\u05DD \u05D1\u05D0\u05EA\u05E8, \u05D1\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF, \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D4\u05DE\u05D5\u05E6\u05E2\u05D9\u05DD \u05D1\u05D4\u05DD \u05D5\u05D1\u05DB\u05DC \u05EA\u05D5\u05DB\u05DF \u05D4\u05DB\u05DC\u05D5\u05DC \u05D1\u05D4\u05DD (\u05DC\u05DE\u05E2\u05D8 \u05EA\u05DB\u05E0\u05D9\u05DD \u05D4\u05E0\u05DE\u05E1\u05E8\u05D9\u05DD \u05DC\u05E4\u05E8\u05E1\u05D5\u05DD \u05E2\u05DC \u05D9\u05D3\u05DA \u05D5\u05E2\u05DC \u05D9\u05D3\u05D9 \u05D9\u05EA\u05E8 \u05D4\u05DE\u05E9\u05EA\u05DE\u05E9\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA) \u05D4\u05D9\u05E0\u05DF \u05E9\u05DC \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05DC\u05D1\u05D3, \u05D0\u05D5 \u05E9\u05DC \u05E6\u05D3\u05D3\u05D9\u05DD \u05E9\u05DC\u05D9\u05E9\u05D9\u05D9\u05DD \u05D0\u05D7\u05E8\u05D9\u05DD, \u05E9\u05D4\u05EA\u05D9\u05E8\u05D5 \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D4\u05E9\u05EA\u05DE\u05E9 \u05D1\u05D4\u05DD. \u05D0\u05D9\u05DF \u05DC\u05D4\u05E2\u05EA\u05D9\u05E7, \u05DC\u05D4\u05E4\u05D9\u05E5, \u05DC\u05D4\u05E6\u05D9\u05D2 \u05D1\u05E4\u05D5\u05DE\u05D1\u05D9, \u05DC\u05D1\u05E6\u05E2 \u05D1\u05E4\u05D5\u05DE\u05D1\u05D9, \u05DC\u05E9\u05D3\u05E8, \u05DC\u05D4\u05E2\u05DE\u05D9\u05D3 \u05DC\u05E8\u05E9\u05D5\u05EA \u05D4\u05E6\u05D9\u05D1\u05D5\u05E8, \u05DC\u05E9\u05E0\u05D5\u05EA, \u05DC\u05E2\u05D1\u05D3, \u05DC\u05D9\u05E6\u05D5\u05E8 \u05D9\u05E6\u05D9\u05E8\u05D5\u05EA \u05E0\u05D2\u05D6\u05E8\u05D5\u05EA, \u05DC\u05DE\u05DB\u05D5\u05E8 \u05D0\u05D5 \u05DC\u05D4\u05E9\u05DB\u05D9\u05E8 \u05DB\u05DC \u05D7\u05DC\u05E7 \u05DE\u05DF \u05D4\u05E0\"\u05DC, \u05D1\u05D9\u05DF \u05E2\u05DC \u05D9\u05D3\u05DA \u05D5\u05D1\u05D9\u05DF \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D0\u05D5 \u05D1\u05E9\u05D9\u05EA\u05D5\u05E3 \u05E6\u05D3 \u05E9\u05DC\u05D9\u05E9\u05D9, \u05D1\u05DB\u05DC \u05D3\u05E8\u05DA \u05D0\u05D5 \u05D0\u05DE\u05E6\u05E2\u05D9, \u05D1\u05D9\u05DF \u05D0\u05DD \u05D0\u05DC\u05E7\u05D8\u05E8\u05D5\u05E0\u05D9\u05D9\u05DD, \u05DE\u05DE\u05D5\u05D7\u05E9\u05D1\u05D9\u05DD, \u05DE\u05DB\u05D0\u05E0\u05D9\u05D9\u05DD, \u05D0\u05D5\u05E4\u05D8\u05D9\u05D9\u05DD, \u05D0\u05DE\u05E6\u05E2\u05D9 \u05E6\u05D9\u05DC\u05D5\u05DD \u05D0\u05D5 \u05D4\u05E7\u05DC\u05D8\u05D4, \u05D0\u05D5 \u05D1\u05DB\u05DC \u05D0\u05DE\u05E6\u05E2\u05D9 \u05D5\u05D3\u05E8\u05DA \u05D0\u05D7\u05E8\u05EA, \u05D1\u05DC\u05D0 \u05E7\u05D1\u05DC\u05EA \u05D4\u05E1\u05DB\u05DE\u05D4 \u05D1\u05DB\u05EA\u05D1 \u05D5\u05DE\u05E8\u05D0\u05E9 \u05DE\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D5 \u05DE\u05D1\u05E2\u05DC\u05D9 \u05D4\u05D6\u05DB\u05D5\u05D9\u05D5\u05EA \u05D4\u05D0\u05D7\u05E8\u05D9\u05DD, \u05DC\u05E4\u05D9 \u05D4\u05E2\u05E0\u05D9\u05D9\u05DF \u05D5\u05D1\u05DB\u05E4\u05D5\u05E3 \u05DC\u05EA\u05E0\u05D0\u05D9 \u05D4\u05D4\u05E1\u05DB\u05DE\u05D4 (\u05DB\u05DB\u05DC \u05E9\u05EA\u05D9\u05E0\u05EA\u05DF). ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(251, "p");
        i0.ɵɵtext(252, "\u05E1\u05D9\u05DE\u05E0\u05D9 \u05D4\u05DE\u05E1\u05D7\u05E8 \u05D5\u05DE\u05D5\u05D3\u05E2\u05D5\u05EA \u05D4\u05E4\u05E8\u05E1\u05D5\u05DE\u05EA \u05E9\u05DC \u05DE\u05E4\u05E8\u05E1\u05DE\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA (\u05DB\u05DB\u05DC \u05E9\u05D9\u05E9\u05E0\u05DD) \u05D4\u05DD \u05E7\u05E0\u05D9\u05D9\u05E0\u05DD \u05E9\u05DC \u05DE\u05E4\u05E8\u05E1\u05DE\u05D9\u05DD \u05D0\u05DC\u05D4 \u05D1\u05DC\u05D1\u05D3. \u05D2\u05DD \u05D1\u05D4\u05DD \u05D0\u05D9\u05DF \u05DC\u05E2\u05E9\u05D5\u05EA \u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05DC\u05D0 \u05D4\u05E1\u05DB\u05DE\u05EA \u05D4\u05DE\u05E4\u05E8\u05E1\u05DD \u05D1\u05DB\u05EA\u05D1 \u05D5\u05DE\u05E8\u05D0\u05E9.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(253, "p");
        i0.ɵɵelementStart(254, "b");
        i0.ɵɵtext(255, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D9\u05E0\u05D4 \u05D8\u05D5\u05E2\u05E0\u05EA \u05DC\u05D1\u05E2\u05DC\u05D5\u05EA \u05D1\u05EA\u05DB\u05E0\u05D9\u05DD \u05E9\u05D0\u05EA\u05D4 \u05DE\u05D5\u05E1\u05E8 \u05DC\u05E4\u05E8\u05E1\u05D5\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D5 \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA\u05D5. \u05DB\u05D0\u05E9\u05E8 \u05D0\u05EA\u05D4 \u05DE\u05D5\u05E1\u05E8 \u05EA\u05DB\u05E0\u05D9\u05DD \u05DC\u05E4\u05E8\u05E1\u05D5\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D0\u05EA\u05D4 \u05DE\u05D0\u05E9\u05E8 \u05D1\u05E2\u05E6\u05DD \u05DE\u05E1\u05D9\u05E8\u05EA\u05DD \u05DC\u05E4\u05E8\u05E1\u05D5\u05DD \u05DB\u05D9 \u05D0\u05EA\u05D4 \u05D1\u05E2\u05DC \u05DE\u05DC\u05D5\u05D0 \u05D4\u05D6\u05DB\u05D5\u05D9\u05D5\u05EA \u05D1\u05D4\u05DD \u05D5\u05DB\u05D9 \u05D4\u05E0\u05DA \u05E8\u05E9\u05D0\u05D9 \u05DC\u05DE\u05D5\u05E1\u05E8\u05DD \u05DC\u05E4\u05E8\u05E1\u05D5\u05DD. \u05D0\u05DD \u05D0\u05D9\u05E0\u05DA \u05D4\u05D9\u05D5\u05E6\u05E8 \u05D0\u05D5 \u05D1\u05E2\u05DC \u05D4\u05D6\u05DB\u05D5\u05D9\u05D5\u05EA \u05D1\u05EA\u05DB\u05E0\u05D9\u05DD \u05E9\u05D0\u05EA\u05D4 \u05DE\u05D5\u05E1\u05E8 \u05DC\u05E4\u05E8\u05E1\u05D5\u05DD, \u05D0\u05EA\u05D4 \u05DE\u05D0\u05E9\u05E8 \u05E9\u05D4\u05E0\u05DA \u05D1\u05E2\u05DC \u05D4\u05E8\u05E9\u05D0\u05D4 \u05DB\u05D3\u05D9\u05DF \u05DE\u05D1\u05E2\u05DC \u05D4\u05D6\u05DB\u05D5\u05D9\u05D5\u05EA, \u05D4\u05DE\u05EA\u05D9\u05E8\u05D4 \u05DC\u05DA \u05DC\u05DE\u05E1\u05D5\u05E8 \u05D0\u05EA \u05D4\u05EA\u05DB\u05E0\u05D9\u05DD \u05DC\u05E4\u05E8\u05E1\u05D5\u05DD \u05D5\u05DC\u05EA\u05EA \u05D1\u05D4\u05DD \u05D6\u05DB\u05D5\u05D9\u05D5\u05EA \u05E9\u05D9\u05DE\u05D5\u05E9 \u05DB\u05DE\u05E4\u05D5\u05E8\u05D8 \u05DC\u05E2\u05D9\u05DC \u05D5\u05DC\u05D4\u05DC\u05DF. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D0 \u05EA\u05D9\u05E9\u05D0 \u05D1\u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05DC\u05DB\u05DC \u05E0\u05D6\u05E7 \u05E9\u05D9\u05D9\u05D2\u05E8\u05DD \u05DB\u05EA\u05D5\u05E6\u05D0\u05D4 \u05DE\u05D4\u05E4\u05E8\u05EA \u05D6\u05DB\u05D5\u05D9\u05D5\u05EA \u05E9\u05DC \u05D1\u05E2\u05DC\u05D9 \u05D4\u05D6\u05DB\u05D5\u05D9\u05D5\u05EA \u05D1\u05EA\u05DB\u05E0\u05D9\u05DD. \u05D4\u05E0\u05DA \u05E0\u05D5\u05E9\u05D0 \u05D1\u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05D4\u05DE\u05DC\u05D0\u05D4 \u05D5\u05D4\u05D1\u05DC\u05E2\u05D3\u05D9\u05EA \u05DC\u05DB\u05DC \u05E7\u05D9\u05E9\u05D5\u05E8, \u05D4\u05E6\u05D2\u05D4 \u05D0\u05D5 \u05E4\u05E8\u05E1\u05D5\u05DD \u05E9\u05DC \u05EA\u05DB\u05E0\u05D9\u05DD, \u05E9\u05E0\u05E2\u05E9\u05D5 \u05E2\u05DC \u05D9\u05D3\u05DA \u05D5\u05DE\u05EA\u05D7\u05D9\u05D9\u05D1 \u05DC\u05E9\u05E4\u05D5\u05EA \u05D0\u05EA \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05D2\u05D9\u05DF \u05DB\u05DC \u05E0\u05D6\u05E7 \u05E9\u05D9\u05D9\u05D2\u05E8\u05DD \u05DB\u05EA\u05D5\u05E6\u05D0\u05D4 \u05DE\u05DB\u05DA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(256, "p");
        i0.ɵɵelementStart(257, "b");
        i0.ɵɵtext(258, "\u05DE\u05D1\u05DC\u05D9 \u05DC\u05D2\u05E8\u05D5\u05E2 \u05DE\u05D4\u05D0\u05DE\u05D5\u05E8 \u05DC\u05E2\u05D9\u05DC, \u05DB\u05D0\u05E9\u05E8 \u05D0\u05EA\u05D4 \u05DE\u05D5\u05E1\u05E8 \u05E1\u05E8\u05D8\u05D5\u05E0\u05D9 \u05D5\u05D9\u05D3\u05D0\u05D5 \u05DC\u05E4\u05E8\u05E1\u05D5\u05DD \u05D1\u05DE\u05E1\u05D2\u05E8\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D0\u05EA\u05D4 \u05DE\u05E6\u05D4\u05D9\u05E8 \u05D5\u05DE\u05D0\u05E9\u05E8 \u05DB\u05D9 \u05E7\u05D9\u05D1\u05DC\u05EA \u05D0\u05EA \u05D4\u05E1\u05DB\u05DE\u05EA\u05DD \u05DB\u05D3\u05D9\u05DF \u05E9\u05DC \u05DB\u05DC \u05D4\u05DE\u05E6\u05D5\u05DC\u05DE\u05D9\u05DD \u05D1\u05DE\u05E1\u05D2\u05E8\u05EA \u05D4\u05E4\u05E8\u05E1\u05D5\u05DD (\u05D5\u05DB\u05DB\u05DC \u05D5\u05DE\u05D3\u05D5\u05D1\u05E8 \u05D1\u05DE\u05E6\u05D5\u05DC\u05DE\u05D9\u05DD \u05E7\u05D8\u05D9\u05E0\u05D9\u05DD \u2013 \u05D0\u05EA \u05D4\u05E1\u05DB\u05DE\u05EA \u05D4\u05D5\u05E8\u05D9\u05D4\u05DD \u05D0\u05D5 \u05E0\u05E6\u05D9\u05D2\u05D9\u05D4\u05DD \u05D4\u05D7\u05D5\u05E7\u05D9\u05D9\u05DD), \u05D1\u05D9\u05DF \u05D4\u05D9\u05EA\u05E8 \u05D1\u05D9\u05D7\u05E1 \u05DC\u05E6\u05D9\u05DC\u05D5\u05DE\u05DD, \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D3\u05DE\u05D5\u05EA\u05DD \u05D5\u05D1\u05D6\u05D4\u05D5\u05EA\u05DD \u05D1\u05DE\u05E1\u05D2\u05E8\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05E1\u05E8\u05D8\u05D5\u05E0\u05D9 \u05D4\u05D5\u05D9\u05D3\u05D0\u05D5 \u05D4\u05DE\u05E6\u05D9\u05D2\u05D9\u05DD \u05D0\u05D5\u05EA\u05DD \u05D1\u05D4\u05EA\u05D0\u05DD \u05DC\u05E8\u05D9\u05E9\u05D9\u05D5\u05DF \u05E9\u05D4\u05E2\u05E0\u05E7\u05EA \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DB\u05D0\u05DE\u05D5\u05E8 \u05DC\u05D4\u05DC\u05DF.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(259, "p");
        i0.ɵɵtext(260, "\u05D1\u05DE\u05E1\u05D9\u05E8\u05EA \u05D4\u05EA\u05DB\u05E0\u05D9\u05DD \u05DC\u05E4\u05E8\u05E1\u05D5\u05DD \u05D0\u05EA\u05D4 \u05DE\u05E7\u05E0\u05D4 \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E8\u05D9\u05E9\u05D9\u05D5\u05DF-\u05D7\u05D9\u05E0\u05DD, \u05DB\u05DC\u05DC \u05E2\u05D5\u05DC\u05DE\u05D9 \u05D5\u05D1\u05DC\u05EA\u05D9 \u05DE\u05D5\u05D2\u05D1\u05DC \u05D1\u05D6\u05DE\u05DF, \u05DC\u05D4\u05E2\u05EA\u05D9\u05E7, \u05DC\u05E9\u05DB\u05E4\u05DC, \u05DC\u05D4\u05E4\u05D9\u05E5, \u05DC\u05E9\u05D5\u05D5\u05E7, \u05DC\u05D4\u05E2\u05DE\u05D9\u05D3 \u05DC\u05E8\u05E9\u05D5\u05EA \u05DC\u05E6\u05D9\u05D1\u05D5\u05E8, \u05DC\u05E9\u05D3\u05E8, \u05DC\u05E2\u05D1\u05D3, \u05DC\u05E2\u05E8\u05D5\u05DA, \u05DC\u05EA\u05E8\u05D2\u05DD \u05D5\u05DC\u05D4\u05E9\u05EA\u05DE\u05E9 \u05D1\u05EA\u05DB\u05E0\u05D9\u05DD \u05DB\u05D0\u05DE\u05D5\u05E8 \u05D1\u05DB\u05DC \u05D3\u05E8\u05DA \u05E0\u05D5\u05E1\u05E4\u05EA, \u05DC\u05E4\u05D9 \u05E9\u05D9\u05E7\u05D5\u05DC \u05D3\u05E2\u05EA\u05D4, \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05D1\u05DB\u05DC \u05E0\u05D2\u05D6\u05E8\u05EA \u05E9\u05DC\u05D5, \u05D1\u05DB\u05DC \u05DE\u05D3\u05D9\u05D4 \u05E9\u05D4\u05D9\u05D0 \u05D5/\u05D0\u05D5 \u05D1\u05DB\u05DC \u05DE\u05E1\u05D2\u05E8\u05EA \u05D0\u05D7\u05E8\u05EA, \u05D1\u05D9\u05DF \u05D0\u05DD \u05D0\u05DC\u05E7\u05D8\u05E8\u05D5\u05E0\u05D9\u05EA \u05D5\u05D1\u05D9\u05DF \u05D0\u05DD \u05DE\u05D5\u05D3\u05E4\u05E1\u05EA. \u05DB\u05D0\u05DE\u05D5\u05E8, \u05E8\u05D9\u05E9\u05D9\u05D5\u05DF \u05D6\u05D4 \u05E0\u05D9\u05EA\u05DF \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05DC\u05D0 \u05DE\u05EA\u05DF \u05EA\u05DE\u05D5\u05E8\u05D4 \u05DB\u05DC\u05E9\u05D4\u05D9\u05D0, \u05DC\u05E8\u05D1\u05D5\u05EA \u05D0\u05D6\u05DB\u05D5\u05E8 ('\u05E7\u05E8\u05D3\u05D9\u05D8') \u05D1\u05DE\u05D9\u05D3\u05EA \u05D4\u05D0\u05E4\u05E9\u05E8, \u05EA\u05DE\u05DC\u05D5\u05D2\u05D9\u05DD \u05D0\u05D5 \u05D8\u05D5\u05D1\u05D5\u05EA \u05D4\u05E0\u05D0\u05D4 \u05D0\u05D7\u05E8\u05D5\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(261, "h2", 15);
        i0.ɵɵtext(262, "\u05D4\u05E1\u05E8\u05EA \u05EA\u05DB\u05E0\u05D9\u05DD \u05DE\u05D4\u05E9\u05D9\u05E8\u05D5\u05EA");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(263, "p");
        i0.ɵɵelementStart(264, "b");
        i0.ɵɵtext(265, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DE\u05DB\u05D1\u05D3\u05EA \u05D0\u05EA \u05D6\u05DB\u05D5\u05D9\u05D5\u05EA\u05D9\u05D4\u05DD \u05E9\u05DC \u05D0\u05D7\u05E8\u05D9\u05DD, \u05DC\u05E8\u05D1\u05D5\u05EA \u05D6\u05DB\u05D5\u05D9\u05D5\u05EA \u05D4\u05E7\u05E0\u05D9\u05D9\u05DF \u05D4\u05E8\u05D5\u05D7\u05E0\u05D9 \u05E9\u05DC \u05E6\u05D3\u05D3\u05D9\u05DD \u05E9\u05DC\u05D9\u05E9\u05D9\u05D9\u05DD \u05DB\u05DE\u05D5 \u05D2\u05DD \u05D0\u05EA \u05D6\u05DB\u05D5\u05EA\u05DD \u05DC\u05E9\u05DD \u05D8\u05D5\u05D1 \u05D5\u05DC\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA. \u05D0\u05DD \u05D4\u05E0\u05DA \u05E1\u05D1\u05D5\u05E8 \u05DB\u05D9 \u05EA\u05D5\u05DB\u05DF \u05DB\u05DC\u05E9\u05D4\u05D5 \u05D4\u05DE\u05EA\u05E4\u05E8\u05E1\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05E4\u05D5\u05D2\u05E2 \u05D1\u05D6\u05DB\u05D5\u05D9\u05D5\u05EA \u05DB\u05DC\u05E9\u05D4\u05DF \u05D4\u05E0\u05EA\u05D5\u05E0\u05D5\u05EA \u05DC\u05DA, \u05D4\u05E0\u05DA \u05DE\u05D5\u05D6\u05DE\u05DF \u05D5\u05DE\u05EA\u05D1\u05E7\u05E9 \u05DC\u05D9\u05D9\u05D3\u05E2 \u05D0\u05EA \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E2\u05DC \u05DB\u05DA \u05DE\u05D9\u05D9\u05D3\u05D9\u05EA \u05D1\u05DB\u05EA\u05D1, \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05E4\u05E8\u05D8\u05D9 \u05D9\u05E6\u05D9\u05E8\u05EA \u05D4\u05E7\u05E9\u05E8 \u05D4\u05DE\u05E6\u05D5\u05D9\u05E0\u05D9\u05DD \u05D1\u05EA\u05E0\u05D0\u05D9\u05DD \u05D0\u05DC\u05D4. \u05E6\u05E2\u05D3\u05D9\u05DD \u05E9\u05D9\u05D9\u05E0\u05E7\u05D8\u05D5 \u05E2\u05DC-\u05D9\u05D3\u05D9 \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DB\u05DB\u05DC \u05E9\u05D9\u05E0\u05E7\u05D8\u05D5, \u05D1\u05D4\u05E1\u05EA\u05DE\u05DA \u05E2\u05DC \u05DE\u05D9\u05D3\u05E2 \u05E9\u05D9\u05D5\u05E2\u05D1\u05E8 \u05DC\u05D9\u05D3\u05D9\u05D4, \u05DC\u05D0 \u05D9\u05D4\u05D5\u05D5 \u05D4\u05D5\u05DB\u05D7\u05D4 \u05DC\u05D4\u05E4\u05E8\u05EA \u05D6\u05DB\u05D5\u05EA \u05DB\u05DC\u05E9\u05D4\u05D9. ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(266, "h2", 16);
        i0.ɵɵtext(267, "\u05D0\u05D7\u05E8\u05D9\u05D5\u05EA");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(268, "p");
        i0.ɵɵtext(269, "\u05D1\u05DB\u05DC \u05DE\u05E7\u05D5\u05DD \u05D1\u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD \u05D1\u05D5 \u05DE\u05E4\u05D5\u05E8\u05D8\u05EA \u05D0\u05D7\u05E8\u05D9\u05D5\u05EA\u05D4 \u05E9\u05DC \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05EA\u05D4\u05D9\u05D4 \u05D4\u05DB\u05D5\u05D5\u05E0\u05D4 \u05D2\u05DD \u05DC\u05D0\u05D7\u05E8\u05D9\u05D5\u05EA\u05DD \u05E9\u05DC \u05E2\u05D5\u05D1\u05D3\u05D9\u05D4, \u05DE\u05E0\u05D4\u05DC\u05D9\u05D4 \u05D5\u05D4\u05E4\u05D5\u05E2\u05DC\u05D9\u05DD \u05DE\u05DB\u05D5\u05D7\u05D4 \u05D0\u05D5 \u05D1\u05E9\u05DE\u05D4.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(270, "p");
        i0.ɵɵelementStart(271, "b");
        i0.ɵɵtext(272, "\u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D0\u05EA\u05E8, \u05D1\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF \u05D5\u05D1\u05DB\u05DC \u05EA\u05D5\u05DB\u05DF, \u05D0\u05D5 \u05E9\u05D9\u05E8\u05D5\u05EA \u05D4\u05DB\u05DC\u05D5\u05DC\u05D9\u05DD \u05D1\u05D4\u05DD \u05D9\u05D9\u05E2\u05E9\u05D4 \u05E2\u05DC \u05D0\u05D7\u05E8\u05D9\u05D5\u05EA\u05DA \u05D4\u05D1\u05DC\u05E2\u05D3\u05D9\u05EA \u05D5\u05D4\u05DE\u05DC\u05D0\u05D4");
        i0.ɵɵelementEnd();
        i0.ɵɵtext(273, " \u05D5\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D0 \u05EA\u05D9\u05E9\u05D0 \u05D1\u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05DC\u05DB\u05DC \u05E0\u05D6\u05E7 \u05D9\u05E9\u05D9\u05E8 \u05D0\u05D5 \u05E2\u05E7\u05D9\u05E3 \u05E9\u05D9\u05D9\u05D2\u05E8\u05DE\u05D5 \u05DC\u05DA \u05D0\u05D5 \u05DC\u05DE\u05D9 \u05DE\u05D8\u05E2\u05DE\u05DA, \u05D0\u05D5 \u05DC\u05DB\u05DC \u05E6\u05D3 \u05E9\u05DC\u05D9\u05E9\u05D9 \u05D0\u05D7\u05E8, \u05DC\u05E8\u05D1\u05D5\u05EA \u05DB\u05DC \u05D4\u05E4\u05E1\u05D3, \u05D0\u05D5\u05D1\u05D3\u05DF \u05D0\u05D5 \u05D4\u05D5\u05E6\u05D0\u05D4, \u05D4\u05E7\u05E9\u05D5\u05E8\u05D9\u05DD, \u05D0\u05D5 \u05E0\u05D5\u05D1\u05E2\u05D9\u05DD, \u05D1\u05DE\u05D9\u05E9\u05E8\u05D9\u05DF \u05D0\u05D5 \u05D1\u05E2\u05E7\u05D9\u05E4\u05D9\u05DF \u05E2\u05DD \u05D4\u05D0\u05EA\u05E8, \u05D4\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF \u05D5\u05DB\u05DC \u05EA\u05D5\u05DB\u05DF \u05D5\u05E9\u05D9\u05E8\u05D5\u05EA \u05D4\u05DB\u05DC\u05D5\u05DC\u05D9\u05DD \u05D1\u05D4\u05DD.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(274, "p");
        i0.ɵɵtext(275, "\u05D4\u05EA\u05DB\u05E0\u05D9\u05DD \u05D5\u05D4\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05E0\u05D9\u05EA\u05E0\u05D9\u05DD \u05DC\u05E9\u05D9\u05DE\u05D5\u05E9 \u05DB\u05DE\u05D5\u05EA \u05E9\u05D4\u05DD (AS IS). \u05DC\u05D0 \u05E0\u05D9\u05EA\u05DF \u05DC\u05D4\u05EA\u05D0\u05D9\u05DE\u05DD \u05DC\u05E6\u05E8\u05DB\u05D9\u05D5 \u05E9\u05DC \u05DB\u05DC \u05D0\u05D3\u05DD \u05D5\u05D0\u05D3\u05DD. \u05D0\u05EA\u05D4 \u05DE\u05D0\u05E9\u05E8 \u05D1\u05D6\u05D0\u05EA \u05DB\u05D9 \u05D1\u05D3\u05E7\u05EA \u05D0\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05DE\u05E6\u05D0\u05EA \u05D0\u05D5\u05EA\u05D5 \u05DE\u05EA\u05D0\u05D9\u05DD \u05DC\u05E6\u05E8\u05DB\u05D9\u05D9\u05DA \u05D5\u05DE\u05D8\u05E8\u05D5\u05EA\u05D9\u05D9\u05DA. \u05DC\u05D0 \u05EA\u05D4\u05D9\u05D4 \u05DC\u05DA \u05DB\u05DC \u05D8\u05E2\u05E0\u05D4, \u05EA\u05D1\u05D9\u05E2\u05D4 \u05D0\u05D5 \u05D3\u05E8\u05D9\u05E9\u05D4 \u05DB\u05DC\u05E4\u05D9 \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05D2\u05D9\u05DF \u05EA\u05DB\u05D5\u05E0\u05D5\u05EA \u05D4\u05EA\u05DB\u05E0\u05D9\u05DD \u05D5\u05D4\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD, \u05D9\u05DB\u05D5\u05DC\u05D5\u05EA\u05D9\u05D4\u05DD, \u05DE\u05D2\u05D1\u05DC\u05D5\u05EA\u05D9\u05D4\u05DD, \u05D4\u05DB\u05DC\u05DC\u05D9\u05DD \u05D4\u05E0\u05D5\u05D4\u05D2\u05D9\u05DD \u05DC\u05D2\u05D1\u05D9\u05D4\u05DD \u05D5\u05D4\u05EA\u05D0\u05DE\u05EA\u05DD \u05D0\u05DC\u05D9\u05DA \u05D5\u05DC\u05E6\u05E8\u05DB\u05D9\u05DA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(276, "p");
        i0.ɵɵtext(277, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D9\u05E0\u05D4 \u05DE\u05EA\u05D7\u05D9\u05D9\u05D1\u05EA \u05DB\u05D9 \u05DC\u05EA\u05DB\u05E0\u05D9\u05DD \u05E9\u05EA\u05E4\u05E8\u05E1\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D0\u05D5 \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA\u05D5 \u05EA\u05D4\u05D9\u05D4 \u05D4\u05D9\u05E2\u05E0\u05D5\u05EA. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D9\u05E0\u05D4 \u05D9\u05DB\u05D5\u05DC\u05D4 \u05DC\u05D3\u05E2\u05EA \u05D0\u05D9\u05DC\u05D5 \u05EA\u05D2\u05D5\u05D1\u05D5\u05EA (\u05D0\u05DD \u05D1\u05DB\u05DC\u05DC) \u05EA\u05E7\u05D1\u05DC \u05D1\u05E2\u05E7\u05D1\u05D5\u05EA \u05D4\u05E4\u05E8\u05E1\u05D5\u05DD \u05D5\u05DE\u05D9 \u05D9\u05D2\u05D9\u05D1 \u05DC\u05EA\u05DB\u05E0\u05D9\u05DD \u05E9\u05EA\u05E4\u05E8\u05E1\u05DD \u05D5\u05DC\u05DB\u05DF \u05DC\u05D0 \u05EA\u05D9\u05E9\u05D0 \u05DB\u05DC\u05E4\u05D9\u05DA (\u05D0\u05D5 \u05DB\u05DC\u05E4\u05D9 \u05DE\u05D9 \u05DE\u05D8\u05E2\u05DE\u05DA) \u05D1\u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05DB\u05DC\u05E9\u05D4\u05D9 \u05DC\u05EA\u05D2\u05D5\u05D1\u05D5\u05EA \u05D4\u05DC\u05DC\u05D5, \u05DC\u05D6\u05D4\u05D5\u05EA \u05D4\u05E4\u05D5\u05E0\u05D9\u05DD \u05D0\u05DC\u05D9\u05DA \u05D0\u05D5 \u05DC\u05DB\u05DC \u05EA\u05D5\u05E6\u05D0\u05D4 \u05E9\u05EA\u05E0\u05D1\u05E2 \u05DE\u05D4\u05E4\u05E8\u05E1\u05D5\u05DD. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D0 \u05EA\u05D4\u05D9\u05D4 \u05D0\u05D7\u05E8\u05D0\u05D9\u05EA \u05DC\u05DB\u05DC \u05E9\u05D9\u05DE\u05D5\u05E9 \u05E9\u05D9\u05D9\u05E2\u05E9\u05D4 \u05E2\u05DC \u05D9\u05D3\u05D9 \u05E6\u05D3 \u05E9\u05DC\u05D9\u05E9\u05D9 \u05D1\u05EA\u05DB\u05E0\u05D9\u05DD \u05E9\u05D9\u05E4\u05D5\u05E8\u05E1\u05DE\u05D5 \u05E2\u05DC-\u05D9\u05D3\u05DA. \u05D4\u05D0\u05EA\u05E8 \u05E2\u05D5\u05DE\u05D3 \u05D1\u05EA\u05E7\u05DF \u05D0\u05D7\u05D9\u05D3 \u05DC\u05D0\u05D1\u05D8\u05D7\u05EA \u05DE\u05D9\u05D3\u05E2 \u05DC\u05E2\u05E1\u05E7\u05D9\u05DD (PCI-DSS)");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(278, "p");
        i0.ɵɵelementStart(279, "b");
        i0.ɵɵtext(280, "\u05DB\u05DC \u05D4\u05D7\u05DC\u05D8\u05D4 \u05E9\u05EA\u05E7\u05D1\u05DC \u05D1\u05D9\u05D7\u05E1 \u05DC\u05EA\u05DB\u05E0\u05D9\u05DD \u05E9\u05D4\u05EA\u05E4\u05E8\u05E1\u05DE\u05D5 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D0\u05D5 \u05D1\u05D9\u05D7\u05E1 \u05DC\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D4\u05DD, \u05D4\u05D9\u05D0 \u05D1\u05D0\u05D7\u05E8\u05D9\u05D5\u05EA\u05DA \u05D4\u05DE\u05DC\u05D0\u05D4 \u05D1\u05DC\u05D1\u05D3. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D9\u05E0\u05D4 \u05DE\u05EA\u05D7\u05D9\u05D9\u05D1\u05EA \u05DB\u05D9 \u05EA\u05DB\u05E0\u05D9\u05DD, \u05DE\u05D5\u05E6\u05E8\u05D9\u05DD \u05D5\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05E9\u05DC \u05E6\u05D3\u05D3\u05D9\u05DD \u05E9\u05DC\u05D9\u05E9\u05D9\u05D9\u05DD, \u05DC\u05E8\u05D1\u05D5\u05EA \u05E9\u05DC \u05D1\u05E2\u05DC\u05D9 \u05E2\u05E1\u05E7\u05D9\u05DD \u05D5\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D5\u05E9\u05DC \u05DE\u05E9\u05EA\u05DE\u05E9\u05D9\u05DD, \u05D4\u05DE\u05EA\u05E4\u05E8\u05E1\u05DE\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D9\u05D4\u05D9\u05D5 \u05DE\u05DC\u05D0\u05D9\u05DD, \u05E0\u05DB\u05D5\u05E0\u05D9\u05DD, \u05D7\u05D5\u05E7\u05D9\u05D9\u05DD \u05D0\u05D5 \u05DE\u05D3\u05D5\u05D9\u05E7\u05D9\u05DD \u05D0\u05D5 \u05D9\u05D4\u05DC\u05DE\u05D5 \u05D0\u05EA \u05E6\u05D9\u05E4\u05D9\u05D5\u05EA\u05D9\u05DA \u05D5\u05D3\u05E8\u05D9\u05E9\u05D5\u05EA\u05D9\u05DA. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D0 \u05EA\u05D9\u05E9\u05D0 \u05D1\u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05DB\u05DC\u05E9\u05D4\u05D9 \u05DC\u05DB\u05DC \u05EA\u05D5\u05E6\u05D0\u05D4 \u05E9\u05EA\u05E0\u05D1\u05E2 \u05DE\u05D4\u05DD, \u05D0\u05D5 \u05DE\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D4\u05DD, \u05D0\u05D5 \u05DE\u05D4\u05E1\u05EA\u05DE\u05DB\u05D5\u05EA \u05E2\u05DC\u05D9\u05D4\u05DD. \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9 \u05D5\u05D1\u05EA\u05DB\u05E0\u05D9 \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D4\u05D5\u05D0 \u05E2\u05DC \u05D0\u05D7\u05E8\u05D9\u05D5\u05EA\u05DA \u05D1\u05DC\u05D1\u05D3.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(281, "p");
        i0.ɵɵtext(282, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DE\u05E2\u05D5\u05D3\u05D3\u05EA \u05D0\u05D5\u05EA\u05DA \u05DC\u05D4\u05EA\u05D9\u05D9\u05D7\u05E1 \u05D1\u05D6\u05D4\u05D9\u05E8\u05D5\u05EA \u05D5\u05D1\u05D1\u05D9\u05E7\u05D5\u05E8\u05EA\u05D9\u05D5\u05EA \u05DC\u05EA\u05DB\u05E0\u05D9\u05DD \u05E9\u05DE\u05EA\u05E4\u05E8\u05E1\u05DE\u05D9\u05DD \u05D1\u05D0\u05D9\u05E0\u05D8\u05E8\u05E0\u05D8, \u05D5\u05D1\u05DB\u05DC\u05DC \u05D6\u05D4 \u05EA\u05DB\u05E0\u05D9\u05DD \u05E9\u05DE\u05E4\u05E8\u05E1\u05DE\u05D9\u05DD \u05DE\u05E9\u05EA\u05DE\u05E9\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DC\u05E8\u05D1\u05D5\u05EA \u05EA\u05D2\u05D5\u05D1\u05D5\u05EA \u05D5\u05D1\u05D9\u05E7\u05D5\u05E8\u05D5\u05EA \u05D5\u05DE\u05D9\u05D3\u05E2 \u05E2\u05DC \u05D1\u05EA\u05D9 \u05E2\u05E1\u05E7 \u05D5\u05E1\u05E4\u05E7\u05D9 \u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD, \u05D5\u05DB\u05DF \u05EA\u05DB\u05E0\u05D9\u05DD \u05E9\u05DE\u05E4\u05E8\u05E1\u05DE\u05EA \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA. \u05EA\u05DB\u05E0\u05D9\u05DD \u05D0\u05DC\u05D4 \u05D0\u05D9\u05E0\u05DD \u05EA\u05D7\u05DC\u05D9\u05E3 \u05DC\u05D9\u05D9\u05E2\u05D5\u05E5 \u05D0\u05D5 \u05DC\u05D8\u05D9\u05E4\u05D5\u05DC \u05DE\u05E7\u05E6\u05D5\u05E2\u05D9\u05D9\u05DD \u05D1\u05DE\u05E7\u05D5\u05DD \u05D1\u05D5 \u05D4\u05DD \u05E0\u05D3\u05E8\u05E9\u05D9\u05DD. \u05DC\u05E2\u05D9\u05EA\u05D9\u05DD \u05EA\u05DB\u05E0\u05D9\u05DD \u05D0\u05DC\u05D4 \u05DE\u05E9\u05E7\u05E4\u05D9\u05DD \u05D2\u05D9\u05E9\u05D4 \u05D0\u05D5 \u05D3\u05E2\u05D4 \u05D0\u05D9\u05E9\u05D9\u05EA \u05DE\u05E1\u05D5\u05D9\u05DE\u05EA \u05E9\u05DC \u05D4\u05DB\u05D5\u05EA\u05D1 \u05D5\u05E2\u05DC \u05DB\u05DF \u05D9\u05E9 \u05DC\u05D4\u05EA\u05D9\u05D9\u05D7\u05E1 \u05D0\u05DC\u05D9\u05D4\u05DD \u05D1\u05D6\u05D4\u05D9\u05E8\u05D5\u05EA \u05D4\u05DE\u05EA\u05D1\u05E7\u05E9. \u05D0\u05E0\u05D5 \u05DE\u05E9\u05EA\u05D3\u05DC\u05D9\u05DD \u05E9\u05D4\u05DE\u05D9\u05D3\u05E2 \u05D5\u05D4\u05EA\u05DB\u05E0\u05D9\u05DD \u05E9\u05D0\u05E0\u05D5 \u05DE\u05E6\u05D9\u05D2\u05D9\u05DD \u05DC\u05DB\u05DD \u05D9\u05D4\u05D9\u05D4 \u05D0\u05DE\u05D9\u05DF \u05D5\u05DE\u05D3\u05D5\u05D9\u05E7, \u05D0\u05D5\u05DC\u05DD \u05D0\u05D9\u05DF \u05D1\u05D9\u05DB\u05D5\u05DC\u05EA\u05E0\u05D5 \u05DC\u05D4\u05D1\u05D8\u05D9\u05D7 \u05D6\u05D0\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(283, "p");
        i0.ɵɵtext(284, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D9\u05E0\u05D4 \u05DE\u05EA\u05D7\u05D9\u05D9\u05D1\u05EA \u05E9\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9 \u05D5\u05EA\u05DB\u05E0\u05D9 \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D0 \u05D9\u05D5\u05E4\u05E8\u05E2\u05D5, \u05D9\u05D9\u05E0\u05EA\u05E0\u05D5 \u05DB\u05E1\u05D3\u05E8\u05DD \u05D0\u05D5 \u05D1\u05DC\u05D0 \u05D4\u05E4\u05E1\u05E7\u05D5\u05EA, \u05D9\u05EA\u05E7\u05D9\u05D9\u05DE\u05D5 \u05D1\u05D1\u05D8\u05D7\u05D4 \u05D5\u05DC\u05DC\u05D0 \u05D8\u05E2\u05D5\u05D9\u05D5\u05EA, \u05D5\u05D9\u05D4\u05D9\u05D5 \u05D7\u05E1\u05D9\u05E0\u05D9\u05DD \u05DE\u05E4\u05E0\u05D9 \u05D2\u05D9\u05E9\u05D4 \u05D1\u05DC\u05EA\u05D9-\u05DE\u05D5\u05E8\u05E9\u05D9\u05EA \u05DC\u05DE\u05D7\u05E9\u05D1\u05D9 \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D5 \u05DE\u05E4\u05E0\u05D9 \u05E0\u05D6\u05E7\u05D9\u05DD, \u05E7\u05DC\u05E7\u05D5\u05DC\u05D9\u05DD, \u05EA\u05E7\u05DC\u05D5\u05EA \u05D0\u05D5 \u05DB\u05E9\u05DC\u05D9\u05DD - \u05D5\u05D4\u05DB\u05D5\u05DC, \u05D1\u05D7\u05D5\u05DE\u05E8\u05D4, \u05D1\u05EA\u05D5\u05DB\u05E0\u05D4, \u05D1\u05E7\u05D5\u05D5\u05D9 \u05D5\u05D1\u05DE\u05E2\u05E8\u05DB\u05D5\u05EA \u05EA\u05E7\u05E9\u05D5\u05E8\u05EA, \u05D0\u05E6\u05DC \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D5 \u05D0\u05E6\u05DC \u05DE\u05D9 \u05DE\u05E1\u05E4\u05E7\u05D9\u05D4. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D0 \u05EA\u05D9\u05E9\u05D0 \u05D1\u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05DC\u05DB\u05DC \u05E0\u05D6\u05E7 \u05D4\u05E0\u05D5\u05D1\u05E2 \u05DE\u05D0\u05D5\u05D1\u05D3\u05DF \u05E4\u05E8\u05D8\u05D9\u05D9\u05DA, \u05D4\u05DE\u05D9\u05D3\u05E2 \u05D1\u05D7\u05E9\u05D1\u05D5\u05E0\u05DA \u05D0\u05D5 \u05DC\u05E9\u05D9\u05D1\u05D5\u05E9 \u05D1\u05E4\u05E8\u05D8\u05D9\u05DD \u05D0\u05DC\u05D4 \u2013 \u05D1\u05D9\u05DF \u05D1\u05D0\u05D5\u05E4\u05DF \u05DE\u05DC\u05D0 \u05D5\u05D1\u05D9\u05DF \u05D7\u05DC\u05E7\u05D9\u05EA, \u05DB\u05EA\u05D5\u05E6\u05D0\u05D4 \u05DE\u05DB\u05E9\u05DC \u05D1\u05DE\u05E2\u05E8\u05DB\u05D5\u05EA \u05D4\u05DE\u05D7\u05E9\u05D5\u05D1 \u05E9\u05DC\u05DA \u05D0\u05D5 \u05E9\u05DC \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D0\u05D5 \u05E9\u05DC \u05DE\u05D9 \u05DE\u05D8\u05E2\u05DE\u05D4.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(285, "h2", 17);
        i0.ɵɵtext(286, "\u05E9\u05D9\u05E4\u05D5\u05D9");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(287, "p");
        i0.ɵɵtext(288, "\u05D4\u05E0\u05DA \u05DE\u05EA\u05D7\u05D9\u05D9\u05D1 \u05DC\u05E9\u05E4\u05D5\u05EA \u05D0\u05EA \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05E2\u05D5\u05D1\u05D3\u05D9\u05D4, \u05DE\u05E0\u05D4\u05DC\u05D9\u05D4 \u05D0\u05D5 \u05DE\u05D9 \u05DE\u05D8\u05E2\u05DE\u05D4, \u05D1\u05D2\u05D9\u05DF \u05DB\u05DC \u05E0\u05D6\u05E7, \u05D4\u05E4\u05E1\u05D3, \u05D0\u05D1\u05D3\u05DF-\u05E8\u05D5\u05D5\u05D7, \u05EA\u05E9\u05DC\u05D5\u05DD \u05D0\u05D5 \u05D4\u05D5\u05E6\u05D0\u05D4 \u05E9\u05D9\u05D9\u05D2\u05E8\u05DE\u05D5 \u05DC\u05D4\u05DD - \u05D5\u05D1\u05DB\u05DC\u05DC \u05D6\u05D4 \u05E9\u05DB\"\u05D8 \u05E2\u05D5\"\u05D3 \u05D5\u05D4\u05D5\u05E6\u05D0\u05D5\u05EA \u05DE\u05E9\u05E4\u05D8 - \u05E2\u05E7\u05D1 \u05D4\u05E4\u05E8\u05EA \u05EA\u05E0\u05D0\u05D9 \u05E9\u05D9\u05DE\u05D5\u05E9 \u05D0\u05DC\u05D4 \u05D0\u05D5 \u05D0\u05D9\u05DC\u05D5 \u05DE\u05D4\u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD. \u05D1\u05E0\u05D5\u05E1\u05E3, \u05EA\u05E9\u05E4\u05D4 \u05D0\u05EA \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05E2\u05D5\u05D1\u05D3\u05D9\u05D4, \u05DE\u05E0\u05D4\u05DC\u05D9\u05D4 \u05D0\u05D5 \u05DE\u05D9 \u05DE\u05D8\u05E2\u05DE\u05D4 \u05D1\u05D2\u05D9\u05DF \u05DB\u05DC \u05D8\u05E2\u05E0\u05D4, \u05EA\u05D1\u05D9\u05E2\u05D4 \u05D0\u05D5 \u05D3\u05E8\u05D9\u05E9\u05D4 \u05E9\u05EA\u05D5\u05E2\u05DC\u05D4 \u05E0\u05D2\u05D3\u05DD \u05E2\u05DC-\u05D9\u05D3\u05D9 \u05E6\u05D3 \u05E9\u05DC\u05D9\u05E9\u05D9 \u05DB\u05DC\u05E9\u05D4\u05D5 \u05DB\u05EA\u05D5\u05E6\u05D0\u05D4 \u05DE\u05EA\u05DB\u05E0\u05D9\u05DD \u05E9\u05DE\u05E1\u05E8\u05EA \u05DC\u05E4\u05E8\u05E1\u05D5\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D0\u05D5 \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DB\u05EA\u05D5\u05E6\u05D0\u05D4 \u05DE\u05E7\u05D9\u05E9\u05D5\u05E8\u05D9\u05DD \u05E9\u05D1\u05D9\u05E6\u05E2\u05EA, \u05D0\u05D5 \u05DB\u05DC \u05E9\u05D9\u05DE\u05D5\u05E9 \u05D0\u05D7\u05E8 \u05E9\u05E2\u05E9\u05D9\u05EA \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(289, "h2", 18);
        i0.ɵɵtext(290, "\u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05E6\u05D3\u05D3\u05D9\u05DD \u05E9\u05DC\u05D9\u05E9\u05D9\u05D9\u05DD \u05DC\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(291, "p");
        i0.ɵɵtext(292, "\u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05E9\u05DC\u05DA \u05D1\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF \u05E2\u05E9\u05D5\u05D9 \u05DC\u05D4\u05D9\u05D5\u05EA \u05DB\u05E4\u05D5\u05E3 \u05DC\u05D4\u05D5\u05E8\u05D0\u05D5\u05EA \u05D7\u05D5\u05D6\u05D9\u05D5\u05EA \u05E0\u05D5\u05E1\u05E4\u05D5\u05EA \u05E9\u05DC \u05E6\u05D3\u05D3\u05D9\u05DD \u05E9\u05DC\u05D9\u05E9\u05D9\u05D9\u05DD, \u05D4\u05DE\u05E1\u05D3\u05D9\u05E8\u05D5\u05EA \u05D0\u05EA \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D7\u05E0\u05D5\u05EA \u05D4\u05DE\u05E7\u05D5\u05D5\u05E0\u05EA \u05E9\u05DE\u05DE\u05E0\u05D4 \u05D4\u05D5\u05E8\u05D3\u05EA \u05D0\u05EA \u05D4\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF, \u05DB\u05D2\u05D5\u05DF Google Play. \u05D4\u05D5\u05E8\u05D0\u05D5\u05EA \u05DB\u05D0\u05DC\u05D4 \u05E2\u05E9\u05D5\u05D9\u05D5\u05EA \u05DC\u05D4\u05D9\u05D7\u05E9\u05D1 \u05D2\u05DD \u05DB\u05D7\u05D5\u05D6\u05D4 \u05DC\u05D8\u05D5\u05D1\u05EA \u05D0\u05D5\u05EA\u05DD \u05E6\u05D3\u05D3\u05D9\u05DD \u05E9\u05DC\u05D9\u05E9\u05D9\u05D9\u05DD, \u05D0\u05DD \u05D4\u05D3\u05D1\u05E8 \u05E0\u05E7\u05D1\u05E2 \u05DB\u05DA \u05D1\u05D0\u05D5\u05EA\u05DF \u05D4\u05D5\u05E8\u05D0\u05D5\u05EA \u05D7\u05D5\u05D6\u05D9\u05D5\u05EA \u05E0\u05D5\u05E1\u05E4\u05D5\u05EA \u05E9\u05DC \u05E6\u05D3\u05D3\u05D9\u05DD \u05E9\u05DC\u05D9\u05E9\u05D9\u05D9\u05DD. \u05DE\u05DB\u05DC \u05DE\u05E7\u05D5\u05DD, \u05D0\u05D5\u05EA\u05DD \u05E6\u05D3\u05D3\u05D9\u05DD \u05E9\u05DC\u05D9\u05E9\u05D9\u05D9\u05DD \u05D0\u05D9\u05E0\u05DD \u05D0\u05D7\u05E8\u05D0\u05D9\u05DD \u05DC\u05DE\u05EA\u05DF \u05EA\u05DE\u05D9\u05DB\u05D4 \u05D0\u05D5 \u05EA\u05D7\u05D6\u05D5\u05E7\u05D4 \u05DC\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF. \u05D4\u05D4\u05D5\u05E8\u05D0\u05D5\u05EA \u05E9\u05DC\u05D4\u05DC\u05DF \u05D1\u05E1\u05E2\u05D9\u05E3 \u05D6\u05D4 \u05D7\u05DC\u05D5\u05EA \u05E8\u05E7 \u05D0\u05DD \u05D4\u05D5\u05E8\u05D3\u05EA \u05D0\u05EA \u05D4\u05D0\u05E4\u05DC\u05D9\u05E7\u05E6\u05D9\u05D4 \u05DE\u05D4\u05D0\u05E4-\u05E1\u05D8\u05D5\u05E8 \u05E9\u05DC \u05D0\u05E4\u05DC (Apple App Store), \u05E9\u05D0\u05D6 \u05D0\u05EA\u05D4 \u05D5\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DE\u05E1\u05DB\u05D9\u05DE\u05D9\u05DD \u05DB\u05D9 - ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(293, "ul");
        i0.ɵɵelementStart(294, "li");
        i0.ɵɵtext(295, "\u05D4\u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD \u05E0\u05DB\u05E8\u05EA\u05D9\u05DD \u05D1\u05D9\u05E0\u05DA \u05DC\u05D1\u05D9\u05DF \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DC\u05D0 \u05E2\u05DD \u05D7\u05D1\u05E8\u05EA .Apple Inc (\"\u05D0\u05E4\u05DC\"). \u05D0\u05E4\u05DC \u05D0\u05D9\u05E0\u05D4 \u05D0\u05D7\u05E8\u05D0\u05D9\u05EA \u05DC\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF. \u05D1\u05DE\u05E7\u05E8\u05D4 \u05E9\u05DC \u05E1\u05EA\u05D9\u05E8\u05D4 \u05D0\u05D5 \u05D0\u05D9-\u05D4\u05EA\u05D0\u05DE\u05D4 \u05D1\u05D9\u05DF \u05D4\u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD \u05DC\u05D1\u05D9\u05DF \u05EA\u05E0\u05D0\u05D9 \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E9\u05DC \u05D4\u05D0\u05E4-\u05E1\u05D8\u05D5\u05E8, \u05D4\u05D5\u05E8\u05D0\u05D5\u05EA \u05EA\u05E0\u05D0\u05D9 \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E9\u05DC \u05D4\u05D0\u05E4-\u05E1\u05D8\u05D5\u05E8 \u05D9\u05D2\u05D1\u05E8\u05D5, \u05D0\u05D5\u05DC\u05DD \u05E8\u05E7 \u05D1\u05D9\u05D7\u05E1 \u05DC\u05D4\u05D5\u05E8\u05D0\u05D5\u05EA \u05E9\u05E0\u05D5\u05D2\u05E2\u05D5\u05EA \u05DC\u05D0\u05D9-\u05D4\u05D4\u05EA\u05D0\u05DE\u05D4 \u05D0\u05D5 \u05DC\u05E1\u05EA\u05D9\u05E8\u05D4.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(296, "li");
        i0.ɵɵtext(297, "\u05D4\u05E8\u05D9\u05E9\u05D9\u05D5\u05DF \u05E9\u05DE\u05D5\u05E2\u05E0\u05E7 \u05DC\u05DA \u05D1\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF \u05DE\u05D5\u05D2\u05D1\u05DC \u05DC\u05E8\u05D9\u05E9\u05D9\u05D5\u05DF \u05E9\u05D0\u05D9\u05E0\u05D5 \u05E0\u05D9\u05EA\u05DF \u05DC\u05D4\u05E2\u05D1\u05E8\u05D4, \u05DC\u05E2\u05E9\u05D5\u05EA \u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF \u05D1\u05DB\u05DC \u05DE\u05D5\u05E6\u05E8\u05D9 iOS \u05E9\u05D1\u05D1\u05E2\u05DC\u05D5\u05EA\u05DA \u05D0\u05D5 \u05D1\u05E9\u05DC\u05D9\u05D8\u05EA\u05DA, \u05E2\u05DC-\u05E4\u05D9 \u05EA\u05E0\u05D0\u05D9 \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 (Usage Rules) \u05D4\u05E7\u05D1\u05D5\u05E2\u05D9\u05DD \u05D1\u05EA\u05E0\u05D0\u05D9 \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E9\u05DC \u05D4\u05D0\u05E4-\u05E1\u05D8\u05D5\u05E8. \u05E2\u05DD \u05D6\u05D4, \u05E0\u05D9\u05EA\u05DF \u05DC\u05D2\u05E9\u05EA \u05DC\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF \u05D5\u05DC\u05D4\u05E9\u05EA\u05DE\u05E9 \u05D1\u05D5 \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D7\u05E9\u05D1\u05D5\u05E0\u05D5\u05EA \u05D0\u05D7\u05E8\u05D9\u05DD \u05D4\u05E7\u05E9\u05D5\u05E8\u05D9\u05DD \u05D1\u05DA, \u05D1\u05D3\u05E8\u05DA \u05E9\u05DC Family Sharing.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(298, "li");
        i0.ɵɵtext(299, "\u05D1\u05DE\u05E7\u05E8\u05D4 \u05E9\u05DC \u05D0\u05D9-\u05E2\u05DE\u05D9\u05D3\u05D4 \u05D1\u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05DE\u05D5\u05E6\u05E8 \u05DB\u05DC\u05E9\u05D4\u05D5 (\u05D0\u05DD \u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05DB\u05D6\u05D5 \u05D7\u05DC\u05D4), \u05D0\u05EA\u05D4 \u05E8\u05E9\u05D0\u05D9 \u05DC\u05D4\u05D5\u05D3\u05D9\u05E2 \u05DC\u05D0\u05E4\u05DC \u05D5\u05D0\u05E4\u05DC \u05EA\u05D6\u05DB\u05D4 \u05D0\u05D5\u05EA\u05DA \u05D1\u05DE\u05D7\u05D9\u05E8 \u05D4\u05D5\u05E8\u05D3\u05EA \u05D4\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF (\u05D0\u05DD \u05E9\u05D9\u05DC\u05DE\u05EA \u05E2\u05DC\u05D9\u05D5). \u05D0\u05D9\u05DF \u05DC\u05D0\u05E4\u05DC \u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05DE\u05D5\u05E6\u05E8 \u05D0\u05D7\u05E8\u05EA \u05DE\u05DB\u05DC \u05E1\u05D5\u05D2 \u05E9\u05D4\u05D5\u05D0 \u05D1\u05D9\u05D7\u05E1 \u05DC\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF \u05D5\u05DB\u05DC \u05D4\u05D8\u05E2\u05E0\u05D5\u05EA, \u05D4\u05E4\u05E1\u05D3\u05D9\u05DD, \u05D7\u05D1\u05D5\u05D9\u05D5\u05EA, \u05E0\u05D6\u05E7\u05D9\u05DD, \u05E2\u05DC\u05D5\u05D9\u05D5\u05EA \u05D5\u05D4\u05D5\u05E6\u05D0\u05D5\u05EA \u05D4\u05DE\u05D9\u05D5\u05D7\u05E1\u05D5\u05EA \u05DC\u05D0\u05D9-\u05E2\u05DE\u05D9\u05D3\u05D4 \u05D1\u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05D4\u05DE\u05D5\u05E6\u05E8 (\u05D0\u05DD \u05D7\u05DC\u05D4), \u05DC\u05D0 \u05EA\u05D4\u05D9\u05D4 \u05D1\u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05D0\u05E4\u05DC.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(300, "li");
        i0.ɵɵtext(301, "\u05D0\u05E4\u05DC \u05D0\u05D9\u05E0\u05D4 \u05D0\u05D7\u05E8\u05D0\u05D9\u05EA \u05DC\u05D8\u05E2\u05E0\u05D5\u05EA \u05E9\u05DC\u05DA \u05D0\u05D5 \u05E9\u05DC \u05E6\u05D3 \u05E9\u05DC\u05D9\u05E9\u05D9 \u05DB\u05DC\u05E9\u05D4\u05D5 \u05D1\u05D9\u05D7\u05E1 \u05DC\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF, \u05DC\u05D0\u05D7\u05D6\u05E7\u05EA\u05DA \u05D1\u05D5 \u05D0\u05D5 \u05DC\u05E9\u05D9\u05DE\u05D5\u05E9 \u05E9\u05DC\u05DA \u05D1\u05D5, \u05DC\u05E8\u05D1\u05D5\u05EA: (\u05D0) \u05D8\u05E2\u05E0\u05D5\u05EA \u05D1\u05E0\u05D5\u05D2\u05E2 \u05DC\u05D0\u05D7\u05E8\u05D9\u05D5\u05EA \u05DE\u05D5\u05E6\u05E8; (\u05D1) \u05D8\u05E2\u05E0\u05D5 \u05DC\u05E4\u05D9\u05D4\u05DF \u05D4\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF \u05D0\u05D9\u05E0\u05D5 \u05E2\u05D5\u05DE\u05D3 \u05D1\u05D3\u05E8\u05D9\u05E9\u05D5\u05EA \u05D7\u05D5\u05E7\u05D9\u05D5\u05EA \u05D0\u05D5 \u05E8\u05D2\u05D5\u05DC\u05D8\u05D5\u05E8\u05D9\u05D5\u05EA \u05DB\u05DC\u05E9\u05D4\u05DF; (\u05D2) \u05D8\u05E2\u05E0\u05D5\u05EA \u05D4\u05E0\u05D5\u05D1\u05E2\u05D5\u05EA \u05DE\u05D7\u05E7\u05D9\u05E7\u05EA \u05D4\u05D2\u05E0\u05EA \u05D4\u05E6\u05E8\u05DB\u05DF \u05D0\u05D5 \u05D7\u05E7\u05D9\u05E7\u05D4 \u05D3\u05D5\u05DE\u05D4.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(302, "li");
        i0.ɵɵtext(303, "\u05D1\u05DE\u05E7\u05E8\u05D4 \u05E9\u05DC \u05D8\u05E2\u05E0\u05EA \u05E6\u05D3 \u05E9\u05DC\u05D9\u05E9\u05D9 \u05DC\u05E4\u05D9\u05D4 \u05D4\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF, \u05D0\u05D7\u05D6\u05E7\u05EA\u05DA \u05D1\u05D5 \u05D0\u05D5 \u05E9\u05D9\u05DE\u05D5\u05E9\u05DA \u05D1\u05D5, \u05DE\u05E4\u05E8 \u05D0\u05EA \u05D6\u05DB\u05D5\u05D9\u05D5\u05EA \u05D4\u05E7\u05E0\u05D9\u05D9\u05DF \u05D4\u05E8\u05D5\u05D7\u05E0\u05D9 \u05E9\u05DC \u05E6\u05D3 \u05E9\u05DC\u05D9\u05E9\u05D9, \u05D0\u05E4\u05DC \u05DC\u05D0 \u05EA\u05D4\u05D9\u05D4 \u05D0\u05D7\u05E8\u05D0\u05D9\u05EA \u05DC\u05D7\u05E7\u05D5\u05E8, \u05DC\u05D4\u05D2\u05DF, \u05DC\u05D4\u05EA\u05E4\u05E9\u05E8 \u05D0\u05D5 \u05DC\u05E1\u05DC\u05E7 \u05EA\u05D1\u05D9\u05E2\u05D5\u05EA \u05D4\u05E4\u05E8\u05D4 \u05DB\u05D0\u05DC\u05D4.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(304, "li");
        i0.ɵɵtext(305, "\u05E2\u05DC\u05D9\u05DA \u05DC\u05E6\u05D9\u05D9\u05EA \u05DC\u05DB\u05DC \u05D4\u05E1\u05DB\u05DD \u05EA\u05E0\u05D0\u05D9 \u05E9\u05D9\u05DE\u05D5\u05E9 \u05E9\u05DC \u05E6\u05D3 \u05E9\u05DC\u05D9\u05E9\u05D9 \u05D4\u05D7\u05DC\u05D9\u05DD \u05E2\u05DC\u05D9\u05DA \u05D1\u05E7\u05E9\u05E8 \u05DC\u05E9\u05D9\u05DE\u05D5\u05E9\u05DA \u05D1\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF (\u05DC\u05DE\u05E9\u05DC, \u05D0\u05E1\u05D5\u05E8 \u05DC\u05DA \u05DC\u05D4\u05E4\u05E8 \u05D0\u05EA \u05EA\u05E0\u05D0\u05D9 \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E9\u05DC \u05E1\u05E4\u05E7 \u05D4\u05EA\u05E7\u05E9\u05D5\u05E8\u05EA \u05D4\u05D0\u05DC\u05D7\u05D5\u05D8\u05D9\u05EA \u05E9\u05DC\u05DA \u05D1\u05E2\u05D5\u05D3\u05DA \u05DE\u05E9\u05EA\u05DE\u05E9 \u05D1\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF).");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(306, "li");
        i0.ɵɵtext(307, "\u05D0\u05E4\u05DC \u05D5\u05D4\u05D7\u05D1\u05E8\u05D5\u05EA-\u05D4\u05D1\u05E0\u05D5\u05EA \u05E9\u05DC\u05D4 \u05D4\u05DD \u05DE\u05D5\u05D8\u05D1\u05D9 \u05E6\u05D3-\u05E9\u05DC\u05D9\u05E9\u05D9 \u05E9\u05DC \u05EA\u05E0\u05D0\u05D9\u05DD \u05D0\u05DC\u05D4. \u05D1\u05D4\u05E1\u05DB\u05DE\u05EA\u05DA \u05DC\u05EA\u05E0\u05D0\u05D9\u05DD \u05D0\u05DC\u05D4, \u05DC\u05D0\u05E4\u05DC \u05EA\u05D4\u05D9\u05D4 \u05D4\u05D6\u05DB\u05D5\u05EA (\u05D5\u05EA\u05D7\u05E9\u05D1 \u05DB\u05DE\u05D9 \u05E9\u05E7\u05D9\u05D1\u05DC\u05D4 \u05D0\u05EA \u05D4\u05D6\u05DB\u05D5\u05EA) \u05DC\u05D0\u05DB\u05D5\u05E3 \u05D0\u05EA \u05D4\u05EA\u05E0\u05D0\u05D9\u05DD \u05DB\u05DC\u05E4\u05D9\u05DA, \u05D5\u05D6\u05D0\u05EA \u05D1\u05EA\u05D5\u05E8 \u05DE\u05D5\u05D8\u05D1 \u05E6\u05D3 \u05E9\u05DC\u05D9\u05E9\u05D9 \u05DC\u05EA\u05E0\u05D0\u05D9\u05DD. \u05D0\u05EA\u05D4 \u05DE\u05E6\u05D4\u05D9\u05E8 \u05DB\u05D9: (\u05D0) \u05D0\u05D9\u05E0\u05DA \u05DE\u05DE\u05D5\u05E7\u05DD \u05D1\u05DE\u05D3\u05D9\u05E0\u05D4 \u05D4\u05E0\u05EA\u05D5\u05E0\u05D4 \u05DC\u05D0\u05DE\u05D1\u05E8\u05D2\u05D5 \u05E9\u05DC \u05DE\u05DE\u05E9\u05DC\u05EA \u05D0\u05E8\u05D4\"\u05D1, \u05D0\u05D5 \u05E9\u05E1\u05D5\u05DE\u05E0\u05D4 \u05E2\u05DC-\u05D9\u05D3\u05D9 \u05DE\u05DE\u05E9\u05DC\u05EA \u05D0\u05E8\u05D4\"\u05D1 \u05DB\u05DE\u05D3\u05D9\u05E0\u05D4 \u05EA\u05D5\u05DE\u05DB\u05EA \u05D8\u05E8\u05D5\u05E8; (\u05D1) \u05D0\u05D9\u05E0\u05DA \u05DE\u05D5\u05E4\u05D9\u05E2 \u05D1\u05E8\u05E9\u05D9\u05DE\u05EA \u05D4\u05D2\u05D5\u05E8\u05DE\u05D9\u05DD \u05D4\u05E4\u05E1\u05D5\u05DC\u05D9\u05DD \u05D0\u05D5 \u05DE\u05D5\u05D2\u05D1\u05DC\u05D9\u05DD \u05E9\u05DC \u05DE\u05DE\u05E9\u05DC\u05EA \u05D0\u05E8\u05D4\"\u05D1.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(308, "h2");
        i0.ɵɵtext(309, "\u05E9\u05D9\u05E0\u05D5\u05D9\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05D4\u05E4\u05E1\u05E7\u05EA \u05E4\u05E2\u05D9\u05DC\u05D5\u05EA");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(310, "p");
        i0.ɵɵtext(311, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E8\u05E9\u05D0\u05D9\u05EA \u05DC\u05E9\u05E0\u05D5\u05EA \u05DE\u05E2\u05EA \u05DC\u05E2\u05EA \u05D0\u05EA \u05DE\u05D1\u05E0\u05D4 \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DE\u05E8\u05D0\u05D4\u05D5 \u05D5\u05E2\u05D9\u05E6\u05D5\u05D1\u05D5, \u05D0\u05EA \u05D4\u05D9\u05E7\u05E4\u05DD \u05D5\u05D6\u05DE\u05D9\u05E0\u05D5\u05EA\u05DD \u05E9\u05DC \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D5\u05D4\u05EA\u05DB\u05E0\u05D9\u05DD \u05D1\u05D5 \u05D5\u05EA\u05D4\u05D9\u05D4 \u05E8\u05E9\u05D0\u05D9\u05EA \u05DC\u05E9\u05E0\u05D5\u05EA \u05DB\u05DC \u05D4\u05D9\u05D1\u05D8 \u05D0\u05D7\u05E8 \u05D4\u05DB\u05E8\u05D5\u05DA \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA - \u05D5\u05D4\u05DB\u05D5\u05DC, \u05D1\u05DC\u05D0 \u05E6\u05D5\u05E8\u05DA \u05DC\u05D4\u05D5\u05D3\u05D9\u05E2 \u05DC\u05DA \u05E2\u05DC \u05DB\u05DA \u05DE\u05E8\u05D0\u05E9. \u05E9\u05D9\u05E0\u05D5\u05D9\u05D9\u05DD \u05DB\u05D0\u05DC\u05D4 \u05D9\u05D1\u05D5\u05E6\u05E2\u05D5, \u05D1\u05D9\u05DF \u05D4\u05E9\u05D0\u05E8, \u05D1\u05D4\u05EA\u05D7\u05E9\u05D1 \u05D1\u05D0\u05D5\u05E4\u05D9 \u05D4\u05D3\u05D9\u05E0\u05D0\u05DE\u05D9 \u05E9\u05DC \u05D4\u05D0\u05D9\u05E0\u05D8\u05E8\u05E0\u05D8 \u05D5\u05D1\u05E9\u05D9\u05E0\u05D5\u05D9\u05D9\u05DD \u05D4\u05D8\u05DB\u05E0\u05D5\u05DC\u05D5\u05D2\u05D9\u05D9\u05DD \u05D5\u05D4\u05D0\u05D7\u05E8\u05D9\u05DD \u05D4\u05DE\u05EA\u05E8\u05D7\u05E9\u05D9\u05DD \u05D1\u05D5. \u05DE\u05D8\u05D1\u05E2\u05DD, \u05E9\u05D9\u05E0\u05D5\u05D9\u05D9\u05DD \u05DE\u05E1\u05D5\u05D2 \u05D6\u05D4 \u05E2\u05DC\u05D5\u05DC\u05D9\u05DD \u05DC\u05D4\u05D9\u05D5\u05EA \u05DB\u05E8\u05D5\u05DB\u05D9\u05DD \u05D1\u05EA\u05E7\u05DC\u05D5\u05EA \u05D0\u05D5 \u05DC\u05E2\u05D5\u05E8\u05E8 \u05D1\u05EA\u05D7\u05D9\u05DC\u05D4 \u05D0\u05D9-\u05E0\u05D5\u05D7\u05D5\u05EA \u05D5\u05DB\u05D9\u05D5\"\u05D1. \u05DC\u05D0 \u05EA\u05D4\u05D9\u05D4 \u05DC\u05DA \u05DB\u05DC \u05D8\u05E2\u05E0\u05D4, \u05EA\u05D1\u05D9\u05E2\u05D4 \u05D0\u05D5 \u05D3\u05E8\u05D9\u05E9\u05D4 \u05DB\u05DC\u05E4\u05D9 \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05D2\u05D9\u05DF \u05D1\u05D9\u05E6\u05D5\u05E2 \u05E9\u05D9\u05E0\u05D5\u05D9\u05D9\u05DD \u05DB\u05D0\u05DE\u05D5\u05E8 \u05D0\u05D5 \u05EA\u05E7\u05DC\u05D5\u05EA \u05E9\u05D9\u05EA\u05E8\u05D7\u05E9\u05D5 \u05D0\u05D2\u05D1 \u05D1\u05D9\u05E6\u05D5\u05E2\u05DD.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(312, "p");
        i0.ɵɵtext(313, "\u05DE\u05D1\u05DC\u05D9 \u05DC\u05D2\u05E8\u05D5\u05E2 \u05DE\u05DF \u05D4\u05D0\u05DE\u05D5\u05E8 \u05DC\u05E2\u05D9\u05DC \u05EA\u05D4\u05D9\u05D4 \u05E8\u05E9\u05D0\u05D9\u05EA \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D4\u05E4\u05E1\u05D9\u05E7 \u05D1\u05DB\u05DC \u05E2\u05EA \u05D0\u05EA \u05DE\u05EA\u05DF \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD, \u05DB\u05D5\u05DC\u05DD \u05D0\u05D5 \u05DE\u05E7\u05E6\u05EA\u05DD, \u05D1\u05D0\u05D5\u05E4\u05DF \u05D6\u05DE\u05E0\u05D9 \u05D0\u05D5 \u05E7\u05D1\u05D5\u05E2. \u05D4\u05E0\u05D4\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05E3 \u05EA\u05D4\u05D9\u05D4 \u05E8\u05E9\u05D0\u05D9\u05EA \u05DC\u05D4\u05E4\u05E1\u05D9\u05E7 \u05DE\u05E4\u05E2\u05DD \u05DC\u05E4\u05E2\u05DD \u05D0\u05EA \u05D4\u05E4\u05E2\u05D9\u05DC\u05D5\u05EA \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05E9\u05DD \u05EA\u05D7\u05D6\u05D5\u05E7\u05D4, \u05EA\u05D9\u05E7\u05D5\u05DF, \u05E9\u05D3\u05E8\u05D5\u05D2 \u05D5\u05E9\u05D9\u05E4\u05D5\u05E8 \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05EA\u05E4\u05E2\u05DC \u05DB\u05DE\u05D9\u05D8\u05D1 \u05D9\u05DB\u05D5\u05DC\u05EA\u05D4 \u05E2\u05DC-\u05DE\u05E0\u05EA \u05DC\u05E6\u05DE\u05E6\u05DD \u05D0\u05EA \u05E4\u05E8\u05E7\u05D9 \u05D4\u05D6\u05DE\u05DF \u05E9\u05D1\u05DE\u05D4\u05DC\u05DB\u05DD \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D0 \u05D9\u05D4\u05D9\u05D4 \u05E4\u05E2\u05D9\u05DC. \u05D1\u05DE\u05D9\u05D3\u05EA \u05D4\u05D0\u05E4\u05E9\u05E8, \u05EA\u05E4\u05E8\u05E1\u05DD \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D4\u05D5\u05D3\u05E2\u05D4 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05E2\u05DC \u05D4\u05E4\u05E1\u05E7\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D6\u05DE\u05DF \u05E1\u05D1\u05D9\u05E8 \u05DE\u05E8\u05D0\u05E9. \u05E2\u05DD \u05D4\u05E4\u05E1\u05E7\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05EA\u05D7\u05D6\u05D9\u05E7 \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05EA \u05D4\u05EA\u05D5\u05DB\u05DF \u05D4\u05DB\u05DC\u05D5\u05DC \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05DE\u05E9\u05DA \u05D6\u05DE\u05DF \u05E1\u05D1\u05D9\u05E8 \u05E0\u05D5\u05E1\u05E3 \u05D5\u05DC\u05D0\u05D7\u05E8 \u05DE\u05DB\u05DF \u05EA\u05D4\u05D9\u05D4 \u05E8\u05E9\u05D0\u05D9\u05EA \u05DC\u05DE\u05D7\u05D5\u05E7 \u05D0\u05D5\u05EA\u05D5 \u05D1\u05DC\u05D0 \u05DC\u05E9\u05DE\u05D5\u05E8 \u05DB\u05DC \u05D2\u05D9\u05D1\u05D5\u05D9 \u05DE\u05DE\u05E0\u05D5 \u05D5\u05D1\u05DC\u05D0 \u05DC\u05EA\u05EA \u05D4\u05D5\u05D3\u05E2\u05D4 \u05E0\u05D5\u05E1\u05E4\u05EA \u05E2\u05DC \u05DB\u05DA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(314, "h2", 19);
        i0.ɵɵtext(315, "\u05E9\u05D9\u05E0\u05D5\u05D9 \u05D1\u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(316, "p");
        i0.ɵɵtext(317, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E8\u05E9\u05D0\u05D9\u05EA \u05DC\u05E9\u05E0\u05D5\u05EA \u05DE\u05E2\u05EA \u05DC\u05E2\u05EA \u05D0\u05EA \u05D4\u05D5\u05E8\u05D0\u05D5\u05EA \u05EA\u05E0\u05D0\u05D9 \u05E9\u05D9\u05DE\u05D5\u05E9 \u05D0\u05DC\u05D4 \u05D5\u05DB\u05DC \u05D0\u05D7\u05D3 \u05DE\u05D4\u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD. \u05D1\u05DE\u05D9\u05D3\u05EA \u05D4\u05D0\u05E4\u05E9\u05E8 \u05D5\u05D0\u05DD \u05D9\u05D1\u05D5\u05E6\u05E2\u05D5 \u05D1\u05EA\u05E0\u05D0\u05D9 \u05E9\u05D9\u05DE\u05D5\u05E9 \u05D0\u05DC\u05D5 \u05D0\u05D5 \u05D1\u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD \u05E9\u05D9\u05E0\u05D5\u05D9\u05D9\u05DD \u05DE\u05D4\u05D5\u05EA\u05D9\u05D9\u05DD, \u05EA\u05E4\u05D5\u05E8\u05E1\u05DD \u05E2\u05DC-\u05DB\u05DA \u05D4\u05D5\u05D3\u05E2\u05D4 \u05D1\u05E2\u05DE\u05D5\u05D3 \u05D4\u05D1\u05D9\u05EA \u05E9\u05DC \u05D4\u05D0\u05EA\u05E8 \u05D5/\u05D0\u05D5 \u05D1\u05DE\u05DE\u05E9\u05E7 \u05D4\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF. \u05D4\u05E7\u05D9\u05E9\u05D5\u05E8\u05D9\u05DD \u05DC\u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD \u05D4\u05DE\u05D5\u05E6\u05D1\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D9\u05E7\u05E9\u05E8\u05D5 \u05EA\u05DE\u05D9\u05D3 \u05DC\u05E0\u05D5\u05E1\u05D7 \u05D4\u05E2\u05D3\u05DB\u05E0\u05D9 \u05E9\u05DC \u05D4\u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD. \u05D4\u05DE\u05E9\u05DA \u05E9\u05D9\u05DE\u05D5\u05E9\u05DA \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D0\u05D7\u05E8 \u05E4\u05E8\u05E1\u05D5\u05DD \u05D4\u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD \u05D4\u05DE\u05E2\u05D5\u05D3\u05DB\u05E0\u05D9\u05DD, \u05DE\u05E2\u05D9\u05D3 \u05E2\u05DC \u05D4\u05E1\u05DB\u05DE\u05EA\u05DA \u05DC\u05D4\u05DD. \u05D0\u05DD \u05D0\u05D9\u05E0\u05DA \u05DE\u05E1\u05DB\u05D9\u05DD \u05E2\u05DD \u05D4\u05D4\u05D5\u05E8\u05D0\u05D5\u05EA \u05D4\u05D7\u05D3\u05E9\u05D5\u05EA \u05D1\u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD, \u05E2\u05DC\u05D9\u05D9\u05DA \u05DC\u05D7\u05D3\u05D5\u05DC \u05DE\u05DC\u05E2\u05E9\u05D5\u05EA \u05DB\u05DC \u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(318, "h2", 20);
        i0.ɵɵtext(319, "\u05D3\u05D9\u05DF \u05D5\u05E1\u05DE\u05DB\u05D5\u05EA \u05E9\u05D9\u05E4\u05D5\u05D8");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(320, "p");
        i0.ɵɵtext(321, "\u05E2\u05DC \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D0\u05EA\u05E8, \u05D1\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF \u05D5\u05D1\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D9\u05D7\u05D5\u05DC\u05D5 \u05D0\u05DA \u05D5\u05E8\u05E7 \u05D3\u05D9\u05E0\u05D9 \u05DE\u05D3\u05D9\u05E0\u05EA \u05D9\u05E9\u05E8\u05D0\u05DC. \u05DE\u05E7\u05D5\u05DD \u05D4\u05E9\u05D9\u05E4\u05D5\u05D8 \u05D4\u05D1\u05DC\u05E2\u05D3\u05D9 \u05D1\u05D2\u05D9\u05DF \u05DB\u05DC \u05D3\u05D1\u05E8 \u05D5\u05E2\u05E0\u05D9\u05D9\u05DF \u05D4\u05E0\u05D5\u05D1\u05E2 \u05DE\u05D4\u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD, \u05D4\u05D9\u05E0\u05D5 \u05D1\u05D1\u05EA\u05D9 \u05D4\u05DE\u05E9\u05E4\u05D8 \u05D4\u05DE\u05D5\u05E1\u05DE\u05DB\u05D9\u05DD \u05D1\u05DE\u05D7\u05D5\u05D6 \u05EA\u05DC-\u05D0\u05D1\u05D9\u05D1, \u05D9\u05E9\u05E8\u05D0\u05DC.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(322, "h2", 21);
        i0.ɵɵtext(323, "\u05D4\u05DE\u05D7\u05D0\u05D4");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(324, "p");
        i0.ɵɵtext(325, "\u05D4\u05E2\u05D1\u05D9\u05E8\u05D4 \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05EA \u05D6\u05DB\u05D5\u05D9\u05D5\u05EA\u05D9\u05D4 \u05D5\u05D4\u05EA\u05D7\u05D9\u05D9\u05D1\u05D5\u05D9\u05D5\u05EA\u05D9\u05D4 \u05D1\u05D9\u05D7\u05E1 \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D0\u05D5 \u05DB\u05DC \u05D7\u05DC\u05E7 \u05DE\u05D4\u05DD, \u05DC\u05E6\u05D3 \u05E9\u05DC\u05D9\u05E9\u05D9, \u05EA\u05D4\u05D9\u05D4 \u05D4\u05D9\u05D0 \u05E8\u05E9\u05D0\u05D9\u05EA \u05DC\u05D4\u05DE\u05D7\u05D5\u05EA \u05D2\u05DD \u05D0\u05EA \u05D6\u05DB\u05D5\u05D9\u05D5\u05EA\u05D9\u05D4 \u05D5\u05D4\u05EA\u05D7\u05D9\u05D9\u05D1\u05D5\u05D9\u05D5\u05EA\u05D9\u05D4, \u05DB\u05D5\u05DC\u05DF \u05D0\u05D5 \u05D7\u05DC\u05E7\u05DF, \u05DC\u05E4\u05D9 \u05D4\u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD. \u05D0\u05D9\u05E0\u05DA \u05E8\u05E9\u05D0\u05D9 \u05DC\u05D4\u05DE\u05D7\u05D5\u05EA \u05DB\u05DC \u05D6\u05DB\u05D5\u05EA \u05D0\u05D5 \u05D7\u05D9\u05D5\u05D1 \u05DC\u05E4\u05D9 \u05D4\u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD \u05D5\u05DB\u05DF \u05D0\u05D9\u05E0\u05DA \u05E8\u05E9\u05D0\u05D9 \u05DC\u05E9\u05E2\u05D1\u05D3, \u05DC\u05D4\u05E1\u05D1, \u05DC\u05DE\u05E9\u05DB\u05DF, \u05DC\u05D4\u05E9\u05D0\u05D9\u05DC \u05D0\u05D5 \u05DC\u05EA\u05EA \u05D6\u05DB\u05D5\u05D9\u05D5\u05EA \u05DB\u05DC\u05E9\u05D4\u05DF \u05DC\u05DB\u05DC \u05E6\u05D3 \u05E9\u05DC\u05D9\u05E9\u05D9 \u05D1\u05DB\u05DC \u05D0\u05DC\u05D4. \u05DB\u05DC \u05DE\u05E2\u05E9\u05D4 \u05D0\u05D5 \u05DE\u05D7\u05D3\u05DC \u05D1\u05E0\u05D9\u05D2\u05D5\u05D3 \u05DC\u05D0\u05DE\u05D5\u05E8 \u05DC\u05E2\u05D9\u05DC \u2013 \u05D1\u05D8\u05DC\u05D9\u05DD.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(326, "h2", 22);
        i0.ɵɵtext(327, "\u05E4\u05E0\u05D4 \u05D0\u05DC\u05D9\u05E0\u05D5");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(328, "p");
        i0.ɵɵtext(329, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DE\u05E7\u05E4\u05D9\u05D3\u05D4 \u05E2\u05DC \u05E7\u05D9\u05D5\u05DD \u05D4\u05D5\u05E8\u05D0\u05D5\u05EA \u05D4\u05D7\u05D5\u05E7 \u05D5\u05DE\u05DB\u05D1\u05D3\u05EA \u05D0\u05EA \u05D6\u05DB\u05D5\u05D9\u05D5\u05EA\u05D9\u05D4\u05DD \u05E9\u05DC \u05DE\u05E9\u05EA\u05DE\u05E9\u05D9 \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05DB\u05DC \u05E6\u05D3 \u05E9\u05DC\u05D9\u05E9\u05D9 \u05D0\u05D7\u05E8. \u05D0\u05DD \u05D0\u05EA\u05D4 \u05E1\u05D1\u05D5\u05E8 \u05DB\u05D9 \u05E4\u05D5\u05E8\u05E1\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05EA\u05D5\u05DB\u05DF \u05D4\u05E4\u05D5\u05D2\u05E2 \u05D1\u05DA \u05DE\u05D8\u05E2\u05DD \u05DB\u05DC\u05E9\u05D4\u05D5, \u05D5\u05DB\u05DF \u05D1\u05DE\u05D9\u05D3\u05D4 \u05D5\u05D9\u05E9 \u05DC\u05DA \u05DB\u05DC \u05E9\u05D0\u05DC\u05D4, \u05D4\u05E6\u05E2\u05D4, \u05EA\u05DC\u05D5\u05E0\u05D4 \u05D5\u05E2\u05D5\u05D3, \u05E0\u05D0 \u05E4\u05E0\u05D4 \u05D0\u05DC\u05D9\u05E0\u05D5 \u05DC\u05E4\u05D9 \u05D4\u05E4\u05E8\u05D8\u05D9\u05DD \u05E9\u05DC\u05D4\u05DC\u05DF, \u05D0\u05D5 \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05DE\u05E0\u05D2\u05E0\u05D5\u05E0\u05D9 \u05D9\u05E6\u05D9\u05E8\u05EA \u05D4\u05E7\u05E9\u05E8 \u05D1\u05D2\u05D5\u05E3 \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D5\u05D0\u05E0\u05D5 \u05E0\u05E9\u05EA\u05D3\u05DC \u05DC\u05D8\u05E4\u05DC \u05D1\u05E4\u05E0\u05D9\u05D9\u05EA\u05DA \u05D1\u05DB\u05DC \u05D4\u05D4\u05E7\u05D3\u05DD:");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(330, "p", 23);
        i0.ɵɵelementStart(331, "a");
        i0.ɵɵelementStart(332, "b");
        i0.ɵɵtext(333, "\u05E6\u05D5\u05E8 \u05E7\u05E9\u05E8");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(334, "p");
        i0.ɵɵtext(335, "\u05E2\u05D5\u05D3\u05DB\u05DF \u05DC\u05D0\u05D7\u05E8\u05D5\u05E0\u05D4: 09/07/2018.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
      }
