        i0.ɵɵtext(57, " \u05DE\u05D7\u05D9\u05D9\u05D1 \u05E8\u05D9\u05E9\u05D5\u05DD \u05D0\u05D5 \u05DE\u05E1\u05D9\u05E8\u05EA \u05DE\u05D9\u05D3\u05E2 \u05D0\u05D9\u05E9\u05D9 \u05D5\u05D0\u05D9\u05DF \u05DB\u05DC \u05D7\u05D5\u05D1\u05D4 \u05D7\u05D5\u05E7\u05D9\u05EA \u05DC\u05E2\u05E9\u05D5\u05EA \u05DB\u05DF. \u05E2\u05DD \u05D6\u05D0\u05EA, \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D7\u05DC\u05E7 \u05DE\u05EA\u05DB\u05D5\u05E0\u05D5\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DB\u05D2\u05D5\u05DF \u05D4\u05D5\u05E1\u05E4\u05EA \u05D5\u05E2\u05D3\u05DB\u05D5\u05DF \u05DE\u05D9\u05D3\u05E2 \u05D5\u05EA\u05DB\u05E0\u05D9\u05DD \u05D0\u05D5\u05D3\u05D5\u05EA \u05E1\u05E4\u05E7\u05D9 \u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D5\u05D1\u05EA\u05D9 \u05E2\u05E1\u05E7 \u05D4\u05DE\u05D5\u05E6\u05D2\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DB\u05EA\u05D9\u05D1\u05EA \u05D1\u05D9\u05E7\u05D5\u05E8\u05D5\u05EA \u05D5\u05DB\u05D9\u05D5\"\u05D1, \u05DB\u05DE\u05D5 \u05D2\u05DD \u05E8\u05D9\u05E9\u05D5\u05DD \u05DB\u05D1\u05E2\u05DC \u05E2\u05E1\u05E7, \u05DE\u05D7\u05D9\u05D9\u05D1 \u05E8\u05D9\u05E9\u05D5\u05DD \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05DE\u05E1\u05D9\u05E8\u05EA \u05D4\u05DE\u05D9\u05D3\u05E2 \u05D4\u05DE\u05EA\u05D1\u05E7\u05E9 \u05D1\u05DE\u05D4\u05DC\u05DA \u05D4\u05E8\u05D9\u05E9\u05D5\u05DD. ");
        i0.ɵɵelementStart(58, "strong");
        i0.ɵɵtext(59, "\u05D4\u05E8\u05D9\u05E9\u05D5\u05DD \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA \u05D4\u05D5\u05D0 \u05DE\u05D2\u05D9\u05DC 18 \u05D5\u05DE\u05E2\u05DC\u05D4. \u05E2\u05DC\u05D9\u05DA \u05DC\u05D4\u05D9\u05DE\u05E0\u05E2 \u05DE\u05DE\u05E1\u05D9\u05E8\u05EA \u05E4\u05E8\u05D8\u05D9\u05DD \u05D0\u05D9\u05E9\u05D9\u05D9\u05DD \u05DB\u05DC\u05E9\u05D4\u05DD \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05DD \u05D2\u05D9\u05DC\u05DA \u05E0\u05DE\u05D5\u05DA \u05DE-18");
        i0.ɵɵelementEnd();
        i0.ɵɵtext(60, ". ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(61, "p");
        i0.ɵɵtext(62, "\u05D4\u05E8\u05D9\u05E9\u05D5\u05DD \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA \u05D9\u05D9\u05E2\u05E9\u05D4 \u05DC\u05E8\u05D5\u05D1 \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D7\u05E9\u05D1\u05D5\u05E0\u05DA \u05D1\u05E8\u05E9\u05EA\u05D5\u05EA \u05D7\u05D1\u05E8\u05EA\u05D9\u05D5\u05EA \u05DB\u05D2\u05D5\u05DF \u05E4\u05D9\u05D9\u05E1\u05D1\u05D5\u05E7 \u05D5\u05D2\u05D5\u05D2\u05DC. \u05DB\u05D0\u05E9\u05E8 \u05D0\u05EA\u05D4 \u05E0\u05E8\u05E9\u05DD \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D7\u05E9\u05D1\u05D5\u05DF \u05D4\u05E8\u05E9\u05EA \u05D4\u05D7\u05D1\u05E8\u05EA\u05D9\u05EA \u05E9\u05DC\u05DA, \u05D0\u05EA\u05D4 \u05DE\u05D0\u05E4\u05E9\u05E8 \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D2\u05D9\u05E9\u05D4 \u05DC\u05DE\u05D9\u05D3\u05E2 \u05D4\u05D1\u05E1\u05D9\u05E1\u05D9 \u05D0\u05D5\u05D3\u05D5\u05EA\u05D9\u05D9\u05DA \u05D1\u05E4\u05E8\u05D5\u05E4\u05D9\u05DC \u05D4\u05E8\u05E9\u05EA \u05D4\u05D7\u05D1\u05E8\u05EA\u05D9\u05EA \u05E9\u05DC\u05DA, \u05DB\u05D2\u05D5\u05DF \u05E9\u05DE\u05DA, \u05DB\u05EA\u05D5\u05D1\u05EA \u05D4\u05D3\u05D5\u05D0\"\u05DC \u05E9\u05DC\u05DA \u05D5\u05DB\u05DC \u05DE\u05D9\u05D3\u05E2 \u05D5\u05EA\u05D5\u05DB\u05DF \u05D0\u05D7\u05E8 \u05D4\u05D6\u05DE\u05D9\u05DF \u05DC\u05E2\u05D9\u05D5\u05DF \u05D1\u05D7\u05E9\u05D1\u05D5\u05DF \u05D6\u05D4. \u05D2\u05D9\u05E9\u05EA \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05DE\u05D9\u05D3\u05E2 \u05D5\u05DC\u05EA\u05DB\u05E0\u05D9\u05DD \u05DB\u05D0\u05DE\u05D5\u05E8 \u05DE\u05D5\u05D2\u05D1\u05DC\u05EA \u05DC\u05DE\u05D9\u05D3\u05E2 \u05D5\u05DC\u05EA\u05DB\u05E0\u05D9\u05DD \u05E9\u05DE\u05E4\u05E2\u05D9\u05DC \u05D4\u05E8\u05E9\u05EA \u05D4\u05D7\u05D1\u05E8\u05EA\u05D9\u05EA \u05D1\u05D4 \u05D0\u05EA\u05D4 \u05DE\u05E9\u05EA\u05DE\u05E9 \u05DE\u05D0\u05E4\u05E9\u05E8 \u05DC\u05E0\u05D5 \u05D2\u05D9\u05E9\u05D4 \u05D0\u05DC\u05D9\u05D4\u05DD, ");
        i0.ɵɵelementStart(63, "strong");
        i0.ɵɵtext(64, "\u05D5\u05D6\u05D0\u05EA \u05D1\u05D4\u05EA\u05D0\u05DD \u05DC\u05D4\u05D2\u05D3\u05E8\u05D5\u05EA \u05D4\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA \u05E9\u05DC\u05DA \u05D1\u05D0\u05D5\u05EA\u05D4 \u05E8\u05E9\u05EA \u05D7\u05D1\u05E8\u05EA\u05D9\u05EA");
        i0.ɵɵelementEnd();
        i0.ɵɵtext(65, ", \u05D0\u05D5\u05EA\u05DF \u05D0\u05EA\u05D4 \u05D9\u05DB\u05D5\u05DC \u05DC\u05E9\u05E0\u05D5\u05EA \u05DE\u05E2\u05EA \u05DC\u05E2\u05EA \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05DE\u05DE\u05E9\u05E7 \u05D4\u05E8\u05E9\u05EA \u05D4\u05D7\u05D1\u05E8\u05EA\u05D9\u05EA. \u05EA\u05E9\u05D5\u05DE\u05EA \u05DC\u05D1\u05DA \u05DB\u05D9 \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05E8\u05E9\u05EA\u05D5\u05EA \u05D7\u05D1\u05E8\u05EA\u05D9\u05D5\u05EA \u05D4\u05D5\u05D0 \u05D7\u05D9\u05E6\u05D5\u05E0\u05D9 \u05D5\u05E0\u05E4\u05E8\u05D3 \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05DB\u05E4\u05D5\u05E3 \u05DC\u05E0\u05D5\u05D4\u05D2\u05D9 \u05D4\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA \u05E9\u05DC \u05D0\u05D5\u05EA\u05DF \u05E8\u05E9\u05EA\u05D5\u05EA \u05D7\u05D1\u05E8\u05EA\u05D9\u05D5\u05EA. \u05DE\u05D3\u05D9\u05E0\u05D9\u05D5\u05EA \u05D6\u05D5 \u05D0\u05D9\u05E0\u05D5 \u05D7\u05DC\u05D4 \u05E2\u05DC \u05E4\u05E2\u05D9\u05DC\u05D5\u05EA\u05DF \u05E9\u05DC \u05E8\u05E9\u05EA\u05D5\u05EA \u05D7\u05D1\u05E8\u05EA\u05D9\u05D5\u05EA \u05D0\u05DC\u05D4. ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(66, "p");
        i0.ɵɵtext(67, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E2\u05E9\u05D5\u05D9\u05D4 \u05DC\u05D0\u05E4\u05E9\u05E8 \u05D2\u05DD \u05E8\u05D9\u05E9\u05D5\u05DD \u05D9\u05E9\u05D9\u05E8 \u05E9\u05D0\u05D9\u05E0\u05D5 \u05DE\u05D7\u05D9\u05D9\u05D1 \u05D4\u05D6\u05D3\u05D4\u05D5\u05EA \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D7\u05E9\u05D1\u05D5\u05DF \u05E8\u05E9\u05EA \u05D7\u05D1\u05E8\u05EA\u05D9\u05EA. \u05D1\u05DE\u05D4\u05DC\u05DA \u05E8\u05D9\u05E9\u05D5\u05DD \u05DB\u05D0\u05DE\u05D5\u05E8 \u05EA\u05EA\u05D1\u05E7\u05E9 \u05DC\u05DE\u05E1\u05D5\u05E8 \u05DC\u05E0\u05D5 \u05E4\u05E8\u05D8\u05D9\u05DD \u05D0\u05D9\u05E9\u05D9\u05D9\u05DD \u05D5\u05E4\u05E8\u05D8\u05D9 \u05D9\u05E6\u05D9\u05E8\u05EA \u05E7\u05E9\u05E8, \u05DB\u05D2\u05D5\u05DF \u05E9\u05DD \u05DE\u05DC\u05D0, \u05DB\u05EA\u05D5\u05D1\u05EA \u05D3\u05D5\u05D0\"\u05DC \u05E4\u05E2\u05D9\u05DC\u05D4, \u05D2\u05D9\u05DC \u05D5\u05E2\u05D5\u05D3. \u05DB\u05D0\u05E9\u05E8 \u05D0\u05EA\u05D4 \u05E0\u05E8\u05E9\u05DD \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA \u05DB\u05D1\u05E2\u05DC \u05E2\u05E1\u05E7, \u05D0\u05E0\u05D5 \u05E2\u05E9\u05D5\u05D9\u05D9\u05DD \u05DC\u05D3\u05E8\u05D5\u05E9 \u05DE\u05DE\u05DA \u05DE\u05D9\u05D3\u05E2 \u05E0\u05D5\u05E1\u05E3, \u05D5\u05D1\u05DB\u05DC\u05DC \u05D6\u05D4 \u05D0\u05E1\u05DE\u05DB\u05EA\u05D0\u05D5\u05EA \u05DC\u05D4\u05E0\u05D7\u05EA \u05D3\u05E2\u05EA\u05D4 \u05E9\u05DC \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D4\u05DE\u05E2\u05D9\u05D3\u05D5\u05EA \u05DB\u05D9 \u05D4\u05E0\u05DA \u05D1\u05E2\u05DC\u05D9\u05D5 \u05E9\u05DC \u05D4\u05E2\u05E1\u05E7 \u05D4\u05E8\u05DC\u05D1\u05E0\u05D8\u05D9. \u05D1\u05E2\u05DC \u05E2\u05E1\u05E7 \u05D4\u05DE\u05E2\u05D5\u05E0\u05D9\u05D9\u05DF \u05DC\u05E8\u05DB\u05D5\u05E9 \u05E4\u05E8\u05E1\u05D5\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D9\u05EA\u05D1\u05E7\u05E9 \u05D2\u05DD \u05DC\u05DE\u05E1\u05D5\u05E8 \u05E4\u05E8\u05D8\u05D9 \u05D0\u05DE\u05E6\u05E2\u05D9 \u05EA\u05E9\u05DC\u05D5\u05DD \u05D5\u05DB\u05DC \u05DE\u05D9\u05D3\u05E2 \u05E0\u05D5\u05E1\u05E3 \u05D4\u05D3\u05E8\u05D5\u05E9 \u05DC\u05E9\u05DD \u05E8\u05DB\u05D9\u05E9\u05EA \u05E9\u05D9\u05E8\u05D5\u05EA\u05D9 \u05D4\u05E4\u05E8\u05E1\u05D5\u05DD \u05E9\u05DC \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D9\u05E0\u05D4 \u05E9\u05D5\u05DE\u05E8\u05EA \u05D0\u05EA \u05E4\u05E8\u05D8\u05D9 \u05D0\u05DE\u05E6\u05E2\u05D9 \u05D4\u05EA\u05E9\u05DC\u05D5\u05DD \u05D1\u05DE\u05D0\u05D2\u05E8\u05D9 \u05D4\u05DE\u05D9\u05D3\u05E2 \u05E9\u05D1\u05D1\u05E2\u05DC\u05D5\u05EA\u05D4.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(68, "p");
        i0.ɵɵtext(69, "\u05D1\u05DE\u05D9\u05D3\u05D4 \u05D5\u05EA\u05E8\u05E6\u05D4 \u05DC\u05E4\u05E0\u05D5\u05EA \u05D0\u05DC \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D8\u05D5\u05E4\u05E1 \u05D9\u05E6\u05D9\u05E8\u05EA \u05D4\u05E7\u05E9\u05E8 \u05D4\u05DE\u05E7\u05D5\u05D5\u05DF \u05E9\u05DE\u05E2\u05DE\u05D9\u05D3\u05D4 \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05E8\u05E9\u05D5\u05EA \u05D4\u05DE\u05E9\u05EA\u05DE\u05E9\u05D9\u05DD, \u05EA\u05D9\u05D3\u05E8\u05E9 \u05DC\u05DE\u05E1\u05D5\u05E8 \u05DC\u05E0\u05D5 \u05D0\u05EA \u05E9\u05DE\u05DA, \u05DB\u05EA\u05D5\u05D1\u05EA \u05D4\u05D3\u05D5\u05D0\"\u05DC \u05E9\u05DC\u05DA \u05D5\u05DC\u05D4\u05D6\u05D9\u05DF \u05D0\u05EA \u05EA\u05D5\u05DB\u05DF \u05E4\u05E0\u05D9\u05D9\u05EA\u05DA \u05D1\u05DE\u05E7\u05D5\u05DD \u05D4\u05DE\u05D9\u05D5\u05E2\u05D3 \u05DC\u05DB\u05DA. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E2\u05E9\u05D5\u05D9\u05D4 \u05DC\u05D0\u05E1\u05D5\u05E3 \u05DE\u05D9\u05D3\u05E2 \u05D0\u05D9\u05E9\u05D9 \u05E0\u05D5\u05E1\u05E3 \u05D1\u05E2\u05EA \u05E9\u05EA\u05D9\u05E6\u05D5\u05E8 \u05E2\u05DE\u05D4 \u05E7\u05E9\u05E8 \u05D5\u05D1\u05DE\u05E1\u05D2\u05E8\u05EA \u05DB\u05DC \u05EA\u05DB\u05EA\u05D5\u05D1\u05EA \u05E9\u05EA\u05E0\u05D4\u05DC \u05DE\u05D5\u05DC\u05D4, \u05DC\u05DE\u05E9\u05DC \u05D1\u05E2\u05EA \u05E9\u05EA\u05D2\u05D9\u05E9 \u05D1\u05E7\u05E9\u05D5\u05EA \u05D0\u05D5 \u05E4\u05E0\u05D9\u05D5\u05EA \u05DB\u05DC\u05E9\u05D4\u05DF \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(70, "p");
        i0.ɵɵtext(71, "\u05D4\u05E9\u05D3\u05D5\u05EA \u05E9\u05D7\u05D5\u05D1\u05D4 \u05DC\u05DE\u05DC\u05D0 \u05D9\u05E1\u05D5\u05DE\u05E0\u05D5 \u05D1\u05DE\u05E4\u05D5\u05E8\u05E9. \u05DC\u05DC\u05D0 \u05DE\u05E1\u05D9\u05E8\u05EA \u05D4\u05DE\u05D9\u05D3\u05E2 \u05D4\u05DE\u05D1\u05D5\u05E7\u05E9 \u05DC\u05D0 \u05EA\u05D5\u05DB\u05DC \u05DC\u05D4\u05E9\u05DC\u05D9\u05DD \u05D0\u05EA \u05D4\u05E8\u05D9\u05E9\u05D5\u05DD \u05D0\u05D5 \u05D0\u05EA \u05D4\u05E4\u05E2\u05D5\u05DC\u05D4 \u05D4\u05DE\u05D1\u05D5\u05E7\u05E9\u05EA. \u05E2\u05DC\u05D9\u05DA \u05DC\u05DE\u05E1\u05D5\u05E8 \u05E8\u05E7 \u05E4\u05E8\u05D8\u05D9\u05DD \u05E0\u05DB\u05D5\u05E0\u05D9\u05DD, \u05DE\u05D3\u05D5\u05D9\u05E7\u05D9\u05DD \u05D5\u05DE\u05DC\u05D0\u05D9\u05DD. \u05D0\u05D5\u05DC\u05DD \u05E4\u05E8\u05D8\u05D9\u05DD \u05E9\u05D2\u05D5\u05D9\u05D9\u05DD, \u05D0\u05D5 \u05D0\u05D9 \u05DE\u05E1\u05D9\u05E8\u05EA \u05DE\u05DC\u05D5\u05D0 \u05D4\u05E4\u05E8\u05D8\u05D9\u05DD \u05D4\u05E0\u05D3\u05E8\u05E9\u05D9\u05DD, \u05E2\u05DC\u05D5\u05DC\u05D9\u05DD \u05DC\u05DE\u05E0\u05D5\u05E2 \u05DE\u05DE\u05DA \u05D0\u05EA \u05D4\u05D0\u05E4\u05E9\u05E8\u05D5\u05EA \u05DC\u05D4\u05E9\u05EA\u05DE\u05E9 \u05D1\u05D7\u05DC\u05E7 \u05DE\u05EA\u05DB\u05D5\u05E0\u05D5\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DC\u05E4\u05D2\u05D5\u05E2 \u05D1\u05D0\u05D9\u05DB\u05D5\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D4\u05E0\u05D9\u05EA\u05DF \u05DC\u05DA \u05D5\u05DB\u05DF \u05DC\u05E4\u05D2\u05D5\u05E2 \u05D1\u05D9\u05DB\u05D5\u05DC\u05EA \u05DC\u05D9\u05E6\u05D5\u05E8 \u05D0\u05D9\u05EA\u05DA \u05E7\u05E9\u05E8 \u05D1\u05DE\u05D9\u05D3\u05EA \u05D4\u05E6\u05D5\u05E8\u05DA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(72, "p");
        i0.ɵɵtext(73, "\u05DB\u05D0\u05E9\u05E8 \u05D0\u05EA\u05D4 \u05DE\u05D5\u05E1\u05E8 \u05E4\u05E8\u05D8\u05D9\u05DD \u05D0\u05D9\u05E9\u05D9\u05D9\u05DD \u05E9\u05DC \u05E6\u05D3 \u05E9\u05DC\u05D9\u05E9\u05D9, \u05D0\u05EA\u05D4 \u05DE\u05E6\u05D4\u05D9\u05E8 \u05D5\u05DE\u05D0\u05E9\u05E8 \u05DB\u05D9 \u05E7\u05D9\u05D1\u05DC\u05EA \u05D0\u05EA \u05D4\u05E1\u05DB\u05DE\u05EA\u05D5 \u05D4\u05DE\u05E4\u05D5\u05E8\u05E9\u05EA \u05D5\u05DE\u05D3\u05E2\u05EA \u05DC\u05DE\u05E1\u05D9\u05E8\u05EA \u05E4\u05E8\u05D8\u05D9\u05D5 \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DC\u05E9\u05DD \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05E4\u05E8\u05D8\u05D9\u05DD \u05D0\u05DC\u05D4 \u05D1\u05D4\u05EA\u05D0\u05DD \u05DC\u05DE\u05D3\u05D9\u05E0\u05D9\u05D5\u05EA \u05D6\u05D5.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(74, "h2");
        i0.ɵɵtext(75, "\u05D0\u05D9\u05E1\u05D5\u05E3 \u05E0\u05EA\u05D5\u05E0\u05D9 \u05DE\u05D9\u05E7\u05D5\u05DD");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(76, "p");
        i0.ɵɵtext(77, "\u05D7\u05DC\u05E7 \u05DE\u05EA\u05DB\u05D5\u05E0\u05D5\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D4\u05D6\u05DE\u05D9\u05E0\u05D5\u05EA \u05DC\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D4\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF, \u05D4\u05DF \"\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05DE\u05D1\u05D5\u05E1\u05E1\u05D9 \u05DE\u05D9\u05E7\u05D5\u05DD\" \u05D4\u05E0\u05E2\u05D6\u05E8\u05D9\u05DD \u05D1\u05D8\u05DB\u05E0\u05D5\u05DC\u05D5\u05D2\u05D9\u05D9\u05EAGPS \u05E2\u05DC-\u05DE\u05E0\u05EA \u05DC\u05E7\u05D1\u05D5\u05E2 \u05D0\u05EA \u05DE\u05D9\u05E7\u05D5\u05DE\u05DA \u05D1\u05E2\u05EA \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D9\u05D9\u05E9\u05D5\u05DE\u05DF \u05D5\u05D2\u05DD \u05DB\u05D0\u05E9\u05E8 \u05D0\u05D9\u05E0\u05DA \u05DE\u05E9\u05EA\u05DE\u05E9 \u05D1\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF, \u05D5\u05DC\u05E1\u05E4\u05E7 \u05DC\u05DA \u05EA\u05D5\u05E6\u05D0\u05D5\u05EA \u05D7\u05D9\u05E4\u05D5\u05E9 \u05D5\u05DB\u05DF \u05DC\u05D4\u05E6\u05D9\u05D2 \u05DC\u05DA \u05D1\u05D0\u05D5\u05E4\u05DF \u05D9\u05D6\u05D5\u05DD \u05D4\u05DE\u05DC\u05E6\u05D5\u05EA \u05D4\u05EA\u05D5\u05D0\u05DE\u05D5\u05EA \u05DB\u05DB\u05DC \u05D4\u05E0\u05D9\u05EA\u05DF \u05DC\u05DE\u05D9\u05E7\u05D5\u05DE\u05DA \u05D5\u05DC\u05D0\u05D6\u05D5\u05E8\u05D9\u05DD \u05D1\u05D4\u05DD \u05D0\u05EA\u05D4 \u05DE\u05E8\u05D1\u05D4 \u05DC\u05E9\u05D4\u05D5\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(78, "p");
        i0.ɵɵtext(79, "\u05DC\u05D0 \u05E0\u05D9\u05EA\u05DF \u05DC\u05E1\u05E4\u05E7 \u05D0\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D4\u05DC\u05DC\u05D5 \u05DC\u05DC\u05D0 \u05D4\u05E1\u05DB\u05DE\u05EA\u05DA \u05DC\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D8\u05DB\u05E0\u05D5\u05DC\u05D5\u05D2\u05D9\u05D4 \u05D6\u05D5 \u05D5\u05E2\u05DC-\u05DB\u05DF \u05D1\u05E2\u05E6\u05DD \u05D4\u05EA\u05E7\u05E0\u05EA \u05D4\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF \u05D5\u05D4\u05E1\u05DB\u05DE\u05EA\u05DA \u05DC\u05DE\u05D3\u05D9\u05E0\u05D9\u05D5\u05EA \u05D6\u05D5 \u05D0\u05DC\u05D4 \u05D0\u05EA\u05D4 \u05DE\u05E1\u05DB\u05D9\u05DD \u05DC\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D6\u05D4. \u05DB\u05DE\u05D5\u05D1\u05DF \u05E9\u05D0\u05D9\u05E0\u05DA \u05DE\u05D7\u05D5\u05D9\u05D1 \u05DC\u05E1\u05E4\u05E7 \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DE\u05D9\u05D3\u05E2 \u05D6\u05D4 \u05D0\u05D5\u05D3\u05D5\u05EA\u05D9\u05DA \u05D5\u05DC\u05E4\u05D9\u05DB\u05DA \u05EA\u05D5\u05DB\u05DC \u05D1\u05DB\u05DC \u05E2\u05EA \u05DC\u05D1\u05D8\u05DC \u05D0\u05EA \u05D4\u05D0\u05E4\u05E9\u05E8\u05D5\u05EA \u05DC\u05D6\u05D4\u05D5\u05EA \u05D0\u05EA \u05DE\u05D9\u05E7\u05D5\u05DE\u05DA \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D4\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF, \u05E2\u05DC-\u05D9\u05D3\u05D9 \u05D1\u05D9\u05D8\u05D5\u05DC \u05EA\u05DB\u05D5\u05E0\u05D4 \u05D6\u05D5 \u05D1\u05D4\u05D2\u05D3\u05E8\u05D5\u05EA \u05DE\u05DB\u05E9\u05D9\u05E8 \u05D4\u05E7\u05E6\u05D4 \u05E9\u05D1\u05E8\u05E9\u05D5\u05EA\u05DA (\u05D0\u05D5 \u05E2\u05DC-\u05D9\u05D3\u05D9 \u05D4\u05E1\u05E8\u05EA \u05D4\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF \u05DE\u05DE\u05DB\u05E9\u05D9\u05E8 \u05D4\u05E7\u05E6\u05D4 \u05E9\u05D1\u05E8\u05E9\u05D5\u05EA\u05DA). \u05E2\u05DC-\u05DE\u05E0\u05EA \u05DC\u05E6\u05E4\u05D5\u05EA \u05D1\u05EA\u05D5\u05E6\u05D0\u05D5\u05EA \u05D7\u05D9\u05E4\u05D5\u05E9 \u05D4\u05EA\u05D5\u05D0\u05DE\u05D5\u05EA \u05D0\u05EA \u05DE\u05D9\u05E7\u05D5\u05DE\u05DA \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D4\u05D0\u05EA\u05E8, \u05D9\u05D4\u05D9\u05D4 \u05E2\u05DC\u05D9\u05DA \u05DC\u05D4\u05E7\u05DC\u05D9\u05D3 \u05D0\u05EA \u05DB\u05EA\u05D5\u05D1\u05EA\u05DA \u05D1\u05DE\u05E7\u05D5\u05DD \u05D4\u05DE\u05D9\u05D5\u05E2\u05D3 \u05DC\u05DB\u05DA \u05D1\u05D0\u05EA\u05E8, \u05D0\u05DD \u05EA\u05D1\u05D7\u05E8 \u05DC\u05E2\u05E9\u05D5\u05EA \u05DB\u05DF, \u05DC\u05E4\u05D9 \u05E9\u05D9\u05E7\u05D5\u05DC \u05D3\u05E2\u05EA\u05DA \u05D4\u05D1\u05DC\u05E2\u05D3\u05D9. \u05D1\u05DE\u05E7\u05E8\u05D4 \u05D6\u05D4 \u05EA\u05E9\u05DE\u05D5\u05E8 \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05EA \u05DE\u05D9\u05D3\u05E2 \u05D6\u05D4.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(80, "h2");
        i0.ɵɵtext(81, "\u05DE\u05D9\u05D3\u05E2 \u05E9\u05E0\u05D0\u05E1\u05E3 \u05D1\u05DE\u05D4\u05DC\u05DA \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(82, "p");
        i0.ɵɵtext(83, "\u05D1\u05E2\u05EA \u05E9\u05D0\u05EA\u05D4 \u05DE\u05E9\u05EA\u05DE\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05EA\u05D0\u05E1\u05D5\u05E3 \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DE\u05D9\u05D3\u05E2 \u05E2\u05DC \u05E0\u05D5\u05D4\u05D2\u05D9\u05DA \u05D1\u05D9\u05D7\u05E1 \u05DC\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DC\u05E8\u05D1\u05D5\u05EA \u05EA\u05D5\u05DB\u05DF \u05E9\u05D9\u05E6\u05E8\u05EA \u05D0\u05D5 \u05E4\u05E8\u05E1\u05DE\u05EA \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DE\u05D9\u05D3\u05E2 \u05D0\u05D5 \u05E4\u05E8\u05E1\u05D5\u05DE\u05D5\u05EA \u05E9\u05E7\u05E8\u05D0\u05EA, \u05D4\u05E2\u05DE\u05D5\u05D3\u05D9\u05DD \u05E9\u05D1\u05D4\u05DD \u05E6\u05E4\u05D9\u05EA, \u05E9\u05D0\u05D9\u05DC\u05EA\u05D5\u05EA \u05D7\u05D9\u05E4\u05D5\u05E9 \u05E9\u05D1\u05D9\u05E6\u05E2\u05EA, \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05E9\u05E2\u05E0\u05D9\u05D9\u05E0\u05D5 \u05D0\u05D5\u05EA\u05DA \u05D5\u05EA\u05DB\u05D9\u05E4\u05D5\u05EA \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D4\u05DD. \u05DB\u05DE\u05D5 \u05DB\u05DF, \u05E0\u05D0\u05E1\u05E3 \u05DE\u05D9\u05D3\u05E2 \u05D4\u05E0\u05D5\u05D2\u05E2 \u05DC\u05DE\u05E7\u05D5\u05DD \u05D4\u05DE\u05D7\u05E9\u05D1 \u05D0\u05D5 \u05DE\u05DB\u05E9\u05D9\u05E8 \u05D4\u05E7\u05E6\u05D4 \u05E9\u05DC\u05DA \u05D5\u05E0\u05EA\u05D5\u05E0\u05D9 \u05DB\u05EA\u05D5\u05D1\u05EA \u05D4\u05D0\u05D9\u05E0\u05D8\u05E8\u05E0\u05D8 (IP address) \u05E9\u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA\u05DD \u05E0\u05D9\u05D2\u05E9\u05EA \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA, \u05E1\u05D5\u05D2 \u05DE\u05E2\u05E8\u05DB\u05EA \u05D4\u05D4\u05E4\u05E2\u05DC\u05D4 \u05D4\u05DE\u05E9\u05DE\u05E9\u05EA \u05D0\u05D5\u05EA\u05DA, \u05E1\u05D5\u05D2 \u05D4\u05DE\u05DB\u05E9\u05D9\u05E8 \u05D4\u05E7\u05E6\u05D4 \u05E9\u05D1\u05E8\u05E9\u05D5\u05EA\u05DA \u05D5\u05DE\u05D9\u05D3\u05E2 \u05D0\u05D5\u05D3\u05D5\u05EA\u05D9\u05D5, \u05D5\u05E2\u05D5\u05D3 (\u05DC\u05D4\u05DC\u05DF: \"");
        i0.ɵɵelementStart(84, "strong");
        i0.ɵɵtext(85, "\u05D4\u05E8\u05D2\u05DC\u05D9 \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9");
        i0.ɵɵelementEnd();
        i0.ɵɵtext(86, "\"). \u05D1\u05E0\u05D5\u05E1\u05E3, \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E8\u05E9\u05D0\u05D9\u05EA \u05DC\u05D0\u05E1\u05D5\u05E3 \u05D5\u05DC\u05D4\u05E1\u05EA\u05D9\u05D9\u05E2 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05D4\u05DD \u05E9\u05DC \u05E6\u05D3\u05D3\u05D9\u05DD \u05E9\u05DC\u05D9\u05E9\u05D9\u05D9\u05DD \u05DB\u05D3\u05D9 \u05DC\u05D0\u05E1\u05D5\u05E3 \u05D5\u05DC\u05E0\u05EA\u05D7 \u05DE\u05D9\u05D3\u05E2 \u05D0\u05E0\u05D5\u05E0\u05D9\u05DE\u05D9, \u05E1\u05D8\u05D8\u05D9\u05E1\u05D8\u05D9, \u05D0\u05D5 \u05DE\u05E6\u05E8\u05E4\u05D9 (\u05D0\u05D2\u05E8\u05D2\u05D8\u05D9\u05D1\u05D9) \u05D1\u05E7\u05E9\u05E8 \u05E2\u05DD \u05D4\u05E8\u05D2\u05DC\u05D9 \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05DC\u05E8\u05D1\u05D5\u05EA \u05DE\u05D9\u05D3\u05E2 \u05D4\u05E0\u05D5\u05D2\u05E2 \u05DC\u05E4\u05E2\u05D9\u05DC\u05D5\u05EA\u05DA \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA. \u05DE\u05D9\u05D3\u05E2 \u05D6\u05D4 \u05E0\u05D0\u05E1\u05E3 \u05DC\u05E8\u05D5\u05D1 \u05D1\u05D0\u05D5\u05E4\u05DF \u05D0\u05D5\u05D8\u05D5\u05DE\u05D8\u05D9 \u05D5\u05DE\u05D0\u05D5\u05D7\u05E1\u05DF \u05D1\u05E7\u05D1\u05E6\u05D9 '\u05DC\u05D5\u05D2' \u05E9\u05DC \u05E9\u05E8\u05EA\u05D9 \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(87, "h2");
        i0.ɵɵtext(88, "\u05DE\u05D9\u05D3\u05E2 \u05DE\u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E9\u05D0\u05EA\u05D4 \u05DE\u05E9\u05EA\u05E3 \u05D1\u05E8\u05E9\u05EA\u05D5\u05EA \u05D7\u05D1\u05E8\u05EA\u05D9\u05D5\u05EA");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(89, "p");
        i0.ɵɵtext(90, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DE\u05E2\u05DE\u05D9\u05D3\u05D4 \u05DC\u05E8\u05E9\u05D5\u05EA\u05DA \u05DB\u05DC\u05D9\u05DD \u05DC\u05E9\u05D9\u05EA\u05D5\u05E3 \u05D5\u05E7\u05D9\u05E9\u05D5\u05E8 \u05EA\u05DB\u05E0\u05D9\u05DD \u05DE\u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05E8\u05E9\u05EA\u05D5\u05EA \u05D7\u05D1\u05E8\u05EA\u05D9\u05D5\u05EA \u05D5\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05DE\u05E7\u05D5\u05D5\u05E0\u05D9\u05DD \u05E9\u05DC \u05E6\u05D3\u05D3\u05D9\u05DD \u05E9\u05DC\u05D9\u05E9\u05D9\u05D9\u05DD, \u05DB\u05D2\u05D5\u05DF \u05E4\u05D9\u05D9\u05E1\u05D1\u05D5\u05E7. \u05DB\u05DE\u05E4\u05D5\u05E8\u05D8 \u05DC\u05E2\u05D9\u05DC, \u05EA\u05E9\u05D5\u05DE\u05EA \u05DC\u05D1\u05DA \u05DB\u05D9 \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05E8\u05E9\u05EA\u05D5\u05EA \u05D7\u05D1\u05E8\u05EA\u05D9\u05D5\u05EA \u05DB\u05D0\u05DE\u05D5\u05E8 \u05D4\u05D5\u05D0 \u05D7\u05D9\u05E6\u05D5\u05E0\u05D9 \u05D5\u05E0\u05E4\u05E8\u05D3 \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05DB\u05E4\u05D5\u05E3 \u05DC\u05E0\u05D5\u05D4\u05D2\u05D9 \u05D4\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA \u05E9\u05DC \u05D0\u05D5\u05EA\u05DF \u05E8\u05E9\u05EA\u05D5\u05EA \u05D7\u05D1\u05E8\u05EA\u05D9\u05D5\u05EA. \u05DE\u05D3\u05D9\u05E0\u05D9\u05D5\u05EA \u05D6\u05D5 \u05D0\u05D9\u05E0\u05D5 \u05D7\u05DC\u05D4 \u05E2\u05DC \u05E4\u05E2\u05D9\u05DC\u05D5\u05EA\u05DF \u05E9\u05DC \u05E8\u05E9\u05EA\u05D5\u05EA \u05D7\u05D1\u05E8\u05EA\u05D9\u05D5\u05EA \u05D0\u05DC\u05D4.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(91, "p");
        i0.ɵɵtext(92, "\u05DB\u05D0\u05DE\u05D5\u05E8, \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DB\u05D5\u05DC\u05DC \u05D2\u05DD \u05DE\u05D9\u05D3\u05E2 \u05D5\u05EA\u05DB\u05E0\u05D9\u05DD \u05D4\u05E0\u05DE\u05E1\u05E8\u05D9\u05DD \u05DC\u05E4\u05E8\u05E1\u05D5\u05DD \u05DE\u05D8\u05E2\u05DD \u05DE\u05E9\u05EA\u05DE\u05E9\u05D9 \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA. \u05DE\u05D9\u05D3\u05E2 \u05D5\u05EA\u05DB\u05E0\u05D9\u05DD \u05D0\u05DC\u05D4 \u05E2\u05E9\u05D5\u05D9\u05D9\u05DD \u05DC\u05DB\u05DC\u05D5\u05DC \u05D2\u05DD \u05DE\u05D9\u05D3\u05E2 \u05D0\u05D9\u05E9\u05D9. \u05D3\u05E2 \u05DB\u05D9 \u05D4\u05EA\u05DB\u05E0\u05D9\u05DD \u05D5\u05D4\u05DE\u05D9\u05D3\u05E2 \u05E9\u05D0\u05EA\u05D4 \u05D8\u05D5\u05E2\u05DF \u05DC\u05D0\u05D6\u05D5\u05E8\u05D9\u05DD \u05D4\u05E4\u05D5\u05DE\u05D1\u05D9\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D9\u05E0\u05DD \u05E4\u05E8\u05D8\u05D9\u05D9\u05DD \u05D0\u05D5 \u05D7\u05E1\u05D5\u05D9\u05D9\u05DD \u05D5\u05DB\u05D9 \u05DC\u05D0 \u05E6\u05E8\u05D9\u05DB\u05D4 \u05DC\u05D4\u05D9\u05D5\u05EA \u05DC\u05DA \u05DB\u05DC \u05E6\u05D9\u05E4\u05D9\u05D9\u05D4 \u05DC\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA \u05D1\u05E0\u05D5\u05D2\u05E2 \u05D0\u05DC\u05D9\u05D4\u05DD. ");
        i0.ɵɵelementStart(93, "strong");
        i0.ɵɵtext(94, "\u05E2\u05DC\u05D9\u05DA \u05DC\u05E0\u05E7\u05D5\u05D8 \u05DE\u05E9\u05E0\u05D4 \u05D6\u05D4\u05D9\u05E8\u05D5\u05EA \u05D5\u05DC\u05D4\u05E4\u05E2\u05D9\u05DC \u05E9\u05D9\u05E7\u05D5\u05DC \u05D3\u05E2\u05EA \u05D1\u05E2\u05EA \u05E9\u05D4\u05E0\u05DA \u05DE\u05D5\u05E1\u05E8 \u05DE\u05D9\u05D3\u05E2 \u05E9\u05E2\u05DC\u05D5\u05DC \u05DC\u05D6\u05D4\u05D5\u05EA \u05D0\u05D5\u05EA\u05DA \u05D0\u05D5 \u05D0\u05D7\u05E8\u05D9\u05DD \u05D5\u05D1\u05DE\u05D9\u05D3\u05EA \u05D4\u05E6\u05D5\u05E8\u05DA \u05DC\u05D4\u05D9\u05DE\u05E0\u05E2 \u05DE\u05DB\u05DA");
        i0.ɵɵelementEnd();
        i0.ɵɵtext(95, ". ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(96, "h2");
        i0.ɵɵtext(97, "\u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05DE\u05D9\u05D3\u05E2");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(98, "p");
        i0.ɵɵtext(99, "\u05D9\u05D3\u05D5\u05E2 \u05DC\u05DA, \u05D5\u05D0\u05EA\u05D4 \u05DE\u05E1\u05DB\u05D9\u05DD, \u05DB\u05D9 \u05E0\u05D5\u05DB\u05DC \u05DC\u05E2\u05E9\u05D5\u05EA \u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05DE\u05D9\u05D3\u05E2 \u05D4\u05D0\u05D9\u05E9\u05D9 \u05D5\u05D1\u05DE\u05D9\u05D3\u05E2 \u05E2\u05DC \u05D4\u05E8\u05D2\u05DC\u05D9 \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05E9\u05DC\u05DA \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05D4\u05EA\u05D0\u05DD \u05DC\u05DE\u05D3\u05D9\u05E0\u05D9\u05D5\u05EA \u05D6\u05D5, \u05D1\u05D9\u05DF \u05D4\u05D9\u05EA\u05E8, \u05DC\u05E6\u05E8\u05DB\u05D9\u05DD \u05D4\u05D1\u05D0\u05D9\u05DD:");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(100, "ul");
        i0.ɵɵelementStart(101, "li");
        i0.ɵɵtext(102, "\u05DB\u05D3\u05D9 \u05DC\u05D0\u05E4\u05E9\u05E8 \u05DC\u05DA \u05DC\u05D4\u05E9\u05EA\u05DE\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05D1\u05EA\u05DB\u05E0\u05D9\u05DD \u05D4\u05DB\u05DC\u05D5\u05DC\u05D9\u05DD \u05D1\u05D5;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(103, "li");
        i0.ɵɵtext(104, "\u05DB\u05D3\u05D9 \u05DC\u05D6\u05D4\u05D5\u05EA \u05D0\u05D5\u05EA\u05DA \u05D1\u05DE\u05D4\u05DC\u05DA \u05DB\u05E0\u05D9\u05E1\u05D5\u05EA \u05D7\u05D5\u05D6\u05E8\u05D5\u05EA \u05E9\u05DC\u05DA \u05DC\u05D0\u05D6\u05D5\u05E8\u05D9\u05DD \u05D4\u05D8\u05E2\u05D5\u05E0\u05D9\u05DD \u05D4\u05E8\u05E9\u05DE\u05D4 \u05D5\u05DB\u05D3\u05D9 \u05DC\u05D7\u05E1\u05D5\u05DA \u05DE\u05DE\u05DA \u05DC\u05D4\u05D6\u05D9\u05DF \u05D1\u05DB\u05DC \u05E4\u05E2\u05DD \u05D0\u05EA \u05E4\u05E8\u05D8\u05D9\u05DA;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(105, "li");
        i0.ɵɵtext(106, "\u05DB\u05D3\u05D9 \u05DC\u05E9\u05E4\u05E8 \u05D5\u05DC\u05D4\u05E2\u05E9\u05D9\u05E8 \u05D0\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D5\u05D4\u05EA\u05DB\u05E0\u05D9\u05DD \u05D4\u05DE\u05D5\u05E6\u05E2\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05D1\u05DB\u05DC\u05DC \u05D6\u05D4 \u05DC\u05D9\u05E6\u05D5\u05E8 \u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D5\u05EA\u05DB\u05E0\u05D9\u05DD \u05D7\u05D3\u05E9\u05D9\u05DD \u05D4\u05DE\u05EA\u05D0\u05D9\u05DE\u05D9\u05DD \u05DC\u05D3\u05E8\u05D9\u05E9\u05D5\u05EA \u05D4\u05DE\u05E9\u05EA\u05DE\u05E9\u05D9\u05DD \u05D5\u05E6\u05D9\u05E4\u05D9\u05D5\u05EA\u05D9\u05D4\u05DD \u05D5\u05DB\u05DF \u05DC\u05E9\u05E0\u05D5\u05EA \u05D0\u05D5 \u05DC\u05D1\u05D8\u05DC \u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D5\u05EA\u05DB\u05E0\u05D9\u05DD \u05E7\u05D9\u05D9\u05DE\u05D9\u05DD. \u05D4\u05DE\u05D9\u05D3\u05E2 \u05E9\u05D9\u05E9\u05DE\u05E9 \u05D0\u05EA \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05E6\u05D5\u05E8\u05DA \u05DB\u05DA \u05D9\u05D4\u05D9\u05D4 \u05D1\u05E2\u05D9\u05E7\u05E8\u05D5 \u05DE\u05D9\u05D3\u05E2 \u05E1\u05D8\u05D8\u05D9\u05E1\u05D8\u05D9, \u05E9\u05D0\u05D9\u05E0\u05D5 \u05DE\u05D6\u05D4\u05D4 \u05D0\u05D5\u05EA\u05DA \u05D0\u05D9\u05E9\u05D9\u05EA;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(107, "li");
        i0.ɵɵtext(108, "\u05DB\u05D3\u05D9 \u05DC\u05D0\u05E4\u05E9\u05E8 \u05DC\u05DA \u05DC\u05D4\u05EA\u05D0\u05D9\u05DD \u05D0\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D4\u05E2\u05D3\u05E4\u05D5\u05EA\u05D9\u05DA;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(109, "li");
        i0.ɵɵtext(110, "\u05DB\u05D3\u05D9 \u05DC\u05E9\u05DC\u05D5\u05D7 \u05D0\u05DC\u05D9\u05DA \u05DE\u05E2\u05EA \u05DC\u05E2\u05EA \u05DE\u05D9\u05D3\u05E2 \u05D1\u05D3\u05D1\u05E8 \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05E1\u05E7\u05E8\u05D9\u05DD, \u05E2\u05DC\u05D5\u05E0\u05D9 \u05DE\u05D9\u05D3\u05E2, \u05DB\u05DE\u05D5 \u05D2\u05DD \u05DE\u05D9\u05D3\u05E2 \u05E4\u05E8\u05E1\u05D5\u05DE\u05D9 \u05D5\u05DE\u05D9\u05D3\u05E2 \u05D1\u05E7\u05E9\u05E8 \u05E2\u05DD \u05DE\u05D5\u05E6\u05E8\u05D9\u05DD \u05D5\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05E9\u05DC \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05E9\u05DC \u05E6\u05D3\u05D3\u05D9\u05DD \u05E9\u05DC\u05D9\u05E9\u05D9\u05D9\u05DD \u05DB\u05D2\u05D5\u05DF \u05DE\u05E4\u05E8\u05E1\u05DE\u05D9\u05DD. \u05DE\u05D9\u05D3\u05E2 \u05DB\u05D6\u05D4 \u05D9\u05E9\u05D5\u05D2\u05E8 \u05D0\u05DC\u05D9\u05DA, \u05DB\u05DB\u05DC \u05E9\u05D4\u05E1\u05DB\u05DE\u05EA \u05DC\u05DB\u05DA \u05DE\u05E4\u05D5\u05E8\u05E9\u05D5\u05EA \u05D1\u05D4\u05EA\u05D0\u05DD \u05DC\u05D4\u05D5\u05E8\u05D0\u05D5\u05EA \u05E1\u05E2\u05D9\u05E3 30\u05D0 \u05DC\u05D7\u05D5\u05E7 \u05D4\u05EA\u05E7\u05E9\u05D5\u05E8\u05EA (\u05D1\u05D6\u05E7 \u05D5\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD), \u05D4\u05EA\u05E9\u05DE\"\u05D1-1982, \u05DB\u05DE\u05E4\u05D5\u05E8\u05D8 \u05D1\u05D4\u05E8\u05D7\u05D1\u05D4 ");
        i0.ɵɵelementStart(111, "a", 2);
        i0.ɵɵtext(112, "\u05D1\u05EA\u05E0\u05D0\u05D9 \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9");
        i0.ɵɵelementEnd();
        i0.ɵɵtext(113, " \u05E9\u05DC \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA . \u05EA\u05D5\u05DB\u05DC \u05DC\u05D1\u05D8\u05DC \u05D0\u05EA \u05D4\u05E1\u05DB\u05DE\u05EA\u05DA \u05D1\u05DB\u05DC \u05E2\u05EA \u05D5\u05DC\u05D7\u05D3\u05D5\u05DC \u05DE\u05E7\u05D1\u05DC\u05EA\u05DD \u05E9\u05DC \u05D3\u05D1\u05E8\u05D9 \u05E4\u05E8\u05E1\u05D5\u05DE\u05EA \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05DC\u05D7\u05E6\u05DF \"\u05D4\u05E1\u05E8\";");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(114, "li");
        i0.ɵɵtext(115, "\u05DC\u05D9\u05E6\u05D9\u05E8\u05EA \u05E7\u05E9\u05E8 \u05D0\u05D9\u05EA\u05DA \u05DB\u05E9\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E1\u05D1\u05D5\u05E8\u05D4 \u05E9\u05E7\u05D9\u05D9\u05DD \u05E6\u05D5\u05E8\u05DA \u05D1\u05DB\u05DA;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(116, "li");
        i0.ɵɵtext(117, "\u05DC\u05E6\u05D5\u05E8\u05DA \u05E0\u05D9\u05EA\u05D5\u05D7, \u05D1\u05E7\u05E8\u05D4 \u05D5\u05DE\u05E1\u05D9\u05E8\u05EA \u05DE\u05D9\u05D3\u05E2 \u05E1\u05D8\u05D8\u05D9\u05E1\u05D8\u05D9 \u05DC\u05E6\u05D3\u05D3\u05D9\u05DD \u05E9\u05DC\u05D9\u05E9\u05D9\u05D9\u05DD. \u05DE\u05D9\u05D3\u05E2 \u05D6\u05D4 \u05DC\u05D0 \u05D9\u05D6\u05D4\u05D4 \u05D0\u05D5\u05EA\u05DA \u05D0\u05D9\u05E9\u05D9\u05EA;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(118, "li");
        i0.ɵɵtext(119, "\u05DC\u05E9\u05DD \u05EA\u05E4\u05E2\u05D5\u05DC\u05D5 \u05D4\u05EA\u05E7\u05D9\u05DF \u05D5\u05E4\u05D9\u05EA\u05D5\u05D7\u05D5 \u05E9\u05DC \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(120, "li");
        i0.ɵɵtext(121, "\u05DB\u05D3\u05D9 \u05DC\u05D0\u05DB\u05D5\u05E3 \u05D0\u05EA ");
        i0.ɵɵelementStart(122, "a", 2);
        i0.ɵɵtext(123, "\u05EA\u05E0\u05D0\u05D9 \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9");
        i0.ɵɵelementEnd();
        i0.ɵɵtext(124, " \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05E2\u05DC-\u05DE\u05E0\u05EA \u05DC\u05DE\u05DC\u05D0 \u05D0\u05D7\u05E8 \u05D3\u05E8\u05D9\u05E9\u05D5\u05EA \u05DB\u05DC \u05D7\u05D5\u05E7, \u05EA\u05E7\u05E0\u05D4 \u05D0\u05D5 \u05D3\u05D1\u05E8 \u05D7\u05E7\u05D9\u05E7\u05D4 \u05D0\u05D7\u05E8 \u05D5\u05DB\u05DF \u05E2\u05DC-\u05DE\u05E0\u05EA \u05DC\u05E1\u05D9\u05D9\u05E2 \u05DC\u05E8\u05E9\u05D5\u05D9\u05D5\u05EA \u05D5\u05E2\u05E8\u05DB\u05D0\u05D5\u05EA \u05DE\u05D5\u05E1\u05DE\u05DB\u05D5\u05EA, \u05D0\u05D5 \u05DC\u05DB\u05DC \u05E6\u05D3 \u05E9\u05DC\u05D9\u05E9\u05D9, \u05DB\u05E9\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E1\u05D1\u05D5\u05E8\u05D4 \u05D1\u05EA\u05D5\u05DD \u05DC\u05D1 \u05DB\u05D9 \u05E2\u05DC\u05D9\u05D4 \u05DC\u05E2\u05E9\u05D5\u05EA \u05DB\u05DF;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(125, "li");
        i0.ɵɵtext(126, "\u05DC\u05DB\u05DC \u05DE\u05D8\u05E8\u05D4 \u05D0\u05D7\u05E8\u05EA, \u05D4\u05DE\u05E4\u05D5\u05E8\u05D8\u05EA \u05D1\u05DE\u05D3\u05D9\u05E0\u05D9\u05D5\u05EA \u05D6\u05D5 \u05D0\u05D5 ");
        i0.ɵɵelementStart(127, "a", 2);
        i0.ɵɵtext(128, "\u05D1\u05EA\u05E0\u05D0\u05D9 \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9");
        i0.ɵɵelementEnd();
        i0.ɵɵtext(129, " \u05E9\u05DC \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(130, "h2");
        i0.ɵɵtext(131, "\u05DE\u05E1\u05D9\u05E8\u05EA \u05DE\u05D9\u05D3\u05E2 \u05DC\u05E6\u05D3\u05D3\u05D9\u05DD \u05E9\u05DC\u05D9\u05E9\u05D9\u05D9\u05DD");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(132, "p");
        i0.ɵɵtext(133, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D0 \u05EA\u05E2\u05D1\u05D9\u05E8 \u05DC\u05E6\u05D3\u05D3\u05D9\u05DD \u05E9\u05DC\u05D9\u05E9\u05D9\u05D9\u05DD \u05D0\u05EA \u05D4\u05DE\u05D9\u05D3\u05E2 \u05D4\u05D0\u05D9\u05E9\u05D9 \u05E9\u05DE\u05E1\u05E8\u05EA \u05D5\u05D0\u05EA \u05D4\u05DE\u05D9\u05D3\u05E2 \u05E9\u05E0\u05D0\u05E1\u05E3 \u05E2\u05DC \u05E4\u05E2\u05D9\u05DC\u05D5\u05EA\u05DA \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D0\u05DC\u05D0 \u05D1\u05DE\u05E7\u05E8\u05D9\u05DD \u05D4\u05DE\u05E4\u05D5\u05E8\u05D8\u05D9\u05DD \u05DC\u05D4\u05DC\u05DF - ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(134, "ul");
        i0.ɵɵelementStart(135, "li");
        i0.ɵɵtext(136, "\u05DB\u05DB\u05DC \u05E9\u05D4\u05D3\u05D1\u05E8 \u05D3\u05E8\u05D5\u05E9 \u05DC\u05E6\u05D5\u05E8\u05DA \u05D0\u05E1\u05E4\u05E7\u05D4 \u05EA\u05E7\u05D9\u05E0\u05D4 \u05E9\u05DC \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(137, "li");
        i0.ɵɵtext(138, "\u05D0\u05DD \u05EA\u05E4\u05E8 \u05D0\u05EA \u05D4\u05D5\u05E8\u05D0\u05D5\u05EA \u05D4\u05DE\u05E1\u05DE\u05DB\u05D9\u05DD \u05D4\u05DE\u05D7\u05D9\u05D9\u05D1\u05D9\u05DD, \u05D0\u05D5 \u05D0\u05DD \u05EA\u05D1\u05E6\u05E2 \u05D1\u05D0\u05DE\u05E6\u05E2\u05D5\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D0\u05D5 \u05D1\u05E7\u05E9\u05E8 \u05E2\u05DE\u05D5, \u05E4\u05E2\u05D5\u05DC\u05D5\u05EA \u05D4\u05E0\u05D7\u05D6\u05D5\u05EA \u05E2\u05DC \u05D9\u05D3\u05D9 \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DB\u05DE\u05E0\u05D5\u05D2\u05D3\u05D5\u05EA \u05DC\u05D3\u05D9\u05DF \u05D0\u05D5 \u05E0\u05D9\u05E1\u05D9\u05D5\u05DF \u05DC\u05D1\u05E6\u05E2 \u05E4\u05E2\u05D5\u05DC\u05D5\u05EA \u05DB\u05D0\u05DC\u05D4;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(139, "li");
        i0.ɵɵtext(140, "\u05D0\u05DD \u05D9\u05EA\u05E7\u05D1\u05DC \u05D1\u05D9\u05D3\u05D9 \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E6\u05D5 \u05E9\u05D9\u05E4\u05D5\u05D8\u05D9, \u05D0\u05D5 \u05D3\u05E8\u05D9\u05E9\u05D4 \u05E9\u05DC \u05E8\u05E9\u05D5\u05EA \u05D4\u05DE\u05D5\u05E1\u05DE\u05DB\u05EA \u05DC\u05DB\u05DA, \u05D4\u05DE\u05D5\u05E8\u05D4 \u05DC\u05D4 \u05DC\u05DE\u05E1\u05D5\u05E8 \u05D0\u05EA \u05E4\u05E8\u05D8\u05D9\u05DA \u05D0\u05D5 \u05D0\u05EA \u05D4\u05DE\u05D9\u05D3\u05E2 \u05D0\u05D5\u05D3\u05D5\u05EA\u05D9\u05DA \u05DC\u05E6\u05D3 \u05E9\u05DC\u05D9\u05E9\u05D9;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(141, "li");
        i0.ɵɵtext(142, "\u05D1\u05DB\u05DC \u05DE\u05D7\u05DC\u05D5\u05E7\u05EA, \u05D8\u05E2\u05E0\u05D4, \u05EA\u05D1\u05D9\u05E2\u05D4, \u05D3\u05E8\u05D9\u05E9\u05D4 \u05D0\u05D5 \u05D4\u05DC\u05D9\u05DB\u05D9\u05DD \u05DE\u05E9\u05E4\u05D8\u05D9\u05D9\u05DD, \u05D0\u05DD \u05D9\u05D4\u05D9\u05D5, \u05D1\u05D9\u05E0\u05DA \u05DC\u05D1\u05D9\u05DF \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D5 \u05DE\u05D9 \u05DE\u05D8\u05E2\u05DE\u05D4;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(143, "li");
        i0.ɵɵtext(144, "\u05D1\u05DB\u05DC \u05DE\u05E7\u05E8\u05D4 \u05E9\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05EA\u05E1\u05D1\u05D5\u05E8 \u05DB\u05D9 \u05DE\u05E1\u05D9\u05E8\u05EA \u05D4\u05DE\u05D9\u05D3\u05E2 \u05E0\u05D7\u05D5\u05E6\u05D4 \u05DB\u05D3\u05D9 \u05DC\u05DE\u05E0\u05D5\u05E2 \u05E0\u05D6\u05E7 \u05D7\u05DE\u05D5\u05E8 \u05DC\u05D2\u05D5\u05E4\u05DA \u05D0\u05D5 \u05DC\u05E8\u05DB\u05D5\u05E9\u05DA \u05D0\u05D5 \u05DC\u05D2\u05D5\u05E4\u05D5 \u05D0\u05D5 \u05DC\u05E8\u05DB\u05D5\u05E9\u05D5 \u05E9\u05DC \u05E6\u05D3 \u05E9\u05DC\u05D9\u05E9\u05D9;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(145, "li");
        i0.ɵɵtext(146, "\u05DC\u05D7\u05D1\u05E8\u05D5\u05EA \u05D0\u05D5 \u05DC\u05D0\u05E8\u05D2\u05D5\u05E0\u05D9\u05DD \u05D0\u05D7\u05E8\u05D9\u05DD \u05D4\u05E7\u05E9\u05D5\u05E8\u05D9\u05DD \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D3\u05D5\u05D2\u05DE\u05EA \u05D7\u05D1\u05E8\u05EA-\u05D0\u05DD, \u05D7\u05D1\u05E8\u05D4-\u05D1\u05EA \u05D5\u05D7\u05D1\u05E8\u05D4 \u05D0\u05D7\u05D5\u05EA \u05D5\u05D1\u05DC\u05D1\u05D3 \u05E9\u05D4\u05DD \u05D9\u05E9\u05EA\u05DE\u05E9\u05D5 \u05D1\u05DE\u05D9\u05D3\u05E2 \u05D6\u05D4 \u05E8\u05E7 \u05E2\u05DC-\u05E4\u05D9 \u05D4\u05D5\u05E8\u05D0\u05D5\u05EA \u05DE\u05D3\u05D9\u05E0\u05D9\u05D5\u05EA \u05E4\u05E8\u05D8\u05D9\u05D5\u05EA \u05D6\u05D5;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(147, "li");
        i0.ɵɵtext(148, "\u05DC\u05E6\u05D5\u05E8\u05DA \u05DE\u05E1\u05D9\u05E8\u05EA \u05D5\u05E9\u05D9\u05EA\u05D5\u05E3 \u05DE\u05D9\u05D3\u05E2 \u05D0\u05E0\u05D5\u05E0\u05D9\u05DE\u05D9, \u05DE\u05E6\u05E8\u05E4\u05D9 \u05D5\u05E1\u05D8\u05D8\u05D9\u05E1\u05D8\u05D9 \u05E2\u05DD \u05D7\u05D1\u05E8\u05D5\u05EA \u05D0\u05D5 \u05D0\u05E8\u05D2\u05D5\u05E0\u05D9\u05DD \u05D0\u05D7\u05E8\u05D9\u05DD \u05D4\u05E7\u05E9\u05D5\u05E8\u05D9\u05DD \u05D1\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05DB\u05DF \u05E2\u05DD \u05E1\u05E4\u05E7\u05D9\u05DD, \u05E9\u05D5\u05EA\u05E4\u05D9\u05DD \u05E2\u05E1\u05E7\u05D9\u05D9\u05DD, \u05DE\u05E4\u05E8\u05E1\u05DE\u05D9\u05DD \u05D5\u05DB\u05DC \u05E6\u05D3 \u05E9\u05DC\u05D9\u05E9\u05D9. \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D0 \u05EA\u05D7\u05E9\u05D5\u05E3 \u05D1\u05E4\u05E0\u05D9 \u05E6\u05D3\u05D3\u05D9\u05DD \u05E9\u05DC\u05D9\u05E9\u05D9\u05D9\u05DD \u05D0\u05DC\u05D4 \u05D1\u05D9\u05D5\u05D3\u05E2\u05D9\u05DF \u05D0\u05EA \u05D6\u05D4\u05D5\u05EA\u05DA \u05DC\u05DC\u05D0 \u05D4\u05E1\u05DB\u05DE\u05EA\u05DA;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(149, "li");
        i0.ɵɵtext(150, "\u05D0\u05DD \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05EA\u05D0\u05E8\u05D2\u05DF \u05D0\u05EA \u05E4\u05E2\u05D9\u05DC\u05D5\u05EA\u05D4 \u05D0\u05D5 \u05D0\u05EA \u05E4\u05E2\u05D9\u05DC\u05D5\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D1\u05DE\u05E1\u05D2\u05E8\u05EA \u05D0\u05D7\u05E8\u05EA \u05D5\u05DB\u05DF \u05D1\u05DE\u05E7\u05E8\u05D4 \u05E9\u05EA\u05E9\u05E0\u05D4 \u05D0\u05EA \u05D4\u05DE\u05D1\u05E0\u05D4 \u05D4\u05DE\u05E9\u05E4\u05D8\u05D9, \u05D0\u05D5 \u05EA\u05EA\u05DE\u05D6\u05D2 \u05E2\u05DD \u05D2\u05D5\u05E3 \u05D0\u05D7\u05E8, \u05D0\u05D5 \u05EA\u05DE\u05D6\u05D2 \u05D0\u05EA \u05E4\u05E2\u05D9\u05DC\u05D5\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E2\u05DD \u05E4\u05E2\u05D9\u05DC\u05D5\u05EA\u05D5 \u05E9\u05DC \u05E6\u05D3 \u05E9\u05DC\u05D9\u05E9\u05D9 - \u05D4\u05D9\u05D0 \u05EA\u05D4\u05D9\u05D4 \u05D6\u05DB\u05D0\u05D9\u05EA \u05DC\u05D4\u05E2\u05D1\u05D9\u05E8 \u05DC\u05D2\u05D5\u05E3 \u05D4\u05D7\u05D3\u05E9 \u05D4\u05E2\u05EA\u05E7 \u05DE\u05DF \u05D4\u05DE\u05D9\u05D3\u05E2 \u05E9\u05E0\u05D0\u05D2\u05E8 \u05D0\u05D5\u05D3\u05D5\u05EA\u05D9\u05DA \u05D1\u05DE\u05D0\u05D2\u05E8 \u05D4\u05DE\u05D9\u05D3\u05E2 \u05E9\u05DC \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05D1\u05DC\u05D1\u05D3 \u05E9\u05D2\u05D5\u05E3 \u05D6\u05D4 \u05D9\u05E7\u05D1\u05DC \u05E2\u05DC \u05E2\u05E6\u05DE\u05D5 \u05DB\u05DC\u05E4\u05D9\u05DA \u05D0\u05EA \u05D4\u05D5\u05E8\u05D0\u05D5\u05EA \u05DE\u05D3\u05D9\u05E0\u05D9\u05D5\u05EA \u05E4\u05E8\u05D8\u05D9\u05D5\u05EA \u05D6\u05D5;");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(151, "li");
        i0.ɵɵtext(152, "\u05E2\u05DC-\u05E4\u05D9 \u05D1\u05E7\u05E9\u05EA\u05DA \u05D4\u05DE\u05E4\u05D5\u05E8\u05E9\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(153, "li");
        i0.ɵɵtext(154, "\u05E9\u05DC\u05D9\u05D7\u05EA \u05EA\u05D5\u05DB\u05DF \u05E9\u05D9\u05D5\u05D5\u05E7\u05D9, \u05DB\u05DB\u05DC \u05E9\u05D4\u05E1\u05DB\u05DE\u05EA \u05DC\u05DB\u05DA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(155, "p");
        i0.ɵɵtext(156, "\u05DB\u05DB\u05DC \u05E9\u05D4\u05D5\u05E2\u05D1\u05E8 \u05DE\u05D9\u05D3\u05E2 \u05DC\u05E6\u05D3 \u05E9\u05DC\u05D9\u05E9\u05D9 \u05D1\u05D4\u05EA\u05D0\u05DD \u05DC\u05DE\u05D3\u05D9\u05E0\u05D9\u05D5\u05EA \u05D4\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA, \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D0 \u05EA\u05D4\u05D9\u05D4 \u05DB\u05DC \u05D7\u05D1\u05D5\u05EA \u05D1\u05DB\u05DC \u05D4\u05E7\u05E9\u05D5\u05E8 \u05DC\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05DE\u05D9\u05D3\u05E2 \u05D6\u05D4 \u05E2\u05DC \u05D9\u05D3\u05D9 \u05D4\u05E6\u05D3 \u05D4\u05E9\u05DC\u05D9\u05E9\u05D9. ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(157, "h2");
        i0.ɵɵtext(158, "\u05D4\u05D9\u05DB\u05DF \u05E0\u05E9\u05DE\u05E8 \u05D4\u05DE\u05D9\u05D3\u05E2?");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(159, "p");
        i0.ɵɵtext(160, "\u05D4\u05DE\u05D9\u05D3\u05E2 \u05E9\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D5\u05E1\u05E4\u05EA \u05E0\u05E9\u05DE\u05E8 \u05D1\u05DE\u05D0\u05D2\u05E8 \u05D4\u05DE\u05D9\u05D3\u05E2 \u05D4\u05E8\u05E9\u05D5\u05DD \u05E9\u05DC\u05D4, \u05D4\u05DE\u05D0\u05D5\u05D7\u05E1\u05DF \u05D0\u05E6\u05DC \u05E1\u05E4\u05E7\u05D9 \u05D0\u05D9\u05E8\u05D5\u05D7 \u05E9\u05E8\u05EA\u05D9\u05DD \u05D5\u05D2\u05D9\u05D1\u05D5\u05D9 \u05DE\u05D9\u05D3\u05E2, \u05E9\u05E2\u05E9\u05D5\u05D9\u05D9\u05DD \u05DC\u05D4\u05D9\u05DE\u05E6\u05D0 \u05D2\u05DD \u05DE\u05D7\u05D5\u05E5 \u05DC\u05D2\u05D1\u05D5\u05DC\u05D5\u05EA \u05D9\u05E9\u05E8\u05D0\u05DC. \u05DE\u05D9\u05D3\u05E2 \u05E0\u05D5\u05E1\u05E3, \u05DB\u05E4\u05D9 \u05E9\u05DE\u05E4\u05D5\u05E8\u05D8 \u05D1\u05DE\u05D3\u05D9\u05E0\u05D9\u05D5\u05EA \u05D6\u05D5, \u05E0\u05D0\u05E1\u05E3 \u05D5\u05E0\u05E9\u05DE\u05E8 \u05E2\u05DC \u05D9\u05D3\u05D9 \u05D7\u05D1\u05E8\u05D5\u05EA \u05D0\u05D7\u05E8\u05D5\u05EA (\u05DB\u05D3\u05D5\u05D2\u05DE\u05EA \u05D7\u05D1\u05E8\u05D5\u05EA \u05E1\u05D8\u05D8\u05D9\u05E1\u05D8\u05D9\u05E7\u05D4 \u05D5\u05DB\u05D9\u05D5\"\u05D1). \u05D0\u05E3 \u05D4\u05DF \u05D9\u05DB\u05D5\u05DC\u05D5\u05EA \u05DC\u05E9\u05DE\u05D5\u05E8 \u05D0\u05EA \u05D4\u05DE\u05D9\u05D3\u05E2 \u05DE\u05D7\u05D5\u05E5 \u05DC\u05D2\u05D1\u05D5\u05DC\u05D5\u05EA \u05D4\u05DE\u05D3\u05D9\u05E0\u05D4. \u05D4\u05E1\u05DB\u05DE\u05EA\u05DA \u05DC\u05DE\u05D3\u05D9\u05E0\u05D9\u05D5\u05EA \u05D6\u05D5 \u05DE\u05D4\u05D5\u05D5\u05D4 \u05D2\u05DD \u05D4\u05E1\u05DB\u05DE\u05D4 \u05DC\u05E9\u05DE\u05D9\u05E8\u05EA \u05D4\u05DE\u05D9\u05D3\u05E2 \u05D1\u05D7\u05D5\"\u05DC \u05D5\u05DC\u05D4\u05E2\u05D1\u05E8\u05EA\u05D5 \u05DE\u05D7\u05D5\u05E5 \u05DC\u05D2\u05D1\u05D5\u05DC\u05D5\u05EA \u05D9\u05E9\u05E8\u05D0\u05DC.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(161, "h2");
        i0.ɵɵtext(162, "Cookies");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(163, "p");
        i0.ɵɵtext(164, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E2\u05E9\u05D5\u05D9\u05D4 \u05DC\u05D4\u05E9\u05EA\u05DE\u05E9 \u05D1\u05D0\u05EA\u05E8 \u05D1'\u05E2\u05D5\u05D2\u05D9\u05D5\u05EA' (Cookies) \u05DC\u05E6\u05D5\u05E8\u05DA \u05EA\u05E4\u05E2\u05D5\u05DC\u05D5 \u05D4\u05E9\u05D5\u05D8\u05E3 \u05D5\u05D4\u05EA\u05E7\u05D9\u05DF, \u05DC\u05E8\u05D1\u05D5\u05EA \u05DB\u05D3\u05D9 \u05DC\u05D0\u05E1\u05D5\u05E3 \u05E0\u05EA\u05D5\u05E0\u05D9\u05DD \u05E1\u05D8\u05D8\u05D9\u05E1\u05D8\u05D9\u05D9\u05DD \u05D0\u05D5\u05D3\u05D5\u05EA \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05D0\u05EA\u05E8, \u05DC\u05D0\u05D9\u05DE\u05D5\u05EA \u05E4\u05E8\u05D8\u05D9\u05DD, \u05DB\u05D3\u05D9 \u05DC\u05D4\u05EA\u05D0\u05D9\u05DD \u05D0\u05EA \u05D4\u05D0\u05EA\u05E8 \u05DC\u05D4\u05E2\u05D3\u05E4\u05D5\u05EA\u05D9\u05DA \u05D4\u05D0\u05D9\u05E9\u05D9\u05D5\u05EA, \u05DC\u05E6\u05E8\u05DB\u05D9 \u05D0\u05D1\u05D8\u05D7\u05EA \u05DE\u05D9\u05D3\u05E2 \u05D5\u05E2\u05D5\u05D3.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(165, "p");
        i0.ɵɵtext(166, "'\u05E2\u05D5\u05D2\u05D9\u05D5\u05EA' (Cookies) \u05D4\u05DF \u05E7\u05D1\u05E6\u05D9 \u05D8\u05E7\u05E1\u05D8, \u05E9\u05D4\u05D3\u05E4\u05D3\u05E4\u05DF \u05E9\u05DC\u05DA \u05D9\u05D5\u05E6\u05E8 \u05DC\u05E4\u05D9 \u05E4\u05E7\u05D5\u05D3\u05D4 \u05DE\u05DE\u05D7\u05E9\u05D1\u05D9 \u05D4\u05D0\u05EA\u05E8. \u05D7\u05DC\u05E7 \u05DE\u05D4\u05E2\u05D5\u05D2\u05D9\u05D5\u05EA \u05D9\u05E4\u05E7\u05E2\u05D5 \u05DB\u05D0\u05E9\u05E8 \u05EA\u05E1\u05D2\u05D5\u05E8 \u05D0\u05EA \u05D4\u05D3\u05E4\u05D3\u05E4\u05DF \u05D5\u05D0\u05D7\u05E8\u05D5\u05EA \u05E0\u05E9\u05DE\u05E8\u05D5\u05EA \u05E2\u05DC \u05D2\u05D1\u05D9 \u05D4\u05DB\u05D5\u05E0\u05DF \u05D4\u05E7\u05E9\u05D9\u05D7 \u05D1\u05DE\u05DB\u05E9\u05D9\u05E8 \u05D4\u05E7\u05E6\u05D4 \u05D4\u05DE\u05E9\u05DE\u05E9 \u05D0\u05D5\u05EA\u05DA. \u05D4-Cookies \u05D9\u05DB\u05D5\u05DC\u05D5\u05EA \u05DC\u05D4\u05DB\u05D9\u05DC \u05DE\u05D9\u05D3\u05E2 \u05DE\u05D2\u05D5\u05D5\u05DF \u05DB\u05D3\u05D5\u05D2\u05DE\u05EA \u05D4\u05D3\u05E4\u05D9\u05DD \u05E9\u05D1\u05D4\u05DD \u05D1\u05D9\u05E7\u05E8\u05EA, \u05DE\u05E9\u05DA \u05D4\u05D6\u05DE\u05DF \u05E9\u05E9\u05D4\u05D9\u05EA \u05D1\u05D0\u05EA\u05E8, \u05DE\u05D4\u05D9\u05DB\u05DF \u05D4\u05D2\u05E2\u05EA \u05D0\u05DC \u05D4\u05D0\u05EA\u05E8, \u05DE\u05D9\u05D3\u05E2 \u05E9\u05D0\u05EA\u05D4 \u05DE\u05D1\u05E7\u05E9 \u05DC\u05E8\u05D0\u05D5\u05EA \u05D1\u05E2\u05EA \u05D4\u05DB\u05E0\u05D9\u05E1\u05D4 \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05E2\u05D5\u05D3.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(167, "p");
        i0.ɵɵtext(168, "\u05D0\u05DD \u05D0\u05D9\u05E0\u05DA \u05E8\u05D5\u05E6\u05D4 \u05DC\u05E7\u05D1\u05DC Cookies, \u05EA\u05D5\u05DB\u05DC \u05DC\u05D4\u05D9\u05DE\u05E0\u05E2 \u05DE\u05DB\u05DA \u05E2\u05DC \u05D9\u05D3\u05D9 \u05E9\u05D9\u05E0\u05D5\u05D9 \u05D4\u05D4\u05D2\u05D3\u05E8\u05D5\u05EA \u05D1\u05D3\u05E4\u05D3\u05E4\u05DF \u05E9\u05DC\u05DA. \u05DC\u05E9\u05DD \u05DB\u05DA \u05E0\u05D0 \u05D4\u05D9\u05D5\u05D5\u05E2\u05E5 \u05D1\u05E7\u05D5\u05D1\u05E5 \u05D4\u05E2\u05D6\u05E8\u05D4 \u05E9\u05DC \u05D4\u05D3\u05E4\u05D3\u05E4\u05DF. \u05D6\u05DB\u05D5\u05E8 \u05E2\u05DD \u05D6\u05D0\u05EA, \u05E9\u05E0\u05D8\u05E8\u05D5\u05DC \u05D4\u05E2\u05D5\u05D2\u05D9\u05D5\u05EA \u05E2\u05DC\u05D5\u05DC \u05DC\u05D2\u05E8\u05D5\u05DD \u05DC\u05DB\u05DA \u05E9\u05DC\u05D0 \u05EA\u05D5\u05DB\u05DC \u05DC\u05D4\u05E9\u05EA\u05DE\u05E9 \u05D1\u05D7\u05DC\u05E7 \u05DE\u05D4\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05DD \u05D5\u05D4\u05EA\u05DB\u05D5\u05E0\u05D5\u05EA \u05D1\u05D0\u05EA\u05E8 \u05D0\u05D5 \u05D1\u05D0\u05EA\u05E8\u05D9 \u05D0\u05D9\u05E0\u05D8\u05E8\u05E0\u05D8 \u05D0\u05D7\u05E8\u05D9\u05DD. \u05D1\u05E0\u05D5\u05E1\u05E3 \u05DC\u05DB\u05DA, \u05D0\u05EA\u05D4 \u05D9\u05DB\u05D5\u05DC \u05DC\u05DE\u05D7\u05D5\u05E7 \u05D0\u05EA \u05D4-Cookies \u05D1\u05DE\u05DB\u05E9\u05D9\u05E8 \u05D4\u05E7\u05E6\u05D4 \u05E9\u05DC\u05DA \u05D1\u05DB\u05DC \u05E8\u05D2\u05E2. \u05DE\u05D5\u05E6\u05E2 \u05E9\u05EA\u05E2\u05E9\u05D4 \u05DB\u05DF, \u05E8\u05E7 \u05D0\u05DD \u05D0\u05EA\u05D4 \u05DE\u05E9\u05D5\u05DB\u05E0\u05E2 \u05E9\u05D0\u05D9\u05E0\u05DA \u05E8\u05D5\u05E6\u05D4 \u05E9\u05D4\u05D0\u05EA\u05E8 \u05D9\u05D5\u05EA\u05D0\u05DD \u05DC\u05D4\u05E2\u05D3\u05E4\u05D5\u05EA\u05D9\u05DA \u05D5\u05D1\u05DE\u05E7\u05E8\u05D4 \u05E9\u05D0\u05EA\u05D4 \u05DE\u05E2\u05D5\u05E0\u05D9\u05D9\u05DF \u05E9\u05D4\u05D0\u05EA\u05E8 \u05DC\u05D0 \u05D9\u05D6\u05D4\u05D4 \u05D0\u05D5\u05EA\u05DA \u05D1\u05DB\u05E0\u05D9\u05E1\u05D5\u05EA \u05D7\u05D5\u05D6\u05E8\u05D5\u05EA \u05DC\u05DE\u05D3\u05D5\u05E8\u05D9\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D4\u05D3\u05D5\u05E8\u05E9\u05D9\u05DD \u05D4\u05E8\u05E9\u05DE\u05D4.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(169, "h2");
        i0.ɵɵtext(170, "\u05D0\u05D1\u05D8\u05D7\u05EA \u05DE\u05D9\u05D3\u05E2");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(171, "p");
        i0.ɵɵtext(172, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DE\u05D9\u05D9\u05D7\u05E1\u05EA \u05D7\u05E9\u05D9\u05D1\u05D5\u05EA \u05E2\u05DC\u05D9\u05D5\u05E0\u05D4 \u05DC\u05D0\u05D1\u05D8\u05D7\u05EA \u05D4\u05DE\u05D9\u05D3\u05E2 \u05D1\u05DE\u05E2\u05E8\u05DB\u05D5\u05EA\u05D9\u05D4. \u05DC\u05E9\u05DD \u05E9\u05DE\u05D9\u05E8\u05EA \u05D4\u05DE\u05D9\u05D3\u05E2, \u05D4\u05D9\u05D0 \u05DE\u05E4\u05E2\u05D9\u05DC\u05D4 \u05DE\u05E2\u05E8\u05DB\u05D5\u05EA, \u05D9\u05D9\u05E9\u05D5\u05DE\u05D9\u05DD \u05D5\u05E0\u05D4\u05DC\u05D9\u05DD \u05DC\u05D0\u05D1\u05D8\u05D7\u05EA \u05D4\u05DE\u05D9\u05D3\u05E2, \u05D4\u05DE\u05D9\u05D5\u05E2\u05D3\u05D9\u05DD \u05DC\u05DE\u05D6\u05E2\u05E8 \u05D0\u05EA \u05D4\u05E1\u05D9\u05DB\u05D5\u05E0\u05D9\u05DD \u05DE\u05E4\u05E0\u05D9 \u05D2\u05E0\u05D9\u05D1\u05D4, \u05E4\u05D2\u05D9\u05E2\u05D4, \u05D0\u05D5\u05D1\u05D3\u05DF \u05D0\u05D5 \u05D2\u05D9\u05E9\u05D4 \u05D1\u05DC\u05EA\u05D9 \u05DE\u05D5\u05E8\u05E9\u05D9\u05EA \u05DC\u05DE\u05D9\u05D3\u05E2, \u05D1\u05D4\u05EA\u05D0\u05DD \u05DC\u05E7\u05D1\u05D5\u05E2 \u05D1\u05EA\u05E7\u05E0\u05D5\u05EA \u05D4\u05D2\u05E0\u05EA \u05D4\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA (\u05D0\u05D1\u05D8\u05D7\u05EA \u05DE\u05D9\u05D3\u05E2), \u05D4\u05EA\u05E9\u05E2\"\u05D6-2017.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(173, "p");
        i0.ɵɵtext(174, "\u05E2\u05DD \u05D6\u05D0\u05EA, \u05D0\u05D9\u05DF \u05D5\u05D5\u05D3\u05D0\u05D5\u05EA \u05E9\u05D9\u05D9\u05E9\u05D5\u05DE\u05D9\u05DD \u05D0\u05DC\u05D4 \u05DE\u05D1\u05D8\u05D9\u05D7\u05D9\u05DD \u05D1\u05D0\u05D5\u05E4\u05DF \u05DE\u05D5\u05D7\u05DC\u05D8 \u05D5\u05D4\u05E8\u05DE\u05D8\u05D9 \u05E9\u05DE\u05D9\u05D3\u05E2 \u05D0\u05D9\u05E9\u05D9 \u05DC\u05D0 \u05D9\u05D9\u05D7\u05E9\u05E3, \u05D9\u05D9\u05D0\u05D1\u05D3 \u05D0\u05D5 \u05D9\u05D9\u05D2\u05E0\u05D1 \u05DE\u05DE\u05D0\u05D2\u05E8\u05D9 \u05D4\u05DE\u05D9\u05D3\u05E2. \u05DC\u05DB\u05DF, \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D0\u05D9\u05E0\u05D4 \u05DE\u05EA\u05D7\u05D9\u05D9\u05D1\u05EA, \u05D5\u05D0\u05D9\u05E0\u05DA \u05D9\u05DB\u05D5\u05DC \u05D1\u05D0\u05D5\u05E4\u05DF \u05E1\u05D1\u05D9\u05E8 \u05DC\u05E6\u05E4\u05D5\u05EA \u05DC\u05DB\u05DA, \u05E9\u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05DE\u05E2\u05E8\u05DB\u05D5\u05EA \u05D4\u05DE\u05D9\u05D3\u05E2 \u05D4\u05DE\u05E9\u05DE\u05E9\u05D5\u05EA \u05D0\u05EA \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05D9\u05D4\u05D9\u05D5 \u05D7\u05E1\u05D9\u05E0\u05D9\u05DD \u05DE\u05E4\u05E0\u05D9 \u05D2\u05D9\u05E9\u05D4 \u05D1\u05DC\u05EA\u05D9-\u05DE\u05D5\u05E8\u05E9\u05D9\u05EA \u05DC\u05DE\u05D9\u05D3\u05E2 \u05D4\u05DE\u05D0\u05D5\u05D7\u05E1\u05DF \u05D1\u05D4\u05DD. \u05D1\u05E2\u05E6\u05DD \u05D4\u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA, \u05D0\u05EA\u05D4 \u05DE\u05D5\u05D3\u05E2 \u05D5\u05DE\u05E1\u05DB\u05D9\u05DD \u05DC\u05DE\u05D2\u05D1\u05DC\u05D5\u05EA \u05D0\u05DC\u05D5.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(175, "h2");
        i0.ɵɵtext(176, "\u05D6\u05DB\u05D5\u05EA \u05DC\u05E2\u05D9\u05D9\u05DF \u05D1\u05DE\u05D9\u05D3\u05E2");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(177, "p");
        i0.ɵɵtext(178, "\u05E2\u05DC-\u05E4\u05D9 \u05D7\u05D5\u05E7 \u05D4\u05D2\u05E0\u05EA \u05D4\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA, , \u05DB\u05DC \u05D0\u05D3\u05DD \u05D6\u05DB\u05D0\u05D9 \u05DC\u05E2\u05D9\u05D9\u05DF \u05D1\u05E2\u05E6\u05DE\u05D5, \u05D0\u05D5 \u05E2\u05DC \u05D9\u05D3\u05D9 \u05D1\u05D0-\u05DB\u05D5\u05D7\u05D5 \u05E9\u05D4\u05E8\u05E9\u05D4\u05D5 \u05D1\u05DB\u05EA\u05D1, \u05D0\u05D5 \u05E2\u05DC \u05D9\u05D3\u05D9 \u05D0\u05E4\u05D5\u05D8\u05E8\u05D5\u05E4\u05D5\u05E1, \u05D1\u05DE\u05D9\u05D3\u05E2 \u05E9\u05E2\u05DC\u05D9\u05D5 \u05D4\u05DE\u05D5\u05D7\u05D6\u05E7 \u05D1\u05DE\u05D0\u05D2\u05E8 \u05DE\u05D9\u05D3\u05E2. \u05D0\u05D3\u05DD \u05E9\u05E2\u05D9\u05D9\u05DF \u05D1\u05DE\u05D9\u05D3\u05E2 \u05E9\u05E2\u05DC\u05D9\u05D5 \u05D5\u05DE\u05E6\u05D0 \u05E9\u05D0\u05D9\u05E0\u05D5 \u05E0\u05DB\u05D5\u05DF, \u05E9\u05DC\u05DD, \u05D1\u05E8\u05D5\u05E8 \u05D0\u05D5 \u05DE\u05E2\u05D5\u05D3\u05DB\u05DF, \u05E8\u05E9\u05D0\u05D9 \u05DC\u05E4\u05E0\u05D5\u05EA \u05DC\u05D1\u05E2\u05DC \u05DE\u05D0\u05D2\u05E8 \u05D4\u05DE\u05D9\u05D3\u05E2 \u05D1\u05D1\u05E7\u05E9\u05D4 \u05DC\u05EA\u05E7\u05DF \u05D0\u05EA \u05D4\u05DE\u05D9\u05D3\u05E2 \u05D0\u05D5 \u05DC\u05DE\u05D7\u05D5\u05E7 \u05D0\u05D5\u05EA\u05D5. \u05D0\u05DD \u05D1\u05E2\u05DC \u05D4\u05DE\u05D0\u05D2\u05E8 \u05E1\u05D9\u05E8\u05D1, \u05E2\u05DC\u05D9\u05D5 \u05DC\u05D4\u05D5\u05D3\u05D9\u05E2 \u05E2\u05DC \u05DB\u05DA \u05DC\u05DE\u05D1\u05E7\u05E9 \u05D1\u05D0\u05D5\u05E4\u05DF \u05D5\u05D1\u05D3\u05E8\u05DA \u05E9\u05E0\u05E7\u05D1\u05E2\u05D5 \u05D1\u05EA\u05E7\u05E0\u05D5\u05EA \u05E9\u05DE\u05DB\u05D5\u05D7 \u05D7\u05D5\u05E7 \u05D4\u05D2\u05E0\u05EA \u05D4\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA. \u05E2\u05DC \u05E1\u05D9\u05E8\u05D5\u05D1\u05D5 \u05E9\u05DC \u05D1\u05E2\u05DC \u05DE\u05D0\u05D2\u05E8 \u05DE\u05D9\u05D3\u05E2 \u05DC\u05D0\u05E4\u05E9\u05E8 \u05E2\u05D9\u05D5\u05DF \u05D5\u05E2\u05DC \u05D4\u05D5\u05D3\u05E2\u05EA \u05E1\u05D9\u05E8\u05D5\u05D1 \u05DC\u05EA\u05E7\u05DF \u05D0\u05D5 \u05DC\u05DE\u05D7\u05D5\u05E7 \u05DE\u05D9\u05D3\u05E2, \u05E8\u05E9\u05D0\u05D9 \u05DE\u05D1\u05E7\u05E9 \u05D4\u05DE\u05D9\u05D3\u05E2 \u05DC\u05E2\u05E8\u05E2\u05E8 \u05D1\u05D0\u05D5\u05E4\u05DF \u05D5\u05D1\u05D3\u05E8\u05DA \u05E9\u05E0\u05E7\u05D1\u05E2\u05D5 \u05D1\u05EA\u05E7\u05E0\u05D5\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(179, "p");
        i0.ɵɵtext(180, "\u05E9\u05D9\u05DD \u05DC\u05D1 \u05DB\u05D9 \u05DE\u05D7\u05D9\u05E7\u05EA \u05DE\u05D9\u05D3\u05E2 \u05E2\u05DC\u05D5\u05DC\u05D4 \u05DC\u05DE\u05E0\u05D5\u05E2 \u05DE\u05DE\u05DA \u05DC\u05D4\u05DE\u05E9\u05D9\u05DA \u05D5\u05DC\u05D4\u05E9\u05EA\u05DE\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D5\u05DC\u05D4\u05D1\u05D9\u05D0 \u05DC\u05D1\u05D9\u05D8\u05D5\u05DC \u05E8\u05D9\u05E9\u05D5\u05DE\u05DA \u05DC\u05E9\u05D9\u05E8\u05D5\u05EA. \u05DE\u05D9\u05D3\u05E2 \u05D4\u05D3\u05E8\u05D5\u05E9 \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA - \u05DC\u05E8\u05D1\u05D5\u05EA \u05EA\u05D9\u05E2\u05D5\u05D3 \u05E4\u05E2\u05D5\u05DC\u05D5\u05EA \u05E9\u05D1\u05D9\u05E6\u05E2\u05EA \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA - \u05D9\u05D5\u05E1\u05D9\u05E3 \u05DC\u05D4\u05D9\u05E9\u05DE\u05E8 \u05E2\u05DC \u05D9\u05D3\u05D9 \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E2\u05DC-\u05E4\u05D9 \u05D3\u05D9\u05DF, \u05D0\u05DA \u05DC\u05D0 \u05D9\u05E9\u05DE\u05E9 \u05E2\u05D5\u05D3 \u05DC\u05E6\u05D5\u05E8\u05DA \u05E4\u05E0\u05D9\u05D5\u05EA \u05D0\u05DC\u05D9\u05DA. \u05D0\u05DD \u05D1\u05EA\u05D5\u05DA 30 \u05D9\u05D5\u05DD \u05DC\u05D0 \u05EA\u05E7\u05D1\u05DC \u05D4\u05D5\u05D3\u05E2\u05D4 \u05DB\u05D9 \u05D4\u05DE\u05D9\u05D3\u05E2 \u05E9\u05E0\u05EA\u05D1\u05E7\u05E9\u05D4 \u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05DE\u05D7\u05D5\u05E7 \u05D0\u05DB\u05DF \u05E0\u05DE\u05D7\u05E7 \u05E2\u05DC-\u05E4\u05D9 \u05E1\u05E2\u05D9\u05E3 \u05D6\u05D4, \u05EA\u05D4\u05D9\u05D4 \u05D6\u05DB\u05D0\u05D9 \u05DC\u05E4\u05E0\u05D5\u05EA \u05DC\u05D1\u05D9\u05EA \u05DE\u05E9\u05E4\u05D8 \u05D1\u05D0\u05D5\u05E4\u05DF \u05D4\u05E7\u05D1\u05D5\u05E2 \u05D1\u05EA\u05E7\u05E0\u05D5\u05EA \u05E9\u05DE\u05DB\u05D5\u05D7 \u05D7\u05D5\u05E7 \u05D4\u05D2\u05E0\u05EA \u05D4\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA, \u05DB\u05D3\u05D9 \u05E9\u05D9\u05D5\u05E8\u05D4 \u05DC\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05E4\u05E2\u05D5\u05DC \u05DB\u05D0\u05DE\u05D5\u05E8.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(181, "h2");
        i0.ɵɵtext(182, "\u05E9\u05D9\u05E0\u05D5\u05D9\u05D9\u05DD \u05D1\u05DE\u05D3\u05D9\u05E0\u05D9\u05D5\u05EA \u05D4\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(183, "p");
        i0.ɵɵtext(184, "\u05DE\u05E4\u05E2\u05D9\u05DC\u05EA \u05D4\u05E9\u05D9\u05E8\u05D5\u05EA \u05E8\u05E9\u05D0\u05D9\u05EA \u05DC\u05E9\u05E0\u05D5\u05EA \u05DE\u05E2\u05EA \u05DC\u05E2\u05EA \u05D0\u05EA \u05D4\u05D5\u05E8\u05D0\u05D5\u05EA \u05DE\u05D3\u05D9\u05E0\u05D9\u05D5\u05EA \u05D4\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA. \u05D1\u05DE\u05D9\u05D3\u05EA \u05D4\u05D0\u05E4\u05E9\u05E8 \u05D5\u05D0\u05DD \u05D9\u05D1\u05D5\u05E6\u05E2\u05D5 \u05D1\u05DE\u05D3\u05D9\u05E0\u05D9\u05D5\u05EA \u05D6\u05D5 \u05E9\u05D9\u05E0\u05D5\u05D9\u05D9\u05DD \u05DE\u05D4\u05D5\u05EA\u05D9\u05D9\u05DD, \u05EA\u05E4\u05D5\u05E8\u05E1\u05DD \u05E2\u05DC-\u05DB\u05DA \u05D4\u05D5\u05D3\u05E2\u05D4 \u05D1\u05E2\u05DE\u05D5\u05D3 \u05D4\u05D1\u05D9\u05EA \u05E9\u05DC \u05D4\u05D0\u05EA\u05E8 \u05D5/\u05D0\u05D5 \u05D1\u05DE\u05DE\u05E9\u05E7 \u05D4\u05D9\u05D9\u05E9\u05D5\u05DE\u05D5\u05DF. \u05D4\u05E7\u05D9\u05E9\u05D5\u05E8 \u05DC\u05DE\u05D3\u05D9\u05E0\u05D9\u05D5\u05EA \u05D4\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA \u05D4\u05DE\u05D5\u05E6\u05D1 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05D9\u05E7\u05E9\u05E8 \u05EA\u05DE\u05D9\u05D3 \u05DC\u05E0\u05D5\u05E1\u05D7 \u05D4\u05E2\u05D3\u05DB\u05E0\u05D9 \u05E9\u05DC \u05D4\u05DE\u05D3\u05D9\u05E0\u05D9\u05D5\u05EA. \u05D4\u05DE\u05E9\u05DA \u05E9\u05D9\u05DE\u05D5\u05E9\u05DA \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA \u05DC\u05D0\u05D7\u05E8 \u05E4\u05E8\u05E1\u05D5\u05DD \u05DE\u05D3\u05D9\u05E0\u05D9\u05D5\u05EA \u05D4\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA \u05D4\u05DE\u05E2\u05D5\u05D3\u05DB\u05E0\u05EA, \u05DE\u05E2\u05D9\u05D3 \u05E2\u05DC \u05D4\u05E1\u05DB\u05DE\u05EA\u05DA \u05DC\u05D4. \u05D0\u05DD \u05D0\u05D9\u05E0\u05DA \u05DE\u05E1\u05DB\u05D9\u05DD \u05E2\u05DD \u05D4\u05D4\u05D5\u05E8\u05D0\u05D5\u05EA \u05D4\u05D7\u05D3\u05E9\u05D5\u05EA \u05D1\u05DE\u05D3\u05D9\u05E0\u05D9\u05D5\u05EA \u05D4\u05E4\u05E8\u05D8\u05D9\u05D5\u05EA, \u05E2\u05DC\u05D9\u05D9\u05DA \u05DC\u05D7\u05D3\u05D5\u05DC \u05DE\u05DC\u05E2\u05E9\u05D5\u05EA \u05DB\u05DC \u05E9\u05D9\u05DE\u05D5\u05E9 \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(185, "p");
        i0.ɵɵtext(186, "\u05E2\u05D5\u05D3\u05DB\u05DF \u05DC\u05D0\u05D7\u05E8\u05D5\u05E0\u05D4: 27/06/2022.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
      }
